import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getProblem, submitCode } from '../services/api';
import './ProblemDetailPage.css';

const ProblemDetailPage = () => {
  const { problemId } = useParams();
  const navigate = useNavigate();
  const [problem, setProblem] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [submitting, setSubmitting] = useState(false);
  const [submission, setSubmission] = useState(null);

  // 选择题相关状态
  const [selectedOptions, setSelectedOptions] = useState([]);
  const [options, setOptions] = useState([]);

  // 判断题相关状态
  const [judgeAnswer, setJudgeAnswer] = useState('');

  // 简答题相关状态
  const [shortAnswer, setShortAnswer] = useState('');

  // 编程题相关状态
  const [form, setForm] = useState({
    code: '',
    language: 'python'
  });

  useEffect(() => {
    fetchProblem();
  }, [problemId]);

  const fetchProblem = async () => {
    try {
      setLoading(true);
      const result = await getProblem(problemId);
      setProblem(result);

      // 根据题目类型初始化相关状态
      if (result.type === 'choice') {
        initializeChoiceOptions(result);
      }
    } catch (error) {
      setError('获取题目信息失败');
      console.error('Error fetching problem:', error);
    } finally {
      setLoading(false);
    }
  };

  // 初始化选择题选项
  const initializeChoiceOptions = (problemData) => {
    if (problemData.test_cases) {
      try {
        const parsedOptions = JSON.parse(problemData.test_cases);
        setOptions(parsedOptions);
      } catch (e) {
        // 如果不是JSON格式，尝试解析为普通文本
        const lines = problemData.test_cases.split('\n').filter(line => line.trim());
        const parsedOptions = lines.map((line, index) => ({
          id: String.fromCharCode(65 + index), // A, B, C, D...
          text: line.trim(),
          value: line.trim()
        }));
        setOptions(parsedOptions);
      }
    }
  };

  // 处理选择题选项变化
  const handleOptionChange = (optionId) => {
    if (problem.is_multiple_choice) {
      // 多选题：可以选择多个选项
      setSelectedOptions(prev => {
        if (prev.includes(optionId)) {
          return prev.filter(id => id !== optionId);
        } else {
          return [...prev, optionId];
        }
      });
    } else {
      // 单选题：只能选择一个选项
      setSelectedOptions([optionId]);
    }
  };

  // 处理判断题答案变化
  const handleJudgeAnswerChange = (answer) => {
    setJudgeAnswer(answer);
  };

  // 处理简答题答案变化
  const handleShortAnswerChange = (e) => {
    setShortAnswer(e.target.value);
  };

  // 处理编程题代码变化
  const handleCodeChange = (e) => {
    setForm({...form, code: e.target.value});
  };

  // 处理编程题语言变化
  const handleLanguageChange = (e) => {
    setForm({...form, language: e.target.value});
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    let submissionData = {
      problem_id: parseInt(problemId),
      language: 'text'
    };

    // 根据题目类型准备提交数据
    switch (problem.type) {
      case 'choice':
        if (selectedOptions.length === 0) {
          setError('请选择答案');
          return;
        }
        submissionData.code = selectedOptions.join(',');
        submissionData.language = 'choice';
        break;

      case 'judge':
        if (!judgeAnswer) {
          setError('请选择答案');
          return;
        }
        submissionData.code = judgeAnswer;
        submissionData.language = 'judge';
        break;

      case 'short_answer':
        if (!shortAnswer.trim()) {
          setError('请输入答案');
          return;
        }
        submissionData.code = shortAnswer;
        submissionData.language = 'short_answer';
        break;

      case 'programming':
        if (!form.code.trim()) {
          setError('请输入代码');
          return;
        }
        submissionData.code = form.code;
        submissionData.language = form.language;
        break;

      default:
        setError('不支持的题目类型');
        return;
    }

    setSubmitting(true);
    setError('');

    try {
      const result = await submitCode(submissionData);
      setSubmission(result);
      // 跳转到结果页面
      navigate(`/result/${result.submission_id}`);
    } catch (error) {
      setError(error.response?.data?.error || '提交失败');
    } finally {
      setSubmitting(false);
    }
  };

  const getDifficultyColor = (diff) => {
    switch (diff) {
      case 'easy': return '#52c41a';
      case 'medium': return '#faad14';
      case 'hard': return '#f5222d';
      default: return '#666';
    }
  };

  const getDifficultyText = (diff) => {
    switch (diff) {
      case 'easy': return '简单';
      case 'medium': return '中等';
      case 'hard': return '困难';
      default: return '未知';
    }
  };

  const getTypeText = (type) => {
    switch (type) {
      case 'programming': return '编程题';
      case 'choice': return '选择题';
      case 'judge': return '判断题';
      case 'short_answer': return '简答题';
      default: return '未知类型';
    }
  };

  if (loading) {
    return (
      <div className="problem-detail-container">
        <div className="loading">加载中...</div>
      </div>
    );
  }

  if (error || !problem) {
    return (
      <div className="problem-detail-container">
        <div className="error-message">{error || '题目不存在'}</div>
      </div>
    );
  }

  return (
    <div className="problem-detail-container">
      <div className="problem-header">
        <div className="problem-title-section">
          <h1>{problem.id}. {problem.title}</h1>
          <div className="problem-badges">
            <span 
              className="type-badge"
              style={{ backgroundColor: '#1890ff' }}
            >
              {getTypeText(problem.type)}
            </span>
            <span 
              className="difficulty-badge"
              style={{ backgroundColor: getDifficultyColor(problem.difficulty) }}
            >
              {getDifficultyText(problem.difficulty)}
            </span>
          </div>
        </div>

        {problem.type === 'programming' && (
          <div className="problem-meta">
            <div className="meta-item">
              <span className="meta-label">时间限制:</span>
              <span className="meta-value">{problem.time_limit}ms</span>
            </div>
            <div className="meta-item">
              <span className="meta-label">内存限制:</span>
              <span className="meta-value">{problem.memory_limit}MB</span>
            </div>
          </div>
        )}
      </div>

      <div className="problem-content">
        <div className="problem-description">
          <h2>题目描述</h2>
          <div className="description-text">
            {problem.description.split('\n').map((line, index) => (
              <p key={index}>{line}</p>
            ))}
          </div>
        </div>

        {/* 根据题目类型显示不同的内容 */}
        {problem.type === 'programming' && (
          <div className="problem-examples">
            <h2>输入输出示例</h2>
            {problem.test_cases && (
              <div className="example-section">
                <h3>输入:</h3>
                <pre className="example-code">{problem.test_cases}</pre>
              </div>
            )}
            {problem.expected_output && (
              <div className="example-section">
                <h3>期望输出:</h3>
                <pre className="example-code">{problem.expected_output}</pre>
              </div>
            )}
          </div>
        )}

        {problem.type === 'choice' && (
          <div className="problem-options">
            <h2>选项</h2>
            <div className="options-list">
              {options.map((option) => (
                <label key={option.id} className="option-item">
                  <input
                    type={problem.is_multiple_choice ? "checkbox" : "radio"}
                    name="choice"
                    value={option.id}
                    checked={selectedOptions.includes(option.id)}
                    onChange={() => handleOptionChange(option.id)}
                  />
                  <span className="option-text">
                    <strong>{option.id}.</strong> {option.text}
                  </span>
                </label>
              ))}
            </div>
            {problem.is_multiple_choice && (
              <p className="multiple-choice-hint">注意：本题为多选题，可以选择多个答案</p>
            )}
          </div>
        )}

        {problem.type === 'judge' && (
          <div className="problem-judge">
            <h2>请选择答案</h2>
            <div className="judge-options">
              <label className="judge-option">
                <input
                  type="radio"
                  name="judge"
                  value="正确"
                  checked={judgeAnswer === "正确"}
                  onChange={() => handleJudgeAnswerChange("正确")}
                />
                <span className="judge-text">正确</span>
              </label>
              <label className="judge-option">
                <input
                  type="radio"
                  name="judge"
                  value="错误"
                  checked={judgeAnswer === "错误"}
                  onChange={() => handleJudgeAnswerChange("错误")}
                />
                <span className="judge-text">错误</span>
              </label>
            </div>
          </div>
        )}

        {problem.type === 'short_answer' && (
          <div className="problem-short-answer">
            <h2>请输入答案</h2>
            <textarea
              value={shortAnswer}
              onChange={handleShortAnswerChange}
              placeholder="在这里输入你的答案..."
              rows={8}
              className="short-answer-editor"
            />
          </div>
        )}
      </div>

      {/* 根据题目类型显示不同的提交区域 */}
      {problem.type === 'programming' ? (
        <div className="code-submission">
          <h2>提交代码</h2>
          <form onSubmit={handleSubmit}>
            <div className="form-row">
              <label>编程语言:</label>
              <select
                value={form.language}
                onChange={handleLanguageChange}
                className="language-select"
              >
                <option value="python">Python</option>
                <option value="cpp">C++</option>
                <option value="java">Java</option>
                <option value="javascript">JavaScript</option>
              </select>
            </div>

            <div className="form-row">
              <label>代码:</label>
              <textarea
                value={form.code}
                onChange={handleCodeChange}
                placeholder="在这里输入你的代码..."
                rows={20}
                className="code-editor"
              />
            </div>

            <div className="form-actions">
              <button 
                type="submit" 
                className="submit-btn" 
                disabled={submitting}
              >
                {submitting ? '提交中...' : '提交代码'}
              </button>
            </div>
          </form>
        </div>
      ) : (
        <div className="answer-submission">
          <h2>提交答案</h2>
          <form onSubmit={handleSubmit}>
            <div className="form-actions">
              <button 
                type="submit" 
                className="submit-btn" 
                disabled={submitting}
              >
                {submitting ? '提交中...' : '提交答案'}
              </button>
            </div>
          </form>
        </div>
      )}

      {submission && (
        <div className="submission-result">
          <h3>提交结果</h3>
          <p>提交ID: {submission.submission_id}</p>
          <p>状态: {submission.status}</p>
          <p>消息: {submission.message}</p>
        </div>
      )}
    </div>
  );
};

export default ProblemDetailPage;
