import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { login } from '../services/api';
import './LoginPage.css';

const LoginPage = () => {
  const [loginType, setLoginType] = useState('student'); // 'student', 'teacher', 'admin'
  const [form, setForm] = useState({
    username: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const result = await login(form);
      localStorage.setItem('token', result.token);
      localStorage.setItem('user', JSON.stringify(result.user));
      // Á´ãÂç≥ÂπøÊí≠ËÆ§ËØÅÁä∂ÊÄÅÂèòÂåñÔºåÁ°Æ‰øùÂØºËà™Ê†èÈ¶ñÊ¨°ÁôªÂΩïÂç≥ÊòæÁ§∫
      window.dispatchEvent(new Event('auth-changed'));

      // Ê†πÊçÆÁî®Êà∑ËßíËâ≤Ë∑≥ËΩ¨Âà∞‰∏çÂêåÈ°µÈù¢
      if (result.user.role === 'admin') {
        navigate('/admin/dashboard');
      } else if (result.user.role === 'teacher') {
        navigate('/teacher/dashboard');
      } else {
        navigate('/problems');
      }
    } catch (error) {
      setError(error.response?.data?.error || 'ÁôªÂΩïÂ§±Ë¥•');
    } finally {
      setLoading(false);
    }
  };

  const handleLoginTypeChange = (type) => {
    setLoginType(type);
    setForm({ username: '', password: '' });
    setError('');
  };

  const getPlaceholder = () => {
    if (loginType === 'student') return 'ËØ∑ËæìÂÖ•Â≠¶Âè∑';
    if (loginType === 'teacher') return 'ËØ∑ËæìÂÖ•Â∑•Âè∑';
    return 'ËØ∑ËæìÂÖ•Â∑•Âè∑';
  };

  const getLabel = () => {
    if (loginType === 'student') return 'Â≠¶Âè∑';
    if (loginType === 'teacher') return 'Â∑•Âè∑';
    return 'Â∑•Âè∑';
  };

  return (
    <div className="login-container">
      {/* ËÉåÊôØË£ÖÈ•∞ÂÖÉÁ¥† */}
      <div className="bg-decoration">
        <div className="decoration-circle circle-1"></div>
        <div className="decoration-circle circle-2"></div>
        <div className="decoration-circle circle-3"></div>
      </div>

      <div className="login-card">
        {/* È°∂ÈÉ®Ë£ÖÈ•∞ */}
        <div className="login-header">
          <div className="logo-section">
            <div className="logo-icon">üöÄ</div>
            <h1>OJÂú®Á∫øËØÑÊµãÁ≥ªÁªü</h1>
            <p className="subtitle">Online Judge System</p>
          </div>
        </div>

        {/* ÁôªÂΩïÁ±ªÂûãÈÄâÊã© */}
        <div className="login-type-selector">
          <button
            type="button"
            className={`type-btn ${loginType === 'student' ? 'active' : ''}`}
            onClick={() => handleLoginTypeChange('student')}
          >
            <span className="type-icon">üéì</span>
            <span className="type-text">Â≠¶ÁîüÁôªÂΩï</span>
          </button>
          <button
            type="button"
            className={`type-btn ${loginType === 'teacher' ? 'active' : ''}`}
            onClick={() => handleLoginTypeChange('teacher')}
          >
            <span className="type-icon">üë®‚Äçüè´</span>
            <span className="type-text">ÊïôÂ∏àÁôªÂΩï</span>
          </button>
          <button
            type="button"
            className={`type-btn ${loginType === 'admin' ? 'active' : ''}`}
            onClick={() => handleLoginTypeChange('admin')}
          >
            <span className="type-icon">üëë</span>
            <span className="type-text">ÁÆ°ÁêÜÂëòÁôªÂΩï</span>
          </button>
        </div>

        {error && <div className="error-message">{error}</div>}

        {/* ÁôªÂΩïË°®Âçï */}
        <div className="form-section">
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>
                {getLabel()}
                <span className="input-icon">üë§</span>
              </label>
              <div className="input-wrapper">
                <input
                  type="text"
                  value={form.username}
                  onChange={(e) => setForm({...form, username: e.target.value})}
                  required
                  placeholder={getPlaceholder()}
                  autoComplete="username"
                />
              </div>
            </div>

            <div className="form-group">
              <label>
                ÂØÜÁ†Å
                <span className="input-icon">üîí</span>
              </label>
              <div className="input-wrapper">
                <input
                  type="password"
                  value={form.password}
                  onChange={(e) => setForm({...form, password: e.target.value})}
                  required
                  placeholder="ËØ∑ËæìÂÖ•ÂØÜÁ†Å"
                  autoComplete="current-password"
                />
              </div>
            </div>

            <button type="submit" className="login-btn" disabled={loading}>
              {loading ? (
                <span className="loading-text">
                  <span className="loading-spinner"></span>
                  ÁôªÂΩï‰∏≠...
                </span>
              ) : (
                <span>üöÄ Á´ãÂç≥ÁôªÂΩï</span>
              )}
            </button>
          </form>
        </div>

        {/* ÁôªÂΩï‰ø°ÊÅØËØ¥Êòé */}
        <div className="login-info">
          {loginType === 'student' ? (
            <div className="info-box">
              <div className="info-header">
                <span className="info-icon">üí°</span>
                <span className="info-title">Â≠¶ÁîüÁôªÂΩïËØ¥Êòé</span>
              </div>
              <ul>
                <li>‰ΩøÁî®Â≠¶Âè∑‰Ωú‰∏∫ÁôªÂΩïË¥¶Âè∑</li>
                <li>ÂØÜÁ†ÅÁî±‰ªªËØæÊïôÂ∏àÊèê‰æõ</li>
                <li>Â¶ÇÊúâÈóÆÈ¢òËØ∑ËÅîÁ≥ª‰ªªËØæÊïôÂ∏à</li>
              </ul>
            </div>
          ) : loginType === 'teacher' ? (
            <div className="info-box">
              <div className="info-header">
                <span className="info-icon">üí°</span>
                <span className="info-title">ÊïôÂ∏àÁôªÂΩïËØ¥Êòé</span>
              </div>
              <ul>
                <li>‰ΩøÁî®Â∑•Âè∑‰Ωú‰∏∫ÁôªÂΩïË¥¶Âè∑</li>
                <li>ÂØÜÁ†ÅÁî±ÁÆ°ÁêÜÂëòÊèê‰æõ</li>
                <li>Â¶ÇÊúâÈóÆÈ¢òËØ∑ËÅîÁ≥ªÁ≥ªÁªüÁÆ°ÁêÜÂëò</li>
              </ul>
            </div>
          ) : (
            <div className="info-box">
              <div className="info-header">
                <span className="info-icon">üí°</span>
                <span className="info-title">ÁÆ°ÁêÜÂëòÁôªÂΩïËØ¥Êòé</span>
              </div>
              <ul>
                <li>‰ΩøÁî®Â∑•Âè∑‰Ωú‰∏∫ÁôªÂΩïË¥¶Âè∑</li>
                <li>ÂØÜÁ†ÅÁî±Á≥ªÁªüÊèê‰æõ</li>
                <li>Â¶ÇÊúâÈóÆÈ¢òËØ∑ËÅîÁ≥ªÁ≥ªÁªüÁª¥Êä§‰∫∫Âëò</li>
              </ul>
            </div>
          )}
        </div>

        {/* Â∫ïÈÉ®ÈìæÊé• */}
        <div className="login-links">
          <a href="/forgot-password">üîë ÂøòËÆ∞ÂØÜÁ†ÅÔºü</a>
          {loginType === 'admin' && <a href="/admin/register">üìù Ê≥®ÂÜåÁÆ°ÁêÜÂëò</a>}
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
