import React, { useState, useEffect } from 'react';
import { getUsers, getSchools, getMajors, getClasses, batchImportStudents, getProblems, createProblem, updateProblem, deleteProblem, updateUser, deleteUser, registerStudent } from '../services/api';
import './TeacherDashboardPage.css';
import AIProblemGenerationPage from './AIProblemGenerationPage';

const TeacherDashboardPage = () => {
  const [activeTab, setActiveTab] = useState('students');
  const [activeSubTab, setActiveSubTab] = useState('list'); // 子标签页
  const [users, setUsers] = useState([]);
  const [problems, setProblems] = useState([]);
  const [schools, setSchools] = useState([]);
  const [majors, setMajors] = useState([]);
  const [classes, setClasses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // 批量导入相关状态
  const [importData, setImportData] = useState('');
  const [selectedSchool, setSelectedSchool] = useState('');
  const [selectedMajor, setSelectedMajor] = useState('');
  const [selectedClass, setSelectedClass] = useState('');

  // 手动创建题目相关状态
  const [problemForm, setProblemForm] = useState({
    title: '',
    description: '',
    test_cases: '',
    expected_output: '',
    difficulty: 'easy',
    time_limit: 1000,
    memory_limit: 128
  });

  // 编辑题目状态
  const [editingProblem, setEditingProblem] = useState(null);
  const [isEditing, setIsEditing] = useState(false);

  // 添加学生相关状态（按新规则：学号、姓名、密码必填）
  const [addStudentForm, setAddStudentForm] = useState({
    student_no: '',
    name: '',
    password: '',
    email: '',
    phone: '',
    school_id: '',
    major_id: '',
    class_id: ''
  });

  // 学生编辑相关状态
  const [editingUser, setEditingUser] = useState(null);
  const [editForm, setEditForm] = useState({
    email: '',
    phone: '',
    school_id: '',
    major_id: '',
    class_id: '',
    new_password: ''
  });

  useEffect(() => {
    fetchInitialData();
  }, []);

  const fetchInitialData = async () => {
    try {
      setLoading(true);
      setError('');
      setSuccess('');
      const [schoolsData, usersData, problemsData] = await Promise.all([
        getSchools(),
        getUsers(1, 50, 'student'),
        getProblems(1, 50)
      ]);

      setSchools(schoolsData);
      setUsers(usersData.users);
      setProblems(problemsData.problems);
    } catch (error) {
      setError(error?.response?.data?.error || '获取数据失败');
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSchoolChange = async (schoolId) => {
    setSelectedSchool(schoolId);
    setSelectedMajor('');
    setSelectedClass('');

    if (schoolId) {
      try {
        const majorsData = await getMajors(schoolId);
        setMajors(majorsData);
      } catch (error) {
        console.error('Error fetching majors:', error);
      }
    } else {
      setMajors([]);
    }
  };

  const handleMajorChange = async (majorId) => {
    setSelectedMajor(majorId);
    setSelectedClass('');

    if (majorId) {
      try {
        const classesData = await getClasses(majorId);
        setClasses(classesData);
      } catch (error) {
        console.error('Error fetching classes:', error);
      }
    } else {
      setClasses([]);
    }
  };

  const handleBatchImport = async () => {
    if (!importData.trim()) {
      setError('请输入要导入的学生数据');
      setSuccess('');
      return;
    }

    if (!selectedSchool || !selectedMajor || !selectedClass) {
      setError('请选择学校、专业和班级');
      setSuccess('');
      return;
    }

    // 解析CSV数据为学生对象数组
    const lines = importData
      .split(/\r?\n/)
      .map(l => l.trim())
      .filter(l => l && !/^\s*#/.test(l));
    const students = [];
    for (const line of lines) {
      const parts = line.split(',').map(s => s.trim());
      if (parts.length < 3) continue; // 至少: 学号,姓名,密码
      const [studentNo, name, password, email, phone] = parts;
      students.push({
        username: studentNo,
        name,
        password,
        email: email || undefined,
        phone: phone || undefined,
        school_id: selectedSchool,
        major_id: selectedMajor,
        class_id: selectedClass,
        role: 'student'
      });
    }

    if (students.length === 0) {
      setError('没有有效的学生数据行。请按 CSV: 学号,姓名,密码[,邮箱][,手机号]');
      setSuccess('');
      return;
    }

    try {
      setLoading(true);
      setError('');
      setSuccess('');

      const resp = await batchImportStudents({ students });

      const msg = resp?.message || `批量导入完成: 成功 ${resp?.success_count ?? students.length} 个, 失败 ${resp?.failed_count ?? 0} 个`;
      setSuccess(msg);
      if (resp?.errors && resp.errors.length > 0) {
        const preview = resp.errors.slice(0, 5).join('；');
        setError(`部分导入失败：${preview}${resp.errors.length > 5 ? ' 等' : ''}`);
      }

      // 刷新用户列表
      const usersData = await getUsers(1, 50, 'student');
      setUsers(usersData.users);
      setImportData('');
    } catch (error) {
      setError(error.response?.data?.error || '批量导入失败');
    } finally {
      setLoading(false);
    }
  };

  const handleCreateProblem = async () => {
    try {
      setLoading(true);
      setError('');
      setSuccess('');

      if (isEditing && editingProblem) {
        await updateProblem(editingProblem.id, problemForm);
        setSuccess('题目更新成功！');
      } else {
        await createProblem(problemForm);
        setSuccess('题目创建成功！');
      }

      // 重置表单
      setProblemForm({
        title: '',
        description: '',
        test_cases: '',
        expected_output: '',
        difficulty: 'easy',
        time_limit: 1000,
        memory_limit: 128
      });

      setIsEditing(false);
      setEditingProblem(null);

      // 刷新题目列表
      const problemsData = await getProblems(1, 50);
      setProblems(problemsData.problems);

      // 切换到题目列表
      setActiveSubTab('list');

    } catch (error) {
      setError(error.response?.data?.error || (isEditing ? '更新题目失败' : '创建题目失败'));
    } finally {
      setLoading(false);
    }
  };

  const handleEditProblem = (problem) => {
    setEditingProblem(problem);
    setProblemForm({
      title: problem.title,
      description: problem.description,
      test_cases: problem.test_cases,
      expected_output: problem.expected_output,
      difficulty: problem.difficulty,
      time_limit: problem.time_limit,
      memory_limit: problem.memory_limit
    });
    setIsEditing(true);
    setActiveSubTab('create');
  };

  const handleDeleteProblem = async (problemId) => {
    if (!window.confirm('确定要删除这个题目吗？此操作不可恢复。')) {
      return;
    }

    try {
      setLoading(true);
      setError('');
      setSuccess('');
      await deleteProblem(problemId);
      setSuccess('题目删除成功！');

      // 刷新题目列表
      const problemsData = await getProblems(1, 50);
      setProblems(problemsData.problems);
    } catch (error) {
      setError(error.response?.data?.error || '删除题目失败');
    } finally {
      setLoading(false);
    }
  };

  const handleCancelEdit = () => {
    setIsEditing(false);
    setEditingProblem(null);
    setProblemForm({
      title: '',
      description: '',
      test_cases: '',
      expected_output: '',
      difficulty: 'easy',
      time_limit: 1000,
      memory_limit: 128
    });
    setActiveSubTab('list');
  };

  // 学生管理标签页渲染函数
  const renderStudentsTab = () => (
    <div className="tab-content">
      <div className="sub-tabs">
        <button 
          className={`sub-tab-btn ${activeSubTab === 'list' ? 'active' : ''}`}
          onClick={() => setActiveSubTab('list')}
        >
          学生列表
        </button>
        <button 
          className={`sub-tab-btn ${activeSubTab === 'add' ? 'active' : ''}`}
          onClick={() => setActiveSubTab('add')}
        >
          添加学生
        </button>
        <button 
          className={`sub-tab-btn ${activeSubTab === 'import' ? 'active' : ''}`}
          onClick={() => setActiveSubTab('import')}
        >
          批量导入
        </button>
      </div>

      {activeSubTab === 'list' && (
        <div className="section-content">
          <div className="section-header">
            <h2>学生列表</h2>
            <button 
              className="add-btn"
              onClick={() => setActiveSubTab('add')}
            >
              添加学生
            </button>
          </div>

          <div className="users-table">
            <table>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>学号</th>
                  <th>姓名</th>
                  <th>邮箱</th>
                  <th>班级</th>
                  <th>状态</th>
                  <th>操作</th>
                </tr>
              </thead>
              <tbody>
                {users.map(user => (
                  <tr key={user.id}>
                    <td>{user.id}</td>
                    <td>{user.username}</td>
                    <td>{user.name}</td>
                    <td>{user.email || '-'}</td>
                    <td>{user.class_id || '-'}</td>
                    <td>
                      <span className={`status-badge ${user.is_active ? 'active' : 'inactive'}`}>
                        {user.is_active ? '活跃' : '禁用'}
                      </span>
                    </td>
                    <td>
                      <button className="action-btn edit" onClick={() => handleEditStudentClick(user)}>编辑</button>
                      <button className="action-btn delete" onClick={() => handleDeleteStudent(user.id)}>删除</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {activeSubTab === 'add' && (
        <div className="section-content">
          <div className="section-header">
            <h2>➕ 添加学生</h2>
            <button 
              className="secondary-btn"
              onClick={() => setActiveSubTab('list')}
            >
              ← 返回学生列表
            </button>
          </div>

          <div className="add-student-form">
            <div className="form-row">
              <div className="form-group">
                <label>学号：<span className="required-mark">*</span> <span className="required-text">必填</span></label>
                <input 
                  type="text" 
                  placeholder="请输入学号"
                  value={addStudentForm.student_no}
                  onChange={(e) => setAddStudentForm(prev => ({ ...prev, student_no: e.target.value }))}
                  required
                />
              </div>

              <div className="form-group">
                <label>密码：<span className="required-mark">*</span> <span className="required-text">必填</span></label>
                <input 
                  type="password" 
                  placeholder="请输入密码"
                  value={addStudentForm.password}
                  onChange={(e) => setAddStudentForm(prev => ({ ...prev, password: e.target.value }))}
                  required
                />
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>姓名：<span className="required-mark">*</span> <span className="required-text">必填</span></label>
                <input 
                  type="text" 
                  placeholder="请输入学生姓名"
                  value={addStudentForm.name}
                  onChange={(e) => setAddStudentForm(prev => ({ ...prev, name: e.target.value }))}
                  required
                />
              </div>

              <div className="form-group">
                <label>邮箱：</label>
                <input 
                  type="email" 
                  placeholder="请输入邮箱地址"
                  value={addStudentForm.email}
                  onChange={(e) => setAddStudentForm(prev => ({ ...prev, email: e.target.value }))}
                />
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>手机号：</label>
                <input 
                  type="tel" 
                  placeholder="请输入手机号码"
                  value={addStudentForm.phone}
                  onChange={(e) => setAddStudentForm(prev => ({ ...prev, phone: e.target.value }))}
                />
              </div>

              <div className="form-group">
                <label>学校：</label>
                <select 
                  value={addStudentForm.school_id} 
                  onChange={(e) => handleAddStudentSchoolChange(e.target.value)}
                >
                  <option value="">请选择学校</option>
                  {schools.map(school => (
                    <option key={school.id} value={school.id}>
                      {school.name}
                    </option>
                  ))}
                </select>
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>专业：</label>
                <select 
                  value={addStudentForm.major_id} 
                  onChange={(e) => handleAddStudentMajorChange(e.target.value)}
                  disabled={!addStudentForm.school_id}
                >
                  <option value="">请选择专业</option>
                  {majors.map(major => (
                    <option key={major.id} value={major.id}>
                      {major.name}
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label>班级：</label>
                <select 
                  value={addStudentForm.class_id} 
                  onChange={(e) => setAddStudentForm(prev => ({ ...prev, class_id: e.target.value }))}
                  disabled={!addStudentForm.major_id}
                >
                  <option value="">请选择班级</option>
                  {classes.map(classItem => (
                    <option key={classItem.id} value={classItem.id}>
                      {classItem.name}
                    </option>
                  ))}
                </select>
              </div>
            </div>

            <div className="form-actions">
              <button 
                className="submit-btn"
                onClick={handleAddStudent}
                disabled={loading || !addStudentForm.student_no || !addStudentForm.name || !addStudentForm.password}
              >
                {loading ? '添加中...' : '添加学生'}
              </button>
            </div>
          </div>
        </div>
      )}

      {activeSubTab === 'import' && (
        <div className="section-content">
          <div className="section-header">
            <h2>📥 批量导入学生</h2>
            <button 
              className="secondary-btn"
              onClick={() => setActiveSubTab('list')}
            >
              ← 返回学生列表
            </button>
          </div>

          <div className="import-form">
            <div className="form-group">
              <label>选择学校:</label>
              <select 
                value={selectedSchool} 
                onChange={(e) => handleSchoolChange(e.target.value)}
              >
                <option value="">请选择学校</option>
                {schools.map(school => (
                  <option key={school.id} value={school.id}>
                    {school.name}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>选择专业:</label>
              <select 
                value={selectedMajor} 
                onChange={(e) => handleMajorChange(e.target.value)}
                disabled={!selectedSchool}
              >
                <option value="">请选择专业</option>
                {majors.map(major => (
                  <option key={major.id} value={major.id}>
                    {major.name}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>选择班级:</label>
              <select 
                value={selectedClass} 
                onChange={(e) => setSelectedClass(e.target.value)}
                disabled={!selectedMajor}
              >
                <option value="">请选择班级</option>
                {classes.map(classItem => (
                  <option key={classItem.id} value={classItem.id}>
                    {classItem.name}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>学生数据 (CSV格式):</label>
              <textarea
                value={importData}
                onChange={(e) => setImportData(e.target.value)}
                placeholder="学号,姓名,密码,邮箱,手机号&#10;2021001,张三,123456,zhangsan@example.com,13800138001&#10;2021002,李四,123456,lisi@example.com,13800138002"
                rows={10}
              />
              <div className="form-help">
                <p>CSV格式说明：每行一个学生，字段用逗号分隔</p>
                <p>必填字段：学号,姓名,密码</p>
                <p>可选字段：邮箱,手机号</p>
              </div>
            </div>

            <div className="form-actions">
              <button 
                className="submit-btn"
                onClick={handleBatchImport}
                disabled={loading || !importData.trim() || !selectedSchool || !selectedMajor || !selectedClass}
              >
                {loading ? '导入中...' : '开始导入'}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );

  // 题目管理标签页渲染函数
  const renderProblemsTab = () => (
    <div className="tab-content">
      <div className="sub-tabs">
        <button 
          className={`sub-tab-btn ${activeSubTab === 'list' ? 'active' : ''}`}
          onClick={() => setActiveSubTab('list')}
        >
          题目列表
        </button>
        <button 
          className={`sub-tab-btn ${activeSubTab === 'create' ? 'active' : ''}`}
          onClick={() => setActiveSubTab('create')}
        >
          {isEditing ? '编辑题目' : '创建题目'}
        </button>
        <button 
          className={`sub-tab-btn ${activeSubTab === 'ai' ? 'active' : ''}`}
          onClick={() => setActiveSubTab('ai')}
        >
          🤖 AI生题
        </button>
      </div>

      {activeSubTab === 'list' && (
        <div className="section-content">
          <div className="section-header">
            <h2>题目列表</h2>
            <button 
              className="add-btn"
              onClick={() => {
                setIsEditing(false);
                setEditingProblem(null);
                setActiveSubTab('create');
              }}
            >
              创建题目
            </button>
          </div>

          <div className="problems-table">
            <table>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>标题</th>
                  <th>难度</th>
                  <th>时间限制</th>
                  <th>内存限制</th>
                  <th>状态</th>
                  <th>操作</th>
                </tr>
              </thead>
              <tbody>
                {problems.map(problem => (
                  <tr key={problem.id}>
                    <td>{problem.id}</td>
                    <td>{problem.title}</td>
                    <td>
                      <span className={`difficulty-badge ${problem.difficulty}`}>
                        {problem.difficulty === 'easy' ? '简单' : 
                         problem.difficulty === 'medium' ? '中等' : '困难'}
                      </span>
                    </td>
                    <td>{problem.time_limit}ms</td>
                    <td>{problem.memory_limit}MB</td>
                    <td>
                      <span className={`status-badge ${problem.is_active ? 'active' : 'inactive'}`}>
                        {problem.is_active ? '启用' : '禁用'}
                      </span>
                    </td>
                    <td>
                      <button 
                        className="action-btn edit"
                        onClick={() => handleEditProblem(problem)}
                      >
                        编辑
                      </button>
                      <button 
                        className="action-btn delete"
                        onClick={() => handleDeleteProblem(problem.id)}
                      >
                        删除
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {activeSubTab === 'create' && (
        <div className="section-content">
          <div className="section-header">
            <h2>{isEditing ? '✏️ 编辑题目' : '➕ 创建题目'}</h2>
            <button 
              className="secondary-btn"
              onClick={handleCancelEdit}
            >
              ← 返回题目列表
            </button>
          </div>

          <div className="problem-form">
            <div className="form-row">
              <div className="form-group">
                <label>题目标题：</label>
                <input 
                  type="text" 
                  placeholder="请输入题目标题"
                  value={problemForm.title}
                  onChange={(e) => setProblemForm(prev => ({ ...prev, title: e.target.value }))}
                />
              </div>

              <div className="form-group">
                <label>难度等级：</label>
                <select 
                  value={problemForm.difficulty}
                  onChange={(e) => setProblemForm(prev => ({ ...prev, difficulty: e.target.value }))}
                >
                  <option value="easy">简单</option>
                  <option value="medium">中等</option>
                  <option value="hard">困难</option>
                </select>
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>时间限制 (ms)：</label>
                <input 
                  type="number" 
                  placeholder="1000"
                  value={problemForm.time_limit}
                  onChange={(e) => setProblemForm(prev => ({ ...prev, time_limit: parseInt(e.target.value) }))}
                  min="100"
                  max="10000"
                />
              </div>

              <div className="form-group">
                <label>内存限制 (MB)：</label>
                <input 
                  type="number" 
                  placeholder="128"
                  value={problemForm.memory_limit}
                  onChange={(e) => setProblemForm(prev => ({ ...prev, memory_limit: parseInt(e.target.value) }))}
                  min="16"
                  max="1024"
                />
              </div>
            </div>

            <div className="form-group">
              <label>题目描述：</label>
              <textarea
                placeholder="请输入题目的详细描述"
                value={problemForm.description}
                onChange={(e) => setProblemForm(prev => ({ ...prev, description: e.target.value }))}
                rows={6}
              />
            </div>

            <div className="form-group">
              <label>测试用例：</label>
              <textarea
                placeholder="请输入测试用例"
                value={problemForm.test_cases}
                onChange={(e) => setProblemForm(prev => ({ ...prev, test_cases: e.target.value }))}
                rows={4}
              />
            </div>

            <div className="form-group">
              <label>期望输出：</label>
              <textarea
                placeholder="请输入期望的输出结果"
                value={problemForm.expected_output}
                onChange={(e) => setProblemForm(prev => ({ ...prev, expected_output: e.target.value }))}
                rows={4}
              />
            </div>

            <div className="form-actions">
              <button 
                className="submit-btn"
                onClick={handleCreateProblem}
                disabled={loading || !problemForm.title || !problemForm.description || !problemForm.test_cases || !problemForm.expected_output}
              >
                {loading ? (isEditing ? '更新中...' : '创建中...') : (isEditing ? '更新题目' : '创建题目')}
              </button>
            </div>
          </div>
        </div>
      )}

      {activeSubTab === 'ai' && (
        <div className="section-content">
          <AIProblemGenerationPage />
        </div>
      )}
    </div>
  );

  // 处理添加学生时的学校选择变化
  const handleAddStudentSchoolChange = async (schoolId) => {
    setAddStudentForm(prev => ({ 
      ...prev, 
      school_id: schoolId,
      major_id: '',
      class_id: ''
    }));

    if (schoolId) {
      try {
        const majorsData = await getMajors(schoolId);
        setMajors(majorsData);
      } catch (error) {
        console.error('Error fetching majors:', error);
      }
    } else {
      setMajors([]);
    }
  };

  // 处理添加学生时的专业选择变化
  const handleAddStudentMajorChange = async (majorId) => {
    setAddStudentForm(prev => ({ 
      ...prev, 
      major_id: majorId,
      class_id: ''
    }));

    if (majorId) {
      try {
        const classesData = await getClasses(majorId);
        setClasses(classesData);
      } catch (error) {
        console.error('Error fetching classes:', error);
      }
    } else {
      setClasses([]);
    }
  };

  // 处理添加学生
  const handleAddStudent = async () => {
    try {
      setLoading(true);
      setError('');
      setSuccess('');

      await registerStudent(addStudentForm);

      setSuccess('学生添加成功！');

      // 重置表单
      setAddStudentForm({
        student_no: '',
        name: '',
        password: '',
        email: '',
        phone: '',
        school_id: '',
        major_id: '',
        class_id: ''
      });

      // 刷新用户列表
      const usersData = await getUsers(1, 50, 'student');
      setUsers(usersData.users);

      // 切换到学生列表
      setActiveSubTab('list');

    } catch (error) {
      setError(error.response?.data?.error || '添加学生失败');
    } finally {
      setLoading(false);
    }
  };

  // 学生编辑/删除相关
  const handleEditStudentClick = (user) => {
    setEditingUser(user);
    setEditForm({
      email: user.email || '',
      phone: user.phone || '',
      school_id: user.school_id || '',
      major_id: user.major_id || '',
      class_id: user.class_id || '',
      new_password: ''
    });
    setError('');
    setSuccess('');
  };

  const handleSaveStudent = async () => {
    if (!editingUser) return;
    try {
      setLoading(true);
      setError('');
      setSuccess('');
      await updateUser(editingUser.id, editForm);
      setSuccess('学生信息已更新');
      // 刷新列表
      const usersData = await getUsers(1, 50, 'student');
      setUsers(usersData.users);
      setEditingUser(null);
    } catch (error) {
      setError(error.response?.data?.error || '更新学生信息失败');
    } finally {
      setLoading(false);
    }
  };

  const handleCancelEditStudent = () => {
    setEditingUser(null);
    setEditForm({ email: '', phone: '', school_id: '', major_id: '', class_id: '', new_password: '' });
  };

  const handleDeleteStudent = async (userId) => {
    if (!window.confirm('确定要删除该学生吗？此操作不可恢复。')) return;
    try {
      setLoading(true);
      setError('');
      setSuccess('');
      await deleteUser(userId);
      setSuccess('学生已删除');
      const usersData = await getUsers(1, 50, 'student');
      setUsers(usersData.users);
    } catch (error) {
      setError(error.response?.data?.error || '删除学生失败');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="teacher-dashboard">
      <div className="dashboard-header">
        <h1>教师管理控制台</h1>
      </div>

      <div className="dashboard-tabs">
        <button 
          className={`tab-btn ${activeTab === 'students' ? 'active' : ''}`}
          onClick={() => {
            setActiveTab('students');
            setActiveSubTab('list');
          }}
        >
          学生管理
        </button>
        <button 
          className={`tab-btn ${activeTab === 'problems' ? 'active' : ''}`}
          onClick={() => {
            setActiveTab('problems');
            setActiveSubTab('list');
          }}
        >
          题目管理
        </button>
        <button 
          className={`tab-btn ${activeTab === 'courses' ? 'active' : ''}`}
          onClick={() => {
            setActiveTab('courses');
            setActiveSubTab('list');
          }}
        >
          我的课程
        </button>
      </div>

      {error && <div className="error-message">{error}</div>}
      {success && <div className="success-message">{success}</div>}

      {activeTab === 'students' && (
        <>
          {renderStudentsTab()}
          {editingUser && (
            <div className="modal-overlay">
              <div className="modal">
                <div className="modal-header">
                  <h3>编辑学生：{editingUser.name}（学号{editingUser.username}）</h3>
                </div>
                <div className="modal-body">
                  <div className="form-row">
                    <div className="form-group">
                      <label>邮箱：</label>
                      <input type="email" value={editForm.email} onChange={(e) => setEditForm(prev => ({ ...prev, email: e.target.value }))} />
                    </div>
                    <div className="form-group">
                      <label>手机号：</label>
                      <input type="tel" value={editForm.phone} onChange={(e) => setEditForm(prev => ({ ...prev, phone: e.target.value }))} />
                    </div>
                  </div>
                  <div className="form-row">
                    <div className="form-group">
                      <label>学校：</label>
                      <select value={editForm.school_id} onChange={async (e) => {
                        const val = e.target.value;
                        setEditForm(prev => ({ ...prev, school_id: val, major_id: '', class_id: '' }));
                        if (val) {
                          const majorsData = await getMajors(val);
                          setMajors(majorsData);
                        } else {
                          setMajors([]);
                          setClasses([]);
                        }
                      }}>
                        <option value="">请选择学校</option>
                        {schools.map(s => <option key={s.id} value={s.id}>{s.name}</option>)}
                      </select>
                    </div>
                    <div className="form-group">
                      <label>专业：</label>
                      <select value={editForm.major_id} onChange={async (e) => {
                        const val = e.target.value;
                        setEditForm(prev => ({ ...prev, major_id: val, class_id: '' }));
                        if (val) {
                          const classesData = await getClasses(val);
                          setClasses(classesData);
                        } else {
                          setClasses([]);
                        }
                      }} disabled={!editForm.school_id}>
                        <option value="">请选择专业</option>
                        {majors.map(m => <option key={m.id} value={m.id}>{m.name}</option>)}
                      </select>
                    </div>
                  </div>
                  <div className="form-row">
                    <div className="form-group">
                      <label>班级：</label>
                      <select value={editForm.class_id} onChange={(e) => setEditForm(prev => ({ ...prev, class_id: e.target.value }))} disabled={!editForm.major_id}>
                        <option value="">请选择班级</option>
                        {classes.map(c => <option key={c.id} value={c.id}>{c.name}</option>)}
                      </select>
                    </div>
                    <div className="form-group">
                      <label>重置密码（可选）：</label>
                      <input type="password" value={editForm.new_password} onChange={(e) => setEditForm(prev => ({ ...prev, new_password: e.target.value }))} />
                    </div>
                  </div>
                </div>
                <div className="modal-footer">
                  <button className="secondary-btn" onClick={handleCancelEditStudent}>取消</button>
                  <button className="submit-btn" onClick={handleSaveStudent} disabled={loading}>{loading ? '保存中...' : '保存'}</button>
                </div>
              </div>
            </div>
          )}
        </>
      )}
      {activeTab === 'problems' && renderProblemsTab()}
    </div>
  );
};

export default TeacherDashboardPage;
