import React, { useState, useEffect } from 'react';
import { getUsers, getSchools, getMajors, getClasses, batchImportStudents, getProblems, createProblem, updateProblem, deleteProblem, updateUser, deleteUser, registerStudent, getTeacherCourses, getCourseAssignments, createAssignment, updateAssignment, deleteAssignment, getCourseStudents, addStudentToCourse, removeStudentFromCourse } from '../services/api';
import './TeacherDashboardPage.css';
import AIProblemGenerationPage from './AIProblemGenerationPage';
import { mockCourses, mockAssignments, formatDate } from '../testData/courses';

const TeacherDashboardPage = () => {
  const [activeTab, setActiveTab] = useState('students');
  const [activeSubTab, setActiveSubTab] = useState('list'); // 子标签页
  const [users, setUsers] = useState([]);
  const [problems, setProblems] = useState([]);
  const [schools, setSchools] = useState([]);
  const [majors, setMajors] = useState([]);
  const [classes, setClasses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // 批量导入相关状态
  const [importData, setImportData] = useState('');
  const [selectedSchool, setSelectedSchool] = useState('');
  const [selectedMajor, setSelectedMajor] = useState('');
  const [selectedClass, setSelectedClass] = useState('');

  // 手动创建题目相关状态
  const [problemForm, setProblemForm] = useState({
    title: '',
    description: '',
    type: 'programming', // 题目类型：programming(编程题), choice(选择题), judge(判断题), short_answer(简答题)
    test_cases: '',
    expected_output: '',
    difficulty: 'easy',
    time_limit: 1000,
    memory_limit: 128,
    // 选择题相关字段
    options: [], // 选项数组
    correct_answers: [], // 正确答案数组（支持多选）
    is_multiple_choice: false, // 是否多选
    // 简答题相关字段
    answer: '' // 简答题答案
  });

  // 编辑题目状态
  const [editingProblem, setEditingProblem] = useState(null);
  const [isEditing, setIsEditing] = useState(false);

  // 添加学生相关状态（按新规则：学号、姓名、密码必填）
  const [addStudentForm, setAddStudentForm] = useState({
    student_no: '',
    name: '',
    password: '',
    email: '',
    phone: '',
    school_id: '',
    major_id: '',
    class_id: ''
  });

  // 学生编辑相关状态
  const [editingUser, setEditingUser] = useState(null);
  const [editForm, setEditForm] = useState({
    email: '',
    phone: '',
    school_id: '',
    major_id: '',
    class_id: '',
    new_password: ''
  });

  // 课程相关状态（只读，用于查看）
  const [courses, setCourses] = useState([]);
  const [editingCourse, setEditingCourse] = useState(null);

  // 作业相关状态
  const [assignments, setAssignments] = useState([]);
  const [assignmentForm, setAssignmentForm] = useState({
    name: '',
    description: '',
    requirements: '',
    due_date: '',
    course_id: '',
    problem_ids: []
  });

  // 编辑作业状态
  const [editingAssignment, setEditingAssignment] = useState(null);
  const [isEditingAssignment, setIsEditingAssignment] = useState(false);
  const [editingAssignmentContext, setEditingAssignmentContext] = useState(''); // 记录编辑作业时的上下文

  // 添加学生到课程的状态
  const [addingStudentContext, setAddingStudentContext] = useState(''); // 记录添加学生时的上下文

  // 学生-教学班关联关系状态
  const [studentClassRelations, setStudentClassRelations] = useState([]); // 存储学生与教学班的关联关系

  useEffect(() => {
    fetchInitialData();
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  // 从API加载学生-课程关联关系
  useEffect(() => {
    const loadCourseStudents = async () => {
      if (editingCourse && editingCourse.id) {
        console.log('开始加载课程学生数据，课程ID:', editingCourse.id);

        try {
          const courseStudentsData = await getCourseStudents(editingCourse.id);
          console.log('从API获取的课程学生数据:', courseStudentsData);

          if (courseStudentsData && courseStudentsData.length > 0) {
            const apiRelations = courseStudentsData.map(student => ({
              student_id: student.student_id,
              class_id: student.class_id,
              course_id: editingCourse.id,
              student_name: student.student_name,
              student_no: student.student_no,
              added_at: student.added_at || new Date().toISOString()
            }));
            setStudentClassRelations(apiRelations);
            console.log('成功加载课程学生数据:', apiRelations);
          } else {
            setStudentClassRelations([]);
            console.log('该课程暂无学生');
          }
        } catch (error) {
          console.error('加载课程学生数据失败:', error);
          setStudentClassRelations([]);
        }
      }
    };

    loadCourseStudents();
  }, [editingCourse]); // eslint-disable-line react-hooks/exhaustive-deps

  const fetchInitialData = async () => {
    try {
      setLoading(true);
      setError('');
      setSuccess('');
      const [schoolsData, usersData, problemsData] = await Promise.all([
        getSchools(),
        getUsers(1, 50, 'student'),
        getProblems(1, 50)
      ]);

      setSchools(schoolsData);
      setUsers(usersData.users);
      setProblems(problemsData.problems);

      // 获取所有专业和班级数据
      await fetchAllMajorsAndClasses();

      // 调试信息：打印题目数据
      console.log('从API获取的题目数据:', problemsData.problems);
      if (problemsData.problems && problemsData.problems.length > 0) {
        problemsData.problems.forEach((problem, index) => {
          console.log(`题目 ${index + 1}:`, {
            id: problem.id,
            title: problem.title,
            type: problem.type,
            difficulty: problem.difficulty
          });
        });
      }

      // 获取课程和作业数据（使用测试数据）
      try {
        const [coursesData, assignmentsData] = await Promise.all([
          getTeacherCourses(),
          getCourseAssignments()
        ]);

        setCourses(coursesData.courses || mockCourses);
        setAssignments(assignmentsData.assignments || mockAssignments);
      } catch (error) {
        console.warn('获取课程或作业数据失败，使用测试数据:', error);
        setCourses(mockCourses);
        setAssignments(mockAssignments);
      }

      // 学生-课程关联关系数据将在进入课程详情页时从API加载
    } catch (error) {
      setError(error?.response?.data?.error || '获取数据失败');
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  // 获取所有专业和班级数据的函数
  const fetchAllMajorsAndClasses = async () => {
    try {
      // 获取所有学校的专业
      const allMajors = [];
      const allClasses = [];

      for (const school of schools) {
        try {
          const majorsData = await getMajors(school.id);
          allMajors.push(...majorsData);

          // 获取每个专业的班级
          for (const major of majorsData) {
            try {
              const classesData = await getClasses(major.id);
              allClasses.push(...classesData);
            } catch (error) {
              console.error(`获取专业 ${major.id} 的班级失败:`, error);
            }
          }
        } catch (error) {
          console.error(`获取学校 ${school.id} 的专业失败:`, error);
        }
      }

      setMajors(allMajors);
      setClasses(allClasses);

      console.log('获取到的所有专业:', allMajors);
      console.log('获取到的所有班级:', allClasses);
    } catch (error) {
      console.error('获取所有专业和班级失败:', error);
    }
  };

  const handleSchoolChange = async (schoolId) => {
    setSelectedSchool(schoolId);
    setSelectedMajor('');
    setSelectedClass('');

    // 不再重置全局的majors和classes数组
    // 这些数组应该保持完整，用于学生列表显示
  };

  const handleMajorChange = async (majorId) => {
    setSelectedMajor(majorId);
    setSelectedClass('');

    // 不再重置全局的classes数组
    // 这个数组应该保持完整，用于学生列表显示
  };

  const handleBatchImport = async () => {
    if (!importData.trim()) {
      setError('请输入要导入的学生数据');
      setSuccess('');
      return;
    }

    if (!selectedSchool || !selectedMajor || !selectedClass) {
      setError('请选择学校、专业和班级');
      setSuccess('');
      return;
    }

    // 解析CSV数据为学生对象数组
    const lines = importData
      .split(/\r?\n/)
      .map(l => l.trim())
      .filter(l => l && !/^\s*#/.test(l));
    const students = [];
    for (const line of lines) {
      const parts = line.split(',').map(s => s.trim());
      if (parts.length < 3) continue; // 至少: 学号,姓名,密码
      const [studentNo, name, password, email, phone] = parts;
      students.push({
        username: studentNo,
        name,
        password,
        email: email || undefined,
        phone: phone || undefined,
        school_id: selectedSchool,
        major_id: selectedMajor,
        class_id: selectedClass,
        role: 'student'
      });
    }

    if (students.length === 0) {
      setError('没有有效的学生数据行。请按 CSV: 学号,姓名,密码[,邮箱][,手机号]');
      setSuccess('');
      return;
    }

    try {
      setLoading(true);
      setError('');
      setSuccess('');

      const resp = await batchImportStudents({ students });

      const msg = resp?.message || `批量导入完成: 成功 ${resp?.success_count ?? students.length} 个, 失败 ${resp?.failed_count ?? 0} 个`;
      setSuccess(msg);
      if (resp?.errors && resp.errors.length > 0) {
        const preview = resp.errors.slice(0, 5).join('；');
        setError(`部分导入失败：${preview}${resp.errors.length > 5 ? ' 等' : ''}`);
      }

      // 刷新用户列表
      const usersData = await getUsers(1, 50, 'student');
      setUsers(usersData.users);
      setImportData('');
    } catch (error) {
      setError(error.response?.data?.error || '批量导入失败');
    } finally {
      setLoading(false);
    }
  };

  const handleCreateProblem = async () => {
    try {
      setLoading(true);
      setError('');
      setSuccess('');

      // 添加调试信息
      console.log('提交的题目数据:', problemForm);
      console.log('题目类型:', problemForm.type);
      console.log('完整表单数据:', JSON.stringify(problemForm, null, 2));

      if (isEditing && editingProblem) {
        console.log('更新题目，ID:', editingProblem.id);
        await updateProblem(editingProblem.id, problemForm);
        setSuccess('题目更新成功！');
      } else {
        console.log('创建新题目');
        await createProblem(problemForm);
        setSuccess('题目创建成功！');
      }

      // 重置表单
      setProblemForm({
        title: '',
        description: '',
        type: 'programming',
        test_cases: '',
        expected_output: '',
        difficulty: 'easy',
        time_limit: 1000,
        memory_limit: 128,
        options: [],
        correct_answers: [],
        is_multiple_choice: false,
        answer: ''
      });

      setIsEditing(false);
      setEditingProblem(null);

      // 刷新题目列表
      const problemsData = await getProblems(1, 50);
      setProblems(problemsData.problems);

      // 切换到题目列表
      setActiveSubTab('list');

    } catch (error) {
      console.error('题目操作失败:', error);
      console.error('错误响应:', error.response);
      setError(error.response?.data?.error || (isEditing ? '更新题目失败' : '创建题目失败'));
    } finally {
      setLoading(false);
    }
  };

  const handleEditProblem = (problem) => {
    setEditingProblem(problem);
    setProblemForm({
      title: problem.title,
      description: problem.description,
      type: problem.type || 'programming',
      test_cases: problem.test_cases || '',
      expected_output: problem.expected_output || '',
      difficulty: problem.difficulty,
      time_limit: problem.time_limit || 1000,
      memory_limit: problem.memory_limit || 128,
      options: problem.type === 'judge' ? [
        { id: 'judge_correct', text: '正确', is_correct: false },
        { id: 'judge_incorrect', text: '错误', is_correct: false }
      ] : (problem.options || []),
      correct_answers: problem.correct_answers || [],
      is_multiple_choice: problem.is_multiple_choice || false,
      answer: problem.answer || ''
    });

    // 如果是判断题，需要根据原有的正确答案设置选项状态
    if (problem.type === 'judge' && problem.correct_answers && problem.correct_answers.length > 0) {
      setProblemForm(prev => ({
        ...prev,
        options: [
          { id: 'judge_correct', text: '正确', is_correct: problem.correct_answers.includes('正确') },
          { id: 'judge_incorrect', text: '错误', is_correct: problem.correct_answers.includes('错误') }
        ]
      }));
    }

    setIsEditing(true);
    setActiveSubTab('create');
  };

  const handleDeleteProblem = async (problemId) => {
    if (!window.confirm('确定要删除这个题目吗？此操作不可恢复。')) {
      return;
    }

    try {
      setLoading(true);
      setError('');
      setSuccess('');
      await deleteProblem(problemId);
      setSuccess('题目删除成功！');

      // 刷新题目列表
      const problemsData = await getProblems(1, 50);
      setProblems(problemsData.problems);
    } catch (error) {
      setError(error.response?.data?.error || '删除题目失败');
    } finally {
      setLoading(false);
    }
  };

  // 根据题目特征推断题目类型
  const inferProblemType = (problem) => {
    console.log('开始推断题目类型，题目数据:', problem);

    // 优先使用明确设置的题目类型
    if (problem.type && problem.type !== 'undefined') {
      console.log('使用明确设置的题目类型:', problem.type);
      return problem.type;
    }

    // 如果没有明确设置类型，则通过特征推断
    console.log('没有明确设置类型，开始推断...');

    // 检查所有可能的选项字段
    const options = problem.options || problem.choices || problem.alternatives || problem.option_list || [];
    console.log('检查到的选项:', options);

    // 如果有选项，判断是选择题还是判断题
    if (Array.isArray(options) && options.length > 0) {
      console.log('发现选项，数量:', options.length);

      // 检查是否是判断题（只有两个选项：正确、错误）
      if (options.length === 2) {
        const optionTexts = options.map(opt => {
          const text = opt.text || opt.option_text || opt.content || opt.label || '';
          console.log('选项文本:', text);
          return text;
        });

        if (optionTexts.includes('正确') && optionTexts.includes('错误')) {
          console.log('推断为判断题');
          return 'judge';
        }
      }

      console.log('推断为选择题');
      return 'choice';
    }

    // 检查所有可能的答案字段
    const answer = problem.answer || problem.solution || problem.correct_answer || problem.answer_text || '';
    console.log('检查到的答案:', answer);

    if (answer && answer.trim()) {
      console.log('推断为简答题');
      return 'short_answer';
    }

    // 检查编程题相关字段
    const testCases = problem.test_cases || problem.test_cases_text || problem.test_input || '';
    const expectedOutput = problem.expected_output || problem.expected_result || problem.expected_answer || '';
    console.log('检查到的测试用例:', testCases);
    console.log('检查到的期望输出:', expectedOutput);

    if (testCases && expectedOutput) {
      console.log('推断为编程题');
      return 'programming';
    }

    // 如果都无法推断，默认返回编程题
    console.log('无法推断类型，默认返回编程题');
    return 'programming';
  };

  // 根据ID获取学校名称
  const getSchoolName = (schoolId) => {
    if (!schoolId) return '-';
    const school = schools.find(s => s.id === parseInt(schoolId) || s.id === schoolId);
    return school ? school.name : schoolId;
  };

  // 根据ID获取专业名称
  const getMajorName = (majorId) => {
    if (!majorId) return '-';
    const major = majors.find(m => m.id === majorId);
    return major ? major.name : majorId;
  };

  // 根据ID获取班级名称
  const getClassName = (classId) => {
    if (!classId) return '-';
    const classItem = classes.find(c => c.id === classId);
