import React, { useState, useEffect } from 'react';
import { getUsers, getSchools, getMajors, getClasses, batchImportStudents, getProblems, createProblem, updateProblem, deleteProblem, updateUser, deleteUser, registerStudent, getTeacherCourses, createCourse, updateCourse, deleteCourse, getCourseAssignments, createAssignment, updateAssignment, deleteAssignment } from '../services/api';
import './TeacherDashboardPage.css';
import AIProblemGenerationPage from './AIProblemGenerationPage';
import { mockCourses, mockAssignments, formatDate } from '../testData/courses';
import WhaleIcon from '../components/WhaleIcon';

const TeacherDashboardPage = () => {
  const [activeTab, setActiveTab] = useState('students');
  const [activeSubTab, setActiveSubTab] = useState('list'); // å­æ ‡ç­¾é¡µ
  const [users, setUsers] = useState([]);
  const [problems, setProblems] = useState([]);
  const [schools, setSchools] = useState([]);
  const [majors, setMajors] = useState([]);
