  });

  // ä½œä¸šç›¸å…³çŠ¶æ€
  const [assignments, setAssignments] = useState([]);
  const [assignmentForm, setAssignmentForm] = useState({
    name: '',
    description: '',
    requirements: '',
    due_date: '',
    course_id: '',
    problem_ids: []
  });

  // ç¼–è¾‘è¯¾ç¨‹çŠ¶æ€
  const [editingCourse, setEditingCourse] = useState(null);
  const [isEditingCourse, setIsEditingCourse] = useState(false);

  // ç¼–è¾‘ä½œä¸šçŠ¶æ€
  const [editingAssignment, setEditingAssignment] = useState(null);
  const [isEditingAssignment, setIsEditingAssignment] = useState(false);
  const [editingAssignmentContext, setEditingAssignmentContext] = useState(''); // è®°å½•ç¼–è¾‘ä½œä¸šæ—¶çš„ä¸Šä¸‹æ–‡

  // æ·»åŠ å­¦ç”Ÿåˆ°è¯¾ç¨‹çš„çŠ¶æ€
  const [addingStudentContext, setAddingStudentContext] = useState(''); // è®°å½•æ·»åŠ å­¦ç”Ÿæ—¶çš„ä¸Šä¸‹æ–‡

  // å­¦ç”Ÿ-æ•™å­¦ç­å…³è”å…³ç³»çŠ¶æ€
  const [studentClassRelations, setStudentClassRelations] = useState([]); // å­˜å‚¨å­¦ç”Ÿä¸Žæ•™å­¦ç­çš„å…³è”å…³ç³»

  useEffect(() => {
    fetchInitialData();
  }, []);

  const fetchInitialData = async () => {
    try {
      setLoading(true);
      setError('');
