  const handleBatchImport = async () => {
    if (!importData.trim()) {
      setError('è¯·è¾“å…¥è¦å¯¼å…¥çš„å­¦ç”Ÿæ•°æ®');
      setSuccess('');
      return;
    }

    if (!selectedSchool || !selectedMajor || !selectedClass) {
      setError('è¯·é€‰æ‹©å­¦æ ¡ã€ä¸“ä¸šå’Œç­çº§');
      setSuccess('');
      return;
    }

    // è§£æžCSVæ•°æ®ä¸ºå­¦ç”Ÿå¯¹è±¡æ•°ç»„
    const lines = importData
      .split(/\r?\n/)
      .map(l => l.trim())
      .filter(l => l && !/^\s*#/.test(l));
    const students = [];
    for (const line of lines) {
      const parts = line.split(',').map(s => s.trim());
      if (parts.length < 3) continue; // è‡³å°‘: å­¦å·,å§“å,å¯†ç 
      const [studentNo, name, password, email, phone] = parts;
      students.push({
        username: studentNo,
        name,
        password,
        email: email || undefined,
        phone: phone || undefined,
        school_id: selectedSchool,
        major_id: selectedMajor,
        class_id: selectedClass,
        role: 'student'
      });
    }

    if (students.length === 0) {
      setError('æ²¡æœ‰æœ‰æ•ˆçš„å­¦ç”Ÿæ•°æ®è¡Œã€‚è¯·æŒ‰ CSV: å­¦å·,å§“å,å¯†ç [,é‚®ç®±][,æ‰‹æœºå·]');
      setSuccess('');
      return;
    }

    try {
      setLoading(true);
      setError('');
      setSuccess('');

      const resp = await batchImportStudents({ students });

      const msg = resp?.message || `æ‰¹é‡å¯¼å…¥å®Œæˆ: æˆåŠŸ ${resp?.success_count ?? students.length} ä¸ª, å¤±è´¥ ${resp?.failed_count ?? 0} ä¸ª`;
      setSuccess(msg);
      if (resp?.errors && resp.errors.length > 0) {
        const preview = resp.errors.slice(0, 5).join('ï¼›');
        setError(`éƒ¨åˆ†å¯¼å…¥å¤±è´¥ï¼š${preview}${resp.errors.length > 5 ? ' ç­‰' : ''}`);
      }

      // åˆ·æ–°ç”¨æˆ·åˆ—è¡¨
      const usersData = await getUsers(1, 50, 'student');
      setUsers(usersData.users);
      setImportData('');
    } catch (error) {
      setError(error.response?.data?.error || 'æ‰¹é‡å¯¼å…¥å¤±è´¥');
    } finally {
      setLoading(false);
    }
  };

  const handleCreateProblem = async () => {
    try {
      setLoading(true);
      setError('');
      setSuccess('');

      if (isEditing && editingProblem) {
        await updateProblem(editingProblem.id, problemForm);
        setSuccess('é¢˜ç›®æ›´æ–°æˆåŠŸï¼');
      } else {
        await createProblem(problemForm);
        setSuccess('é¢˜ç›®åˆ›å»ºæˆåŠŸï¼');
      }

      // é‡ç½®è¡¨å•
