import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { getSchools, getMajors, getClasses } from '../services/api';
import './RegisterPage.css';

const RegisterPage = () => {
  console.log('RegisterPage组件开始渲染');

  // 测试弹窗 - 确认组件加载
  useEffect(() => {
    alert('RegisterPage组件已加载！');
  }, []);

  const [form, setForm] = useState({
    // 学生自助注册已关闭，仅保留教师注册入口说明
    username: '',
    password: '',
    confirmPassword: '',
    name: '',
    email: '',
    phone: '',
    role: 'student',
    school_id: '',
    major_id: '',
    class_id: ''
  });

  const [schools, setSchools] = useState([]);
  const [majors, setMajors] = useState([]);
  const [classes, setClasses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  console.log('RegisterPage状态初始化完成:', { schools, majors, classes, form });

  useEffect(() => {
    console.log('RegisterPage useEffect被触发');
    fetchSchools();
  }, []);

  const fetchSchools = async () => {
    try {
      console.log('开始获取学校列表...');
      const schoolsData = await getSchools();
      console.log('获取到的学校数据:', schoolsData);
      setSchools(schoolsData);
      console.log('学校数据已设置到状态:', schoolsData);
    } catch (error) {
      console.error('获取学校列表失败:', error);
    }
  };

  const handleSchoolChange = async (schoolId) => {
    console.log('=== handleSchoolChange函数开始执行 ===');
    console.log('传入的schoolId:', schoolId, '类型:', typeof schoolId);
    console.log('选择前的表单状态:', form);

    // 先更新表单状态
    setForm(prev => {
      const newForm = { ...prev, school_id: schoolId, major_id: '', class_id: '' };
      console.log('setForm回调执行，新表单状态:', newForm);
      return newForm;
    });

    console.log('setForm调用完成，等待状态更新...');

    // 清空相关状态
    setMajors([]);
    setClasses([]);
    console.log('已清空专业和班级列表');

    if (schoolId) {
      try {
        console.log('开始获取专业数据，schoolId:', schoolId);
        const majorsData = await getMajors(schoolId);
        console.log('获取到的专业数据:', majorsData);
        setMajors(majorsData);
        console.log('专业数据已设置到状态');
      } catch (error) {
        console.error('获取专业列表失败:', error);
      }
    }

    console.log('=== handleSchoolChange函数执行完成 ===');
  };

  const handleMajorChange = async (majorId) => {
    setForm(prev => ({ ...prev, major_id: majorId, class_id: '' }));
    setClasses([]);

    if (majorId) {
      try {
        const classesData = await getClasses(majorId);
        setClasses(classesData);
      } catch (error) {
        console.error('获取班级列表失败:', error);
      }
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    // 学生自助注册已关闭
    setError('当前已关闭自助注册，请联系任课教师创建账号');
  };

  const handleInputChange = (field, value) => {
    setForm(prev => ({ ...prev, [field]: value }));
  };

  console.log('RegisterPage准备渲染JSX，当前状态:', { schools, majors, classes, form });

  return (
    <div className="register-container">
      <div className="register-card">
        <h1>用户注册</h1>

        {/* 测试信息 - 确认组件渲染 */}
        <div style={{
          background: 'yellow',
          border: '3px solid red',
          padding: '20px',
          margin: '20px 0',
          fontSize: '18px',
          fontWeight: 'bold',
          textAlign: 'center'
        }}>
          🚨 测试信息：如果你能看到这个黄色框，说明RegisterPage组件正在渲染！
          <br />
          当前时间: {new Date().toLocaleString()}
          <br />
          学校数量: {schools.length}
          <br />
          表单状态: {JSON.stringify(form)}
        </div>

        {/* 测试按钮 */}
        <button 
          type="button" 
          onClick={() => {
            alert('测试按钮工作正常！');
            console.log('测试按钮被点击');
          }}
          style={{
            background: 'red',
            color: 'white',
            padding: '10px',
            margin: '10px',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer'
          }}
        >
          测试按钮 - 点击我
        </button>

        {error && <div className="error-message">⚠️ {error}</div>}
        {success && <div className="success-message">✅ {success}</div>}

        <form onSubmit={handleSubmit}>
          <div className="info-message" style={{marginBottom: '12px'}}>
            已关闭学生自助注册。请联系任课教师创建学生账号。
          </div>
          {/* 基本信息 */}
          <div className="form-section">
            <h3>基本信息</h3>

            <div className="form-group">
              <label>用户名 *</label>
              <input
                type="text"
                value={form.username}
                onChange={(e) => handleInputChange('username', e.target.value)}
                placeholder="请输入用户名"
                required
              />
            </div>

            <div className="form-group">
              <label>姓名 *</label>
              <input
                type="text"
                value={form.name}
                onChange={(e) => handleInputChange('name', e.target.value)}
                placeholder="请输入真实姓名"
                required
              />
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>密码 *</label>
                <input
                  type="password"
                  value={form.password}
                  onChange={(e) => handleInputChange('password', e.target.value)}
                  placeholder="至少6位"
                  required
                />
              </div>

              <div className="form-group">
                <label>确认密码 *</label>
                <input
                  type="password"
                  value={form.confirmPassword}
                  onChange={(e) => handleInputChange('confirmPassword', e.target.value)}
                  placeholder="再次输入密码"
                  required
                />
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>邮箱</label>
                <input
                  type="email"
                  value={form.email}
                  onChange={(e) => handleInputChange('email', e.target.value)}
                  placeholder="请输入邮箱地址"
                />
              </div>

              <div className="form-group">
                <label>手机号</label>
                <input
                  type="tel"
                  value={form.phone}
                  onChange={(e) => handleInputChange('phone', e.target.value)}
                  placeholder="请输入手机号"
                />
              </div>
            </div>

            <div className="form-group">
              <label>用户角色 *</label>
              <select
                value={form.role}
                onChange={(e) => handleInputChange('role', e.target.value)}
                required
              >
                <option value="student">学生</option>
                <option value="teacher">教师</option>
              </select>
            </div>
          </div>

          {/* 学校信息（可选） */}
          {form.role === 'student' && (
            <div className="form-section">
              <h3>学校信息（可选）</h3>

              <div className="form-group">
                <label>学校</label>
                <select
                  value={form.school_id}
                  onChange={(e) => {
                    const newValue = e.target.value;
                    console.log('选择器onChange被触发，新值:', newValue);
                    alert(`选择器工作正常！选中值: ${newValue}`);

                    // 直接更新状态
                    setForm(prev => {
                      const newForm = { ...prev, school_id: newValue, major_id: '', class_id: '' };
                      console.log('更新后的表单状态:', newForm);
                      return newForm;
                    });

                    // 调用原来的处理函数
                    handleSchoolChange(newValue);
                  }}
                >
                  <option value="">请选择学校</option>
                  {schools.map(school => (
                    <option key={school.id} value={school.id}>
                      {school.name}
                    </option>
                  ))}
                </select>
                {/* 调试信息 */}
                <div style={{fontSize: '12px', color: '#666', marginTop: '4px'}}>
                  当前选中值: {form.school_id} | 学校数量: {schools.length}
                </div>
                <div style={{fontSize: '12px', color: '#666', marginTop: '2px'}}>
                  表单状态: {JSON.stringify(form)}
                </div>
              </div>

              <div className="form-group">
                <label>专业</label>
                <select
                  value={form.major_id}
                  onChange={(e) => handleMajorChange(e.target.value)}
                  disabled={!form.school_id}
                >
                  <option value="">请选择专业</option>
                  {majors.map(major => (
                    <option key={major.id} value={major.id}>
                      {major.name}
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label>班级</label>
                <select
                  value={form.class_id}
                  onChange={(e) => handleInputChange('class_id', e.target.value)}
                  disabled={!form.major_id}
                >
                  <option value="">请选择班级</option>
                  {classes.map(cls => (
                    <option key={cls.id} value={cls.id}>
                      {cls.name}
                    </option>
                  ))}
                </select>
              </div>
            </div>
          )}

          <div className="form-actions">
            <button 
              type="submit" 
              disabled={loading || success}
              className="register-btn"
            >
              {loading ? '注册中...' : success ? '注册成功！' : '注册'}
            </button>
          </div>
        </form>

        <div className="login-link">
          已有账户？<Link to="/login">立即登录</Link>
        </div>
      </div>
    </div>
  );
};

export default RegisterPage;
