import React, { useState, useEffect } from 'react';
import { getUsers, getSchools, getMajors, getClasses, registerTeacher, updateUser, deleteUser, getAllCourses, createCourse, updateCourse, deleteCourse } from '../services/api';
import './AdminDashboardPage.css';
import WhaleIcon from '../components/WhaleIcon';

const AdminDashboardPage = () => {
  const [activeTab, setActiveTab] = useState('teachers');
  const [activeSubTab, setActiveSubTab] = useState('list');
  const [teachers, setTeachers] = useState([]);
  const [schools, setSchools] = useState([]);
  const [majors, setMajors] = useState([]);
  const [classes, setClasses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // 编辑教师相关状态
  const [editingTeacher, setEditingTeacher] = useState(null);
  const [editForm, setEditForm] = useState({
    email: '',
    phone: '',
    school_id: '',
    major_id: '',
    is_active: true,
    new_password: ''
  });

  // 添加教师相关状态
  const [addTeacherForm, setAddTeacherForm] = useState({
    job_no: '',
    name: '',
    password: '',
    email: '',
    phone: '',
    school_id: '',
    major_id: ''
  });

  // 课程相关状态
  const [courses, setCourses] = useState([]);
  const [courseForm, setCourseForm] = useState({
    name: '',
    description: '',
    class_id: '',
    major_id: '',
    school_id: '',
    teacher_id: ''
  });

  // 编辑课程状态
  const [editingCourse, setEditingCourse] = useState(null);
  const [isEditingCourse, setIsEditingCourse] = useState(false);

  useEffect(() => {
    fetchInitialData();
  }, []);

  const fetchInitialData = async () => {
    try {
      setLoading(true);
      setError('');
      setSuccess('');

      // 从真实API获取数据
      try {
        const schoolsData = await getSchools();
        setSchools(schoolsData);
        console.log('从API获取的学校数据:', schoolsData);
      } catch (apiError) {
        console.warn('API获取学校数据失败:', apiError);
        setSchools([]);
      }

      // 尝试从API获取教师数据
      try {
        const teachersData = await getUsers(1, 50, 'teacher');
        setTeachers(teachersData.users);
      } catch (apiError) {
        console.warn('API获取教师数据失败:', apiError);
        setTeachers([]);
      }

      // 尝试从API获取课程数据
      try {
        const coursesData = await getAllCourses(1, 50);
        setCourses(coursesData.courses || []);
      } catch (apiError) {
        console.warn('API获取课程数据失败:', apiError);
        setCourses([]);
      }

    } catch (error) {
      setError(error?.response?.data?.error || '获取数据失败');
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSchoolChange = async (schoolId) => {
    console.log('学校选择改变:', schoolId);
    setAddTeacherForm(prev => ({ ...prev, school_id: schoolId, major_id: '' }));

    if (schoolId) {
      try {
        const majorsData = await getMajors(schoolId);
        setMajors(majorsData);
        console.log('从API获取的专业数据:', majorsData);
      } catch (error) {
        console.error('Error fetching majors:', error);
        setMajors([]);
      }
    } else {
      setMajors([]);
    }
  };

  // 课程相关处理函数
  const handleCourseSchoolChange = async (schoolId) => {
    setCourseForm(prev => ({ 
      ...prev, 
      school_id: schoolId,
      major_id: '',
      class_id: ''
    }));

    if (schoolId) {
      try {
        const majorsData = await getMajors(schoolId);
        setMajors(majorsData);
      } catch (error) {
        console.error('Error fetching majors:', error);
      }
    } else {
      setMajors([]);
    }
  };

  const handleCourseMajorChange = async (majorId) => {
    setCourseForm(prev => ({ 
      ...prev, 
      major_id: majorId,
      class_id: ''
    }));

    if (majorId) {
      try {
        const classesData = await getClasses(majorId);
        setClasses(classesData);
      } catch (error) {
        console.error('Error fetching classes:', error);
      }
    } else {
      setClasses([]);
    }
  };

  const handleCreateCourse = async () => {
    try {
      setLoading(true);
      setError('');
      setSuccess('');

      if (isEditingCourse && editingCourse) {
        await updateCourse(editingCourse.id, courseForm);
        setSuccess('课程更新成功！');
      } else {
        await createCourse(courseForm);
        setSuccess('课程创建成功！');
      }

      // 重置表单
      setCourseForm({
        name: '',
        description: '',
        class_id: '',
        major_id: '',
        school_id: '',
        teacher_id: ''
      });

      setIsEditingCourse(false);
      setEditingCourse(null);

      // 刷新课程列表
      const coursesData = await getAllCourses(1, 50);
      setCourses(coursesData.courses || []);

      // 切换到课程列表
      setActiveSubTab('list');

    } catch (error) {
      setError(error.response?.data?.error || (isEditingCourse ? '更新课程失败' : '创建课程失败'));
    } finally {
      setLoading(false);
    }
  };

  const handleEditCourse = (course) => {
    setEditingCourse(course);
    setCourseForm({
      name: course.name,
      description: course.description || '',
      school_id: course.school_id || '',
      major_id: course.major_id || '',
      class_id: course.class_id || '',
      teacher_id: course.teacher_id || ''
    });
    setIsEditingCourse(true);
    setActiveSubTab('add');
  };

  const handleDeleteCourse = async (courseId) => {
    if (!window.confirm('确定要删除该课程吗？此操作不可恢复。')) return;
    try {
      setLoading(true);
      setError('');
      setSuccess('');

      await deleteCourse(courseId);
      setSuccess('课程已删除');

      // 刷新课程列表
      const coursesData = await getAllCourses(1, 50);
      setCourses(coursesData.courses || []);

    } catch (error) {
      setError(error.response?.data?.error || '删除课程失败');
    } finally {
      setLoading(false);
    }
  };

  const handleAddTeacher = async () => {
    try {
      setLoading(true);
      setError('');
      setSuccess('');

      await registerTeacher(addTeacherForm);

      setSuccess('教师账号创建成功！');

      // 重置表单
      setAddTeacherForm({
        job_no: '',
        name: '',
        password: '',
        email: '',
        phone: '',
        school_id: '',
        major_id: ''
      });

      // 刷新教师列表
      const teachersData = await getUsers(1, 50, 'teacher');
      setTeachers(teachersData.users);

      // 切换到教师列表
      setActiveSubTab('list');

    } catch (error) {
      setError(error.response?.data?.error || '创建教师账号失败');
    } finally {
      setLoading(false);
    }
  };

  const handleEditTeacherClick = (teacher) => {
    setEditingTeacher(teacher);
    setEditForm({
      email: teacher.email || '',
      phone: teacher.phone || '',
      school_id: teacher.school_id || '',
      major_id: teacher.major_id || '',
      is_active: !!teacher.is_active,
      new_password: ''
    });
    setError('');
    setSuccess('');
  };

  const handleSaveTeacher = async () => {
    if (!editingTeacher) return;
    try {
      setLoading(true);
      setError('');
      setSuccess('');
      await updateUser(editingTeacher.id, editForm);
      setSuccess('教师信息已更新');
      // 刷新列表
      const teachersData = await getUsers(1, 50, 'teacher');
      setTeachers(teachersData.users);
      setEditingTeacher(null);
    } catch (error) {
      setError(error.response?.data?.error || '更新教师信息失败');
    } finally {
      setLoading(false);
    }
  };

  const handleCancelEditTeacher = () => {
    setEditingTeacher(null);
    setEditForm({ email: '', phone: '', school_id: '', major_id: '', is_active: true, new_password: '' });
  };

  const handleDeleteTeacher = async (userId) => {
    if (!window.confirm('确定要删除该教师吗？此操作不可恢复。')) return;
    try {
      setLoading(true);
      setError('');
      setSuccess('');
      await deleteUser(userId);
      setSuccess('教师已删除');
      const teachersData = await getUsers(1, 50, 'teacher');
      setTeachers(teachersData.users);
    } catch (error) {
      setError(error.response?.data?.error || '删除教师失败');
    } finally {
      setLoading(false);
    }
  };



  return (
    <div className="admin-dashboard">
      <div className="dashboard-header">
        <h1>管理员控制台</h1>
      </div>

      {error && <div className="error-message">{error}</div>}
      {success && <div className="success-message">{success}</div>}

      <div className="dashboard-tabs">
        <button 
          className={`tab-btn ${activeTab === 'teachers' ? 'active' : ''}`}
          onClick={() => {
            setActiveTab('teachers');
            setActiveSubTab('list');
            // 清除课程相关状态
            setEditingCourse(null);
          }}
        >
          教师管理
        </button>
        <button 
          className={`tab-btn ${activeTab === 'courses' ? 'active' : ''}`}
          onClick={() => {
            setActiveTab('courses');
            setActiveSubTab('list');
            // 清除教师相关状态
            setEditingTeacher(null);
          }}
        >
          课程管理
        </button>
      </div>

      {activeTab === 'teachers' && (
        <div className="tab-content">
          <div className="sub-tabs">
            <button 
              className={`sub-tab-btn ${activeSubTab === 'list' ? 'active' : ''}`}
              onClick={() => setActiveSubTab('list')}
            >
              教师列表
            </button>
            <button 
              className={`sub-tab-btn ${activeSubTab === 'add' ? 'active' : ''}`}
              onClick={() => setActiveSubTab('add')}
            >
              添加教师
            </button>
          </div>

          {activeSubTab === 'list' && (
            <div className="section-content">
              <div className="section-header">
                <h2>教师列表</h2>
                <button 
                  className="add-btn"
                  onClick={() => setActiveSubTab('add')}
                >
                  添加教师
                </button>
              </div>

                <div className="teachers-table">
                  <table>
                    <thead>
                      <tr>
                        <th>ID</th>
                        <th>工号</th>
                        <th>姓名</th>
                        <th>邮箱</th>
                        <th>学校</th>
                        <th>院部</th>
                        <th>状态</th>
                        <th>操作</th>
                      </tr>
                    </thead>
                    <tbody>
                      {teachers.map(teacher => (
                        <tr key={teacher.id}>
                          <td>{teacher.id}</td>
                          <td>{teacher.username}</td>
                          <td>{teacher.name}</td>
                          <td>{teacher.email || '-'}</td>
                          <td>{teacher.school_id || '-'}</td>
                          <td>{teacher.major_id || '-'}</td>
                          <td>
                            <span className={`status-badge ${teacher.is_active ? 'active' : 'inactive'}`}>
                              {teacher.is_active ? '活跃' : '禁用'}
                            </span>
                          </td>
                          <td>
                            <button className="action-btn edit" onClick={() => handleEditTeacherClick(teacher)}>编辑</button>
                            <button className="action-btn delete" onClick={() => handleDeleteTeacher(teacher.id)}>删除</button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            )}

          {activeSubTab === 'add' && (
            <div className="section-content">
              <div className="section-header">
                <h2>➕ 添加教师</h2>
                <div className="header-actions">
                  <button 
                    className="debug-btn"
                    onClick={() => {
                      console.log('当前状态:', { schools, majors, addTeacherForm });
                      alert(`学校数量: ${schools.length}\n院部数量: ${majors.length}\n选择的学校ID: ${addTeacherForm.school_id}`);
                    }}
                  >
                    🐛 调试信息
                  </button>
                  <button 
                    className="secondary-btn"
                    onClick={() => setActiveSubTab('list')}
                  >
                    ← 返回教师列表
                  </button>
                </div>
              </div>

                <div className="add-teacher-form">
                  <div className="form-row">
                    <div className="form-group">
                      <label>工号：<span className="required-mark">*</span></label>
                      <input 
                        type="text" 
                        placeholder="请输入工号"
                        value={addTeacherForm.job_no}
                        onChange={(e) => setAddTeacherForm(prev => ({ ...prev, job_no: e.target.value }))}
                      />
                    </div>

                    <div className="form-group">
                      <label>姓名：<span className="required-mark">*</span></label>
                      <input 
                        type="text" 
                        placeholder="请输入教师姓名"
                        value={addTeacherForm.name}
                        onChange={(e) => setAddTeacherForm(prev => ({ ...prev, name: e.target.value }))}
                      />
                    </div>
                  </div>

                  <div className="form-row">
                    <div className="form-group">
                      <label>密码：<span className="required-mark">*</span></label>
                      <input 
                        type="password" 
                        placeholder="请输入初始密码"
                        value={addTeacherForm.password}
                        onChange={(e) => setAddTeacherForm(prev => ({ ...prev, password: e.target.value }))}
                      />
                    </div>

                    <div className="form-group">
                      <label>邮箱：</label>
                      <input 
                        type="email" 
                        placeholder="请输入邮箱地址"
                        value={addTeacherForm.email}
                        onChange={(e) => setAddTeacherForm(prev => ({ ...prev, email: e.target.value }))}
                      />
                    </div>
                  </div>

                  <div className="form-row">
                    <div className="form-group">
                      <label>手机号：</label>
                      <input 
                        type="tel" 
                        placeholder="请输入手机号码"
                        value={addTeacherForm.phone}
                        onChange={(e) => setAddTeacherForm(prev => ({ ...prev, phone: e.target.value }))}
                      />
                    </div>

                    <div className="form-group">
                      <label>学校：<span className="required-mark">*</span></label>
                      <select 
                        value={addTeacherForm.school_id} 
                        onChange={(e) => handleSchoolChange(e.target.value)}
                      >
                        <option value="">请选择学校</option>
                        {schools.map(school => (
                          <option key={school.id} value={school.id}>
                            {school.name}
                          </option>
                        ))}
                      </select>
                      <small>可用学校数量: {schools.length}</small>
                    </div>
                  </div>

                  <div className="form-row">
                    <div className="form-group">
                      <label>院部：<span className="required-mark">*</span></label>
                      <select 
                        value={addTeacherForm.major_id} 
                        onChange={(e) => setAddTeacherForm(prev => ({ ...prev, major_id: e.target.value }))}
                        disabled={!addTeacherForm.school_id}
                      >
                        <option value="">请选择院部</option>
                        {majors.map(major => (
                          <option key={major.id} value={major.id}>
                            {major.name}
                          </option>
                        ))}
                      </select>
                      <small>可用院部数量: {majors.length} | 学校ID: {addTeacherForm.school_id}</small>
                    </div>

                    <div className="form-group">
                      <label>&nbsp;</label>
                      <div className="form-help">
                        <p>教师账号创建后，工号、姓名、学校、院部信息不可修改</p>
                        <p>教师只能修改自己的密码</p>
                      </div>
                    </div>
                  </div>

                  <div className="form-actions">
                    <button 
                      className="submit-btn"
                      onClick={handleAddTeacher}
                      disabled={loading || !addTeacherForm.job_no || !addTeacherForm.name || !addTeacherForm.password || !addTeacherForm.school_id || !addTeacherForm.major_id}
                    >
                      {loading ? '创建中...' : '创建教师账号'}
                    </button>
                  </div>
                </div>
              </div>
            )}
          </div>
        )}

      {activeTab === 'courses' && (
        <div className="tab-content">
          <div className="sub-tabs">
            <button 
              className={`sub-tab-btn ${activeSubTab === 'list' ? 'active' : ''}`}
              onClick={() => setActiveSubTab('list')}
            >
              课程列表
            </button>
            <button 
              className={`sub-tab-btn ${activeSubTab === 'add' ? 'active' : ''}`}
              onClick={() => setActiveSubTab('add')}
            >
              添加课程
            </button>
          </div>

          {activeSubTab === 'list' && (
            <div className="section-content">
              <div className="section-header">
                <h2>课程列表</h2>
                <button 
                  className="add-btn"
                  onClick={() => setActiveSubTab('add')}
                >
                  添加课程
                </button>
              </div>

                <div className="courses-table">
                  <table>
                    <thead>
                      <tr>
                        <th>ID</th>
                        <th>课程名称</th>
                        <th>描述</th>
                        <th>教师工号</th>
                        <th>教师姓名</th>
                        <th>班级</th>
                        <th>院部</th>
                        <th>学校</th>
                        <th>操作</th>
                      </tr>
                    </thead>
                    <tbody>
                      {courses.map(course => (
                        <tr key={course.id}>
                          <td>{course.id}</td>
                          <td>{course.name}</td>
                          <td>{course.description || '-'}</td>
                          <td>{course.teacher_username || '-'}</td>
                          <td>{course.teacher_name || '-'}</td>
                          <td>{course.class_id || '-'}</td>
                          <td>{course.major_name || '-'}</td>
                          <td>{course.school_name || '-'}</td>
                          <td>
                            <button className="action-btn edit" onClick={() => handleEditCourse(course)}>编辑</button>
                            <button className="action-btn delete" onClick={() => handleDeleteCourse(course.id)}>删除</button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            )}

          {activeSubTab === 'add' && (
            <div className="section-content">
              <div className="section-header">
                <h2>{isEditingCourse ? '✏️ 编辑课程' : '➕ 添加课程'}</h2>
                <div className="header-actions">
                  <button 
                    className="debug-btn"
                    onClick={() => {
                      console.log('当前状态:', { schools, majors, courseForm });
                      alert(`学校数量: ${schools.length}\n院部数量: ${majors.length}\n选择的学校ID: ${courseForm.school_id}`);
                    }}
                  >
                    🐛 调试信息
                  </button>
                  <button 
                    className="secondary-btn"
                    onClick={() => setActiveSubTab('list')}
                  >
                    ← 返回课程列表
                  </button>
                </div>
              </div>

                <div className="add-course-form">
                  <div className="form-row">
                    <div className="form-group">
                      <label>课程名称：<span className="required-mark">*</span></label>
                      <input 
                        type="text" 
                        placeholder="请输入课程名称"
                        value={courseForm.name}
                        onChange={(e) => setCourseForm(prev => ({ ...prev, name: e.target.value }))}
                      />
                    </div>

                    <div className="form-group">
                      <label>课程描述：</label>
                      <textarea 
                        placeholder="请输入课程描述"
                        value={courseForm.description}
                        onChange={(e) => setCourseForm(prev => ({ ...prev, description: e.target.value }))}
                      />
                    </div>
                  </div>

                  <div className="form-row">
                    <div className="form-group">
                      <label>授课教师：<span className="required-mark">*</span></label>
                      <select 
                        value={courseForm.teacher_id} 
                        onChange={(e) => setCourseForm(prev => ({ ...prev, teacher_id: e.target.value }))}
                      >
                        <option value="">请选择授课教师</option>
                        {teachers.map(teacher => (
                          <option key={teacher.id} value={teacher.id}>
                            {teacher.name}（{teacher.username}）
                          </option>
                        ))}
                      </select>
                      <small>可用教师数量: {teachers.length}</small>
                    </div>
                  </div>

                  <div className="form-row">
                    <div className="form-group">
                      <label>班级：<span className="required-mark">*</span></label>
                      <select 
                        value={courseForm.class_id} 
                        onChange={(e) => setCourseForm(prev => ({ ...prev, class_id: e.target.value }))}
                        disabled={!courseForm.major_id}
                      >
                        <option value="">请选择班级</option>
                        {classes.map(cls => (
                          <option key={cls.id} value={cls.id}>
                            {cls.name}
                          </option>
                        ))}
                      </select>
                      <small>可用班级数量: {classes.length} | 院部ID: {courseForm.major_id}</small>
                    </div>

                    <div className="form-group">
                      <label>院部：<span className="required-mark">*</span></label>
                      <select 
                        value={courseForm.major_id} 
                        onChange={(e) => handleCourseMajorChange(e.target.value)}
                        disabled={!courseForm.school_id}
                      >
                        <option value="">请选择院部</option>
                        {majors.map(major => (
                          <option key={major.id} value={major.id}>
                            {major.name}
                          </option>
                        ))}
                      </select>
                      <small>可用院部数量: {majors.length} | 学校ID: {courseForm.school_id}</small>
                    </div>
                  </div>

                  <div className="form-row">
                    <div className="form-group">
                      <label>学校：<span className="required-mark">*</span></label>
                      <select 
                        value={courseForm.school_id} 
                        onChange={(e) => handleCourseSchoolChange(e.target.value)}
                      >
                        <option value="">请选择学校</option>
                        {schools.map(school => (
                          <option key={school.id} value={school.id}>
                            {school.name}
                          </option>
                        ))}
                      </select>
                      <small>可用学校数量: {schools.length}</small>
                    </div>
                  </div>

                  <div className="form-actions">
                    <button 
                      className="submit-btn"
                      onClick={handleCreateCourse}
                      disabled={loading || !courseForm.name || !courseForm.teacher_id || !courseForm.school_id || !courseForm.major_id || !courseForm.class_id}
                    >
                      {loading ? (isEditingCourse ? '更新中...' : '创建中...') : (isEditingCourse ? '更新课程' : '创建课程')}
                    </button>
                  </div>
                </div>
              </div>
            )}
          </div>
        )}

      {editingTeacher && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h3>编辑教师：{editingTeacher.name}（工号 {editingTeacher.username}）</h3>
            </div>
            <div className="modal-body">
              <div className="form-row">
                <div className="form-group">
                  <label>邮箱：</label>
                  <input type="email" value={editForm.email} onChange={(e) => setEditForm(prev => ({ ...prev, email: e.target.value }))} />
                </div>
                <div className="form-group">
                  <label>手机号：</label>
                  <input type="tel" value={editForm.phone} onChange={(e) => setEditForm(prev => ({ ...prev, phone: e.target.value }))} />
                </div>
              </div>
              <div className="form-row">
                <div className="form-group">
                  <label>学校：</label>
                  <select value={editForm.school_id} onChange={async (e) => {
                    const val = e.target.value;
                    setEditForm(prev => ({ ...prev, school_id: val, major_id: '' }));
                    if (val) {
                      const majorsData = await getMajors(val);
                      setMajors(majorsData);
                    } else {
                      setMajors([]);
                    }
                  }}>
                    <option value="">请选择学校</option>
                    {schools.map(s => <option key={s.id} value={s.id}>{s.name}</option>)}
                  </select>
                </div>
                <div className="form-group">
                  <label>院部：</label>
                  <select value={editForm.major_id} onChange={(e) => setEditForm(prev => ({ ...prev, major_id: e.target.value }))} disabled={!editForm.school_id}>
                    <option value="">请选择院部</option>
                    {majors.map(m => <option key={m.id} value={m.id}>{m.name}</option>)}
                  </select>
                </div>
              </div>
              <div className="form-row">
                <div className="form-group">
                  <label>启用状态：</label>
                  <select value={editForm.is_active ? '1' : '0'} onChange={(e) => setEditForm(prev => ({ ...prev, is_active: e.target.value === '1' }))}>
                    <option value="1">启用</option>
                    <option value="0">禁用</option>
                  </select>
                </div>
                <div className="form-group">
                  <label>重置密码（可选）：</label>
                  <input type="password" value={editForm.new_password} onChange={(e) => setEditForm(prev => ({ ...prev, new_password: e.target.value }))} />
                </div>
              </div>
            </div>
            <div className="modal-footer">
              <button className="secondary-btn" onClick={handleCancelEditTeacher}>取消</button>
              <button className="submit-btn" onClick={handleSaveTeacher} disabled={loading}>{loading ? '保存中...' : '保存'}</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboardPage;
