      return;
    }
    setLoading(true);
    try {
      await forgotPassword({ identifier, new_password: newPassword });
      setSuccess('ÂØÜÁ†ÅÂ∑≤ÈáçÁΩÆÔºåËØ∑‰ΩøÁî®Êñ∞ÂØÜÁ†ÅÁôªÂΩï');
      setTimeout(() => navigate('/login'), 1200);
