    }
  };

  return (
    <div className="login-container">
      <div className="bg-decoration">
        <div className="decoration-circle circle-1"></div>
        <div className="decoration-circle circle-2"></div>
        <div className="decoration-circle circle-3"></div>
      </div>
      <div className="login-card">
        <div className="login-header">
          <div className="logo-section">
            <div className="logo-icon">üîë</div>
            <h1>ÊâæÂõûÂØÜÁ†Å</h1>
            <p className="subtitle">ÈÄöËøáÈÇÆÁÆ±ÊàñÊâãÊú∫Âè∑ÈáçÁΩÆÂØÜÁ†Å</p>
          </div>
        </div>

        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">{success}</div>}

        <div className="form-section">
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>
                ÈÇÆÁÆ±ÊàñÊâãÊú∫Âè∑
                <span className="input-icon">üìÆ</span>
              </label>
              <div className="input-wrapper">
                <input
                  type="text"
                  value={identifier}
                  onChange={(e) => setIdentifier(e.target.value)}
                  placeholder="ËØ∑ËæìÂÖ•ÈÇÆÁÆ±ÊàñÊâãÊú∫Âè∑"
                  autoComplete="username"
                  required
                />
              </div>
            </div>

            <div className="form-group">
              <label>
                Êñ∞ÂØÜÁ†Å
                <span className="input-icon">üîí</span>
              </label>
              <div className="input-wrapper">
                <input
                  type="password"
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                  placeholder="ËØ∑ËæìÂÖ•Êñ∞ÂØÜÁ†Å"
                  autoComplete="new-password"
                  required
                />
              </div>
            </div>

            <div className="form-group">
              <label>
                Á°ÆËÆ§Êñ∞ÂØÜÁ†Å
                <span className="input-icon">‚úÖ</span>
              </label>
              <div className="input-wrapper">
                <input
                  type="password"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                  placeholder="ËØ∑ÂÜçÊ¨°ËæìÂÖ•Êñ∞ÂØÜÁ†Å"
                  autoComplete="new-password"
                  required
                />
              </div>
            </div>

            <button type="submit" className="login-btn" disabled={loading}>
              {loading ? (
                <span className="loading-text">
                  <span className="loading-spinner"></span>
                  Êèê‰∫§‰∏≠...
                </span>
              ) : (
                <span>Êèê‰∫§ÈáçÁΩÆ</span>
