  const handleBatchImport = async () => {
    if (!importData.trim()) {
      setError('请输入要导入的学生数据');
      setSuccess('');
      return;
    }

    if (!selectedSchool || !selectedMajor || !selectedClass) {
      setError('请选择学校、专业和班级');
      setSuccess('');
      return;
    }

    // 解析CSV数据为学生对象数组
    const lines = importData
      .split(/\r?\n/)
      .map(l => l.trim())
      .filter(l => l && !/^\s*#/.test(l));
    const students = [];
    for (const line of lines) {
      const parts = line.split(',').map(s => s.trim());
      if (parts.length < 3) continue; // 至少: 学号,姓名,密码
      const [studentNo, name, password, email, phone] = parts;
      students.push({
        username: studentNo,
        name,
        password,
        email: email || undefined,
        phone: phone || undefined,
        school_id: selectedSchool,
        major_id: selectedMajor,
        class_id: selectedClass,
        role: 'student'
      });
    }

    if (students.length === 0) {
      setError('没有有效的学生数据行。请按 CSV: 学号,姓名,密码[,邮箱][,手机号]');
      setSuccess('');
      return;
    }

    try {
      setLoading(true);
      setError('');
      setSuccess('');

      const resp = await batchImportStudents({ students });

      const msg = resp?.message || `批量导入完成: 成功 ${resp?.success_count ?? students.length} 个, 失败 ${resp?.failed_count ?? 0} 个`;
      setSuccess(msg);
      if (resp?.errors && resp.errors.length > 0) {
        const preview = resp.errors.slice(0, 5).join('；');
        setError(`部分导入失败：${preview}${resp.errors.length > 5 ? ' 等' : ''}`);
      }

      // 刷新用户列表
      const usersData = await getUsers(1, 50, 'student');
      setUsers(usersData.users);
      setImportData('');
    } catch (error) {
      setError(error.response?.data?.error || '批量导入失败');
    } finally {
      setLoading(false);
    }
  };

