import React, { useState, useEffect } from 'react';
import './ProblemSelectionModal.css';

const ProblemSelectionModal = ({ 
  isOpen, 
  onClose, 
  problems, 
  selectedProblemIds, 
  onSelectionChange,
  title = "选择题目"
}) => {
  const [currentPage, setCurrentPage] = useState(1);
  const [perPage] = useState(9);
  const [searchTerm, setSearchTerm] = useState('');
  const [difficultyFilter, setDifficultyFilter] = useState('');
  const [filteredProblems, setFilteredProblems] = useState([]);

  // 添加调试信息
  console.log('ProblemSelectionModal render:', { isOpen, problemsCount: problems?.length, selectedCount: selectedProblemIds?.length });

  useEffect(() => {
    if (problems) {
      let filtered = problems.filter(problem => {
        const matchesSearch = problem.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                            problem.id.toString().includes(searchTerm);
        const matchesDifficulty = !difficultyFilter || problem.difficulty === difficultyFilter;
        return matchesSearch && matchesDifficulty;
      });
      setFilteredProblems(filtered);
      setCurrentPage(1); // 重置到第一页
    }
  }, [problems, searchTerm, difficultyFilter]);

  const totalPages = Math.ceil(filteredProblems.length / perPage);
  const startIndex = (currentPage - 1) * perPage;
  const endIndex = startIndex + perPage;
  const currentProblems = filteredProblems.slice(startIndex, endIndex);

  const handleProblemToggle = (problemId) => {
    const newSelection = selectedProblemIds.includes(problemId)
      ? selectedProblemIds.filter(id => id !== problemId)
      : [...selectedProblemIds, problemId];
    onSelectionChange(newSelection);
  };

  const handleSelectAll = () => {
    const allIds = currentProblems.map(p => p.id);
    const newSelection = [...new Set([...selectedProblemIds, ...allIds])];
    onSelectionChange(newSelection);
  };

  const handleDeselectAll = () => {
    const currentIds = currentProblems.map(p => p.id);
    const newSelection = selectedProblemIds.filter(id => !currentIds.includes(id));
    onSelectionChange(newSelection);
  };

  const getDifficultyColor = (difficulty) => {
    switch (difficulty) {
      case 'easy': return '#52c41a';
      case 'medium': return '#faad14';
      case 'hard': return '#f5222d';
      default: return '#666';
    }
  };

  const getDifficultyText = (difficulty) => {
    switch (difficulty) {
      case 'easy': return '简单';
      case 'medium': return '中等';
      case 'hard': return '困难';
      default: return '未知';
    }
  };

  if (!isOpen) {
    console.log('Modal not open');
    return null;
  }

  console.log('Modal is open, rendering...');

  return (
    <div className="problem-selection-modal-overlay" onClick={onClose}>
      <div className="problem-selection-modal" onClick={e => e.stopPropagation()}>
        <div className="modal-header">
          <h3>{title}</h3>
          <button className="close-btn" onClick={onClose}>×</button>
        </div>

        <div className="modal-content">
          {/* 搜索和筛选 */}
          <div className="search-filters">
            <div className="search-box">
              <input
                type="text"
                placeholder="搜索题目ID或标题..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="search-input"
              />
            </div>

            <div className="filter-controls">
              <select
                value={difficultyFilter}
                onChange={(e) => setDifficultyFilter(e.target.value)}
                className="difficulty-filter"
              >
                <option value="">全部难度</option>
                <option value="easy">简单</option>
                <option value="medium">中等</option>
                <option value="hard">困难</option>
              </select>

              <div className="selection-controls">
                <button 
                  type="button" 
                  onClick={handleSelectAll}
                  className="select-all-btn"
                >
                  全选当前页
                </button>
                <button 
                  type="button" 
                  onClick={handleDeselectAll}
                  className="deselect-all-btn"
                >
                  取消当前页
                </button>
              </div>
            </div>
          </div>

          {/* 题目列表 */}
          <div className="problems-list">
            {currentProblems.length === 0 ? (
              <div className="no-problems">
                <p>没有找到匹配的题目</p>
              </div>
            ) : (
              currentProblems.map(problem => (
                <div 
                  key={problem.id} 
                  className={`problem-item ${selectedProblemIds.includes(problem.id) ? 'selected' : ''}`}
                  onClick={() => handleProblemToggle(problem.id)}
                >
                  <div className="problem-checkbox">
                    <input
                      type="checkbox"
                      checked={selectedProblemIds.includes(problem.id)}
                      onChange={() => handleProblemToggle(problem.id)}
                      onClick={e => e.stopPropagation()}
                    />
                  </div>

                  <div className="problem-info">
                    <div className="problem-header">
                      <span className="problem-id">#{problem.id}</span>
                      <span className="problem-title">{problem.title}</span>
                      <span 
                        className="difficulty-badge"
                        style={{ backgroundColor: getDifficultyColor(problem.difficulty) }}
                      >
                        {getDifficultyText(problem.difficulty)}
                      </span>
                    </div>

                    <div className="problem-meta">
                      <span>时间: {problem.time_limit}ms</span>
                      <span>内存: {problem.memory_limit}MB</span>
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>

          {/* 分页 */}
          {totalPages > 1 && (
            <div className="modal-pagination">
              <div className="pagination-info">
                第 {currentPage} 页，共 {totalPages} 页，共 {filteredProblems.length} 题
              </div>

              <div className="pagination-controls">
                <button
                  onClick={() => setCurrentPage(Math.max(1, currentPage - 1))}
                  disabled={currentPage === 1}
                  className="page-btn"
                >
                  上一页
                </button>

                {/* 智能分页显示 */}
                {(() => {
                  const pages = [];
                  const maxVisible = 7;

                  if (totalPages <= maxVisible) {
                    for (let i = 1; i <= totalPages; i++) {
                      pages.push(i);
                    }
                  } else {
                    if (currentPage <= 4) {
                      for (let i = 1; i <= 5; i++) {
                        pages.push(i);
                      }
                      pages.push('...');
                      pages.push(totalPages);
                    } else if (currentPage >= totalPages - 3) {
                      pages.push(1);
                      pages.push('...');
                      for (let i = totalPages - 4; i <= totalPages; i++) {
                        pages.push(i);
                      }
                    } else {
                      pages.push(1);
                      pages.push('...');
                      for (let i = currentPage - 1; i <= currentPage + 1; i++) {
                        pages.push(i);
                      }
                      pages.push('...');
                      pages.push(totalPages);
                    }
                  }

                  return pages.map((page, index) => (
                    page === '...' ? (
                      <span key={`ellipsis-${index}`} className="page-ellipsis">...</span>
                    ) : (
                      <button
                        key={page}
                        onClick={() => setCurrentPage(page)}
                        className={`page-btn ${currentPage === page ? 'active' : ''}`}
                      >
                        {page}
                      </button>
                    )
                  ));
                })()}

                <button
                  onClick={() => setCurrentPage(Math.min(totalPages, currentPage + 1))}
                  disabled={currentPage === totalPages}
                  className="page-btn"
                >
                  下一页
                </button>
              </div>
            </div>
          )}
        </div>

        <div className="modal-footer">
          <div className="selection-summary">
            已选择 <strong>{selectedProblemIds.length}</strong> 道题目
          </div>

          <div className="modal-actions">
            <button 
              type="button" 
              onClick={onClose}
              className="cancel-btn"
            >
              完成选择
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProblemSelectionModal;
