                  <div className="form-group">
                    <label>选择题目：</label>
                    <div className="problem-selection">
                      {problems.map(problem => (
                        <label key={problem.id} className="problem-checkbox">
                          <input 
                            type="checkbox" 
                            value={problem.id}
                            checked={assignmentForm.problem_ids.includes(problem.id)}
                            onChange={(e) => {
                              if (e.target.checked) {
                                setAssignmentForm(prev => ({ 
                                  ...prev, 
                                  problem_ids: [...prev.problem_ids, problem.id] 
                                }));
                              } else {
                                setAssignmentForm(prev => ({ 
                                  ...prev, 
                                  problem_ids: prev.problem_ids.filter(id => id !== problem.id) 
                                }));
                              }
                            }}
                          />
                          <span className="problem-title">{problem.title}</span>
                          <span className={`difficulty-badge ${problem.difficulty}`}>
                            {problem.difficulty === 'easy' ? '简单' : 
                             problem.difficulty === 'medium' ? '中等' : '困难'}
                          </span>
                        </label>
                      ))}
                    </div>
                  </div>

                  <div className="form-actions">
                    <button 
                      className="submit-btn"
                      onClick={handleCreateAssignment}
                      disabled={loading || !assignmentForm.name || !assignmentForm.description || !assignmentForm.requirements || !assignmentForm.due_date || !assignmentForm.course_id}
                    >
                      {loading ? '创建中...' : '布置作业'}
                    </button>
                  </div>
                </div>
              </div>
            )}



          </div>
        </div>
      )}

      {/* 添加学生到课程的表单 */}
      {activeSubTab === 'add-student-to-course' && editingCourse && (
        <div className="section-content">
          <div className="section-header">
            <h3>👥 添加学生到课程：{editingCourse.name}</h3>
            <p className="form-description">请输入已注册学生的学号和姓名，将其添加到当前教学班</p>
            <button 
              className="secondary-btn"
              onClick={() => setActiveSubTab('detail')}
            >
              ← 返回课程详情
            </button>
          </div>

          <div className="add-student-form">
            <div className="form-row">
              <div className="form-group">
                <label>学号：<span className="required-mark">*</span></label>
                <input 
                  type="text" 
                  placeholder="请输入学号"
                  value={addStudentForm.student_no}
                  onChange={(e) => setAddStudentForm(prev => ({ ...prev, student_no: e.target.value }))}
                  required
                />
              </div>

              <div className="form-group">
                <label>姓名：<span className="required-mark">*</span></label>
                <input 
                  type="text" 
                  placeholder="请输入学生姓名"
                  value={addStudentForm.name}
                  onChange={(e) => setAddStudentForm(prev => ({ ...prev, name: e.target.value }))}
                  required
                />
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>教学班：</label>
                <input 
                  type="text" 
                  value={editingCourse.class_name}
                  disabled
                  className="disabled-input"
                />
              </div>
            </div>





            <div className="form-actions">
              <button 
                className="submit-btn"
                onClick={handleAddStudentToCourse}
                disabled={loading || !addStudentForm.student_no || !addStudentForm.name}
              >
                {loading ? '添加中...' : '添加学生'}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );

  // 处理添加学生时的学校选择变化
  const handleAddStudentSchoolChange = async (schoolId) => {
    setAddStudentForm(prev => ({ 
      ...prev, 
      school_id: schoolId,
      major_id: '',
      class_id: ''
    }));

    if (schoolId) {
      try {
        const majorsData = await getMajors(schoolId);
        setMajors(majorsData);
      } catch (error) {
        console.error('Error fetching majors:', error);
      }
    } else {
      setMajors([]);
    }
  };

  // 处理添加学生时的专业选择变化
  const handleAddStudentMajorChange = async (majorId) => {
    setAddStudentForm(prev => ({ 
      ...prev, 
      major_id: majorId,
      class_id: ''
    }));

    if (majorId) {
      try {
        const classesData = await getClasses(majorId);
        setClasses(classesData);
      } catch (error) {
        console.error('Error fetching classes:', error);
      }
    } else {
      setClasses([]);
    }
  };

  // 处理添加学生
  const handleAddStudent = async () => {
    try {
      setLoading(true);
      setError('');
      setSuccess('');

      await registerStudent(addStudentForm);

      setSuccess('学生添加成功！');

      // 重置表单
      setAddStudentForm({
        student_no: '',
        name: '',
        password: '',
        email: '',
        phone: '',
        school_id: '',
        major_id: '',
        class_id: ''
      });

      // 刷新用户列表
      const usersData = await getUsers(1, 50, 'student');
      setUsers(usersData.users);

      // 切换到学生列表
      setActiveSubTab('list');

    } catch (error) {
      setError(error.response?.data?.error || '添加学生失败');
    } finally {
      setLoading(false);
    }
  };

  // 处理向课程添加学生
  const handleAddStudentToCourse = async () => {
    try {
      setLoading(true);
      setError('');
      setSuccess('');

      // 查找学生是否已存在
      const existingStudent = users.find(user => 
        user.username === addStudentForm.student_no && 
        user.name === addStudentForm.name &&
        user.role === 'student'
      );

      if (!existingStudent) {
        setError('未找到该学生，请确认学号和姓名是否正确');
        return;
      }

      // 检查学生是否已经在该教学班中
      // 由于一个学生可以属于多个教学班，我们需要检查学生是否已经在该课程的教学班中
      const isAlreadyInClass = users.some(user => 
        user.id === existingStudent.id && 
        user.class_id === editingCourse.class_id
      );

      if (isAlreadyInClass) {
        setError('该学生已经在该教学班中');
        return;
      }

      // 检查学生是否已经通过关联关系添加到该课程
      const isAlreadyRelated = studentClassRelations.some(relation => 
        relation.student_id === existingStudent.id && 
        relation.course_id === editingCourse.id
      );

      if (isAlreadyRelated) {
        setError('该学生已经在该课程中');
        return;
      }

      // 使用API添加学生到课程
      try {
        await addStudentToCourse(editingCourse.id, {
          student_id: existingStudent.id,
          class_id: editingCourse.class_id
        });

        setSuccess('学生已成功添加到课程！');

        // 重新加载课程学生数据
        const courseStudentsData = await getCourseStudents(editingCourse.id);
        if (courseStudentsData && courseStudentsData.length > 0) {
          const apiRelations = courseStudentsData.map(student => ({
            student_id: student.student_id,
            class_id: student.class_id,
            course_id: editingCourse.id,
            student_name: student.student_name,
            student_no: student.student_no,
            added_at: student.added_at || new Date().toISOString()
          }));
          setStudentClassRelations(apiRelations);
        }
      } catch (apiError) {
        console.error('添加学生到课程失败:', apiError);
        setError(apiError.response?.data?.error || '添加学生失败');
        return;
      }

      // 重置表单
      setAddStudentForm({
        student_no: '',
        name: '',
        password: '',
        email: '',
        phone: '',
        school_id: '',
        major_id: '',
        class_id: ''
      });

      // 返回到课程详情页
      setActiveSubTab('detail');

    } catch (error) {
      setError(error.response?.data?.error || '添加学生失败');
    } finally {
      setLoading(false);
    }
  };

  // 处理从课程中移除学生
  const handleRemoveStudentFromCourse = async (relationId) => {
    if (!window.confirm('确定要从该课程中移除该学生吗？')) return;

    try {
      // 解析relationId获取学生ID和课程ID
      const [studentId] = relationId.split('_');

      // 使用API从课程中移除学生
      try {
        await removeStudentFromCourse(editingCourse.id, studentId);
        setSuccess('学生已从课程中移除');

        // 重新加载课程学生数据
        const courseStudentsData = await getCourseStudents(editingCourse.id);
        if (courseStudentsData && courseStudentsData.length > 0) {
          const apiRelations = courseStudentsData.map(student => ({
            student_id: student.student_id,
            class_id: student.class_id,
            course_id: editingCourse.id,
            student_name: student.student_name,
            student_no: student.student_no,
            added_at: student.added_at || new Date().toISOString()
          }));
          setStudentClassRelations(apiRelations);
        } else {
          setStudentClassRelations([]);
        }
      } catch (apiError) {
        console.error('从课程中移除学生失败:', apiError);
        setError(apiError.response?.data?.error || '移除学生失败');
        return;
      }
    } catch (error) {
      setError('移除学生失败');
    }
  };

  // 学生编辑/删除相关
  const handleEditStudentClick = (user) => {
    setEditingUser(user);
    setEditForm({
      email: user.email || '',
      phone: user.phone || '',
      school_id: user.school_id || '',
      major_id: user.major_id || '',
      class_id: user.class_id || '',
      new_password: ''
    });
    setError('');
    setSuccess('');
  };

  const handleSaveStudent = async () => {
    if (!editingUser) return;
    try {
      setLoading(true);
      setError('');
      setSuccess('');
      await updateUser(editingUser.id, editForm);
      setSuccess('学生信息已更新');
      // 刷新列表
      const usersData = await getUsers(1, 50, 'student');
      setUsers(usersData.users);
      setEditingUser(null);
    } catch (error) {
      setError(error.response?.data?.error || '更新学生信息失败');
    } finally {
      setLoading(false);
    }
  };

  const handleCancelEditStudent = () => {
    setEditingUser(null);
    setEditForm({ email: '', phone: '', school_id: '', major_id: '', class_id: '', new_password: '' });
  };

  const handleDeleteStudent = async (userId) => {
    if (!window.confirm('确定要删除该学生吗？此操作不可恢复。')) return;
    try {
      setLoading(true);
      setError('');
      setSuccess('');
      await deleteUser(userId);
      setSuccess('学生已删除');
      const usersData = await getUsers(1, 50, 'student');
      setUsers(usersData.users);
    } catch (error) {
      setError(error.response?.data?.error || '删除学生失败');
    } finally {
      setLoading(false);
    }
  };

  // 课程相关处理函数
  const handleCourseSchoolChange = async (schoolId) => {
    setCourseForm(prev => ({ 
      ...prev, 
      school_id: schoolId,
      major_id: '',
      class_id: ''
    }));

    if (schoolId) {
      try {
        const majorsData = await getMajors(schoolId);
        setMajors(majorsData);
      } catch (error) {
        console.error('Error fetching majors:', error);
      }
    } else {
      setMajors([]);
    }
  };

  const handleCourseMajorChange = async (majorId) => {
    setCourseForm(prev => ({ 
      ...prev, 
      major_id: majorId,
      class_id: ''
    }));

    if (majorId) {
      try {
        const classesData = await getClasses(majorId);
        setClasses(classesData);
      } catch (error) {
        console.error('Error fetching classes:', error);
      }
    } else {
      setClasses([]);
    }
  };

  const handleCreateCourse = async () => {
    try {
      setLoading(true);
      setError('');
      setSuccess('');

      if (isEditingCourse && editingCourse) {
        await updateCourse(editingCourse.id, courseForm);
        setSuccess('课程更新成功！');
      } else {
        await createCourse(courseForm);
        setSuccess('课程创建成功！');
      }

      // 重置表单
      setCourseForm({
        name: '',
        description: '',
        class_id: '',
        major_id: '',
        school_id: ''
      });

      setIsEditingCourse(false);
      setEditingCourse(null);

      // 刷新课程列表
      const coursesData = await getTeacherCourses();
      setCourses(coursesData.courses || []);

      // 切换到课程列表
      setActiveSubTab('list');

    } catch (error) {
      setError(error.response?.data?.error || (isEditingCourse ? '更新课程失败' : '创建课程失败'));
    } finally {
      setLoading(false);
    }
  };

  const handleEditCourse = (course) => {
    setEditingCourse(course);
    setCourseForm({
      name: course.name,
      description: course.description || '',
      school_id: course.school_id || '',
      major_id: course.major_id || '',
      class_id: course.class_id || ''
    });
    setIsEditingCourse(true);
    setActiveSubTab('add');
  };

  const handleDeleteCourse = async (courseId) => {
    if (!window.confirm('确定要删除该课程吗？此操作不可恢复。')) return;
    try {
      setLoading(true);
      setError('');
      setSuccess('');

      await deleteCourse(courseId);
      setSuccess('课程已删除');

      // 刷新课程列表
      const coursesData = await getTeacherCourses();
      setCourses(coursesData.courses || []);

    } catch (error) {
      setError(error.response?.data?.error || '删除课程失败');
    } finally {
      setLoading(false);
    }
  };

  const handleViewCourseDetail = (courseId) => {
    const course = courses.find(c => c.id === courseId);
    if (course) {
      setActiveTab('courses');
      setActiveSubTab('detail');
      setEditingCourse(course);
    }
  };

  // 作业相关处理函数
  const handleCreateAssignment = async () => {
    try {
      setLoading(true);
      setError('');
      setSuccess('');

      // 如果在课程详情页面创建作业，自动设置课程ID
      const assignmentData = { ...assignmentForm };
      if (activeSubTab === 'create-assignment' && editingCourse) {
        assignmentData.course_id = editingCourse.id;
      }

      if (isEditingAssignment && editingAssignment) {
        await updateAssignment(editingAssignment.id, assignmentData);
        setSuccess('作业更新成功！');
      } else {
        await createAssignment(assignmentData);
        setSuccess('作业布置成功！');
      }

      // 重置表单
      setAssignmentForm({
        name: '',
        description: '',
        requirements: '',
        due_date: '',
        course_id: '',
        problem_ids: []
      });

      setIsEditingAssignment(false);
      setEditingAssignment(null);

      // 刷新作业列表
      const assignmentsData = await getCourseAssignments();
      setAssignments(assignmentsData.assignments || []);

      // 如果在课程详情页面，返回到课程详情；否则返回到课程列表
      if (activeSubTab === 'create-assignment') {
        setActiveSubTab('detail');
      } else {
        setActiveSubTab('list');
      }

    } catch (error) {
      setError(error.response?.data?.error || (isEditingAssignment ? '更新作业失败' : '布置作业失败'));
    } finally {
      setLoading(false);
    }
  };

  const handleEditAssignment = (assignment) => {
    setEditingAssignment(assignment);
    setAssignmentForm({
      name: assignment.name,
      description: assignment.description || '',
      requirements: assignment.requirements || '',
      due_date: assignment.due_date || '',
      course_id: assignment.course_id || '',
      problem_ids: assignment.problem_ids || []
    });
    setIsEditingAssignment(true);

    // 记录编辑作业时的上下文
    if (activeSubTab === 'detail' && editingCourse) {
      setEditingAssignmentContext('course-detail');
    } else {
      setEditingAssignmentContext('course-list');
    }

    // 切换到作业创建模式
    setActiveSubTab('create-assignment');
  };

  const handleDeleteAssignment = async (assignmentId) => {
    if (!window.confirm('确定要删除该作业吗？此操作不可恢复。')) return;
    try {
      setLoading(true);
      setError('');
      setSuccess('');

      await deleteAssignment(assignmentId);
      setSuccess('作业已删除');

      // 刷新作业列表
      const assignmentsData = await getCourseAssignments();
      setAssignments(assignmentsData.assignments || []);

    } catch (error) {
      setError(error.response?.data?.error || '删除作业失败');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="teacher-dashboard">
      <div className="dashboard-header">
        <h1>教师管理控制台</h1>
      </div>

      <div className="dashboard-tabs">
        <button 
          className={`tab-btn ${activeTab === 'students' ? 'active' : ''}`}
          onClick={() => {
            setActiveTab('students');
            setActiveSubTab('list');
            // 清除课程相关状态
            setEditingCourse(null);
            setEditingAssignmentContext('');
            setAddingStudentContext('');
          }}
        >
          学生管理
        </button>
        <button 
          className={`tab-btn ${activeTab === 'problems' ? 'active' : ''}`}
          onClick={() => {
            setActiveTab('problems');
            setActiveSubTab('list');
            // 清除课程相关状态
            setEditingCourse(null);
            setEditingAssignmentContext('');
            setAddingStudentContext('');
          }}
        >
          题目管理
        </button>
        <button 
          className={`tab-btn ${activeTab === 'courses' ? 'active' : ''}`}
          onClick={() => {
            setActiveTab('courses');
            setActiveSubTab('list');
            // 清除课程详情状态，但保留课程列表
            setEditingCourse(null);
            setEditingAssignmentContext('');
            setAddingStudentContext('');
          }}
        >
          我的课程
        </button>
      </div>

      {error && <div className="error-message">{error}</div>}
      {success && <div className="success-message">{success}</div>}

      {activeTab === 'students' && renderStudentsTab()}
      {activeTab === 'problems' && renderProblemsTab()}
      {activeTab === 'courses' && renderCoursesTab()}

      {/* 编辑学生表单 - 全局显示 */}
      {editingUser && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h3>编辑学生：{editingUser.name}（学号{editingUser.username}）</h3>
            </div>
            <div className="modal-body">
              <div className="form-row">
                <div className="form-group">
                  <label>邮箱：</label>
                  <input type="email" value={editForm.email} onChange={(e) => setEditForm(prev => ({ ...prev, email: e.target.value }))} />
                </div>
                <div className="form-group">
                  <label>手机号：</label>
                  <input type="tel" value={editForm.phone} onChange={(e) => setEditForm(prev => ({ ...prev, phone: e.target.value }))} />
                </div>
              </div>
              <div className="form-row">
                <div className="form-group">
                  <label>学校：</label>
                  <select value={editForm.school_id} onChange={async (e) => {
                    const val = e.target.value;
                    setEditForm(prev => ({ ...prev, school_id: val, major_id: '', class_id: '' }));
                    if (val) {
                      const majorsData = await getMajors(val);
                      setMajors(majorsData);
                    } else {
                      setMajors([]);
                      setClasses([]);
                    }
                  }}>
                    <option value="">请选择学校</option>
                    {schools.map(s => <option key={s.id} value={s.id}>{s.name}</option>)}
                  </select>
                </div>
                <div className="form-group">
                  <label>专业：</label>
                  <select value={editForm.major_id} onChange={async (e) => {
                    const val = e.target.value;
                    setEditForm(prev => ({ ...prev, major_id: val, class_id: '' }));
                    if (val) {
                      const classesData = await getClasses(val);
                      setClasses(classesData);
                    } else {
                      setClasses([]);
                    }
                  }} disabled={!editForm.school_id}>
                    <option value="">请选择专业</option>
                    {majors.map(m => <option key={m.id} value={m.id}>{m.name}</option>)}
                  </select>
                </div>
              </div>
              <div className="form-row">
                <div className="form-group">
                  <label>班级：</label>
                  <select value={editForm.class_id} onChange={(e) => setEditForm(prev => ({ ...prev, class_id: e.target.value }))} disabled={!editForm.major_id}>
                    <option value="">请选择班级</option>
                    {classes.map(c => <option key={c.id} value={c.id}>{c.name}</option>)}
                  </select>
                </div>
                <div className="form-group">
                  <label>重置密码（可选）：</label>
                  <input type="password" value={editForm.new_password} onChange={(e) => setEditForm(prev => ({ ...prev, new_password: e.target.value }))} />
                </div>
              </div>
            </div>
            <div className="modal-footer">
              <button className="secondary-btn" onClick={handleCancelEditStudent}>取消</button>
              <button className="submit-btn" onClick={handleSaveStudent} disabled={loading}>{loading ? '保存中...' : '保存'}</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default TeacherDashboardPage;
