            if (updatedCourse) {
              setEditingCourse(updatedCourse);
            }
          }
        } catch (error) {
          console.warn('刷新课程列表失败:', error);
        }
      } catch (apiError) {
        console.error('从课程中移除学生失败:', apiError);
        setError(apiError.response?.data?.error || '移除学生失败');
