import React, { useState, useEffect } from 'react';
import { getUsers, getSchools, getMajors, getClasses, batchImportStudents } from '../services/api';
import './TeacherDashboardPage.css';
import AIProblemGenerationPage from './AIProblemGenerationPage';

const TeacherDashboardPage = () => {
  const [activeTab, setActiveTab] = useState('students');
  const [users, setUsers] = useState([]);
  const [schools, setSchools] = useState([]);
  const [majors, setMajors] = useState([]);
  const [classes, setClasses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // 批量导入相关状态
  const [importData, setImportData] = useState('');
  const [selectedSchool, setSelectedSchool] = useState('');
  const [selectedMajor, setSelectedMajor] = useState('');
  const [selectedClass, setSelectedClass] = useState('');

  // 手动创建题目相关状态
  const [problemForm, setProblemForm] = useState({
    title: '',
    description: '',
    test_cases: '',
    expected_output: '',
    difficulty: 'easy',
    time_limit: 1000,
    memory_limit: 128
  });

  // 添加学生相关状态
  const [addStudentForm, setAddStudentForm] = useState({
    username: '',
    password: '',
    name: '',
    email: '',
    phone: '',
    school_id: '',
    major_id: '',
    class_id: ''
  });

  useEffect(() => {
    fetchInitialData();
  }, []);

  const fetchInitialData = async () => {
    try {
      setLoading(true);
      const [schoolsData, usersData] = await Promise.all([
        getSchools(),
        getUsers(1, 50, 'student')
      ]);

      setSchools(schoolsData);
      setUsers(usersData.users);
    } catch (error) {
      setError('获取数据失败');
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSchoolChange = async (schoolId) => {
    setSelectedSchool(schoolId);
    setSelectedMajor('');
    setSelectedClass('');

    if (schoolId) {
      try {
        const majorsData = await getMajors(schoolId);
        setMajors(majorsData);
      } catch (error) {
        console.error('Error fetching majors:', error);
      }
    } else {
      setMajors([]);
    }
  };

  const handleMajorChange = async (majorId) => {
    setSelectedMajor(majorId);
    setSelectedClass('');

    if (majorId) {
      try {
        const classesData = await getClasses(majorId);
        setClasses(classesData);
      } catch (error) {
        console.error('Error fetching classes:', error);
      }
    } else {
      setClasses([]);
    }
  };

  const handleBatchImport = async () => {
    if (!importData.trim()) {
      setError('请输入学生数据');
      return;
    }

    try {
      setLoading(true);
      setError('');

      // 解析CSV格式的数据
      const lines = importData.trim().split('\n');
      const students = lines.map(line => {
        const [username, password, name, email, phone] = line.split(',').map(s => s.trim());
        return {
          username,
          password: password || '123456', // 默认密码
          name,
          email: email || '',
          phone: phone || '',
          class_id: selectedClass || null,
          major_id: selectedMajor || null,
          school_id: selectedSchool || null
        };
      });

      const result = await batchImportStudents({ students });
      alert(`批量导入完成: 成功 ${result.success_count} 个, 失败 ${result.failed_count} 个`);

      if (result.success_count > 0) {
        // 刷新用户列表
        const usersData = await getUsers(1, 50, 'student');
        setUsers(usersData.users);
        setImportData('');
      }
    } catch (error) {
      setError(error.response?.data?.error || '批量导入失败');
    } finally {
      setLoading(false);
    }
  };

  const renderStudentsTab = () => (
    <div className="tab-content">
      <div className="section-header">
        <h2>学生管理</h2>
        <button 
          className="add-btn"
          onClick={() => setActiveTab('add-student')}
        >
          添加学生
        </button>
      </div>

      <div className="users-table">
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>用户名</th>
              <th>姓名</th>
              <th>邮箱</th>
              <th>班级</th>
              <th>状态</th>
              <th>操作</th>
            </tr>
          </thead>
          <tbody>
            {users.map(user => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.username}</td>
                <td>{user.name}</td>
                <td>{user.email || '-'}</td>
                <td>{user.class_id || '-'}</td>
                <td>
                  <span className={`status-badge ${user.is_active ? 'active' : 'inactive'}`}>
                    {user.is_active ? '活跃' : '禁用'}
                  </span>
                </td>
                <td>
                  <button className="action-btn edit">编辑</button>
                  <button className="action-btn delete">删除</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );

  const renderBatchImportTab = () => (
    <div className="tab-content">
      <h2>批量导入学生</h2>

      <div className="import-form">
        <div className="form-group">
          <label>选择学校:</label>
          <select 
            value={selectedSchool} 
            onChange={(e) => handleSchoolChange(e.target.value)}
          >
            <option value="">请选择学校</option>
            {schools.map(school => (
              <option key={school.id} value={school.id}>
                {school.name}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>选择专业:</label>
          <select 
            value={selectedMajor} 
            onChange={(e) => handleMajorChange(e.target.value)}
            disabled={!selectedSchool}
          >
            <option value="">请选择专业</option>
            {majors.map(major => (
              <option key={major.id} value={major.id}>
                {major.name}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>选择班级:</label>
          <select 
            value={selectedClass} 
            onChange={(e) => setSelectedClass(e.target.value)}
            disabled={!selectedMajor}
          >
            <option value="">请选择班级</option>
            {classes.map(cls => (
              <option key={cls.id} value={cls.id}>
                {cls.name}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>学生数据 (CSV格式: 用户名,密码,姓名,邮箱,手机号):</label>
          <textarea
            value={importData}
            onChange={(e) => setImportData(e.target.value)}
            placeholder="student1,123456,张三,zhangsan@example.com,13800138001&#10;student2,123456,李四,lisi@example.com,13800138002"
            rows={10}
            className="import-textarea"
          />
        </div>

        <button 
          onClick={handleBatchImport}
          disabled={loading || !importData.trim()}
          className="import-btn"
        >
          {loading ? '导入中...' : '开始导入'}
        </button>
      </div>
    </div>
  );

  const renderProblemsTab = () => (
    <div className="tab-content">
      <div className="section-header">
        <h2>题目管理</h2>
        <div className="problem-actions">
          <button 
            className="action-btn primary"
            onClick={() => setActiveTab('ai-generate')}
          >
            🤖 AI智能生题
          </button>
          <button 
            className="action-btn secondary"
            onClick={() => setActiveTab('manual-create')}
          >
            ✏️ 手动创建题目
          </button>
        </div>
      </div>
      <p>选择创建题目的方式...</p>
    </div>
  );

  // 添加AI生题标签页渲染函数
  const renderAIGenerateTab = () => (
    <div className="tab-content full-width">
      <AIProblemGenerationPage />
    </div>
  );

  // 添加手动创建题目标签页渲染函数
  const renderManualCreateTab = () => (
    <div className="tab-content">
      <h2>✏️ 手动创建题目</h2>
      <div className="manual-create-form">
        <div className="form-group">
          <label>题目名称：</label>
          <input 
            type="text" 
            placeholder="请输入题目名称"
            value={problemForm.title}
            onChange={(e) => setProblemForm(prev => ({ ...prev, title: e.target.value }))}
          />
        </div>

        <div className="form-group">
          <label>题目描述：</label>
          <textarea 
            rows={6} 
            placeholder="请输入题目描述，包括问题背景、输入输出格式说明等"
            value={problemForm.description}
            onChange={(e) => setProblemForm(prev => ({ ...prev, description: e.target.value }))}
          />
        </div>

        <div className="form-group">
          <label>测试用例：</label>
          <textarea 
            rows={8} 
            placeholder="请输入测试用例，格式为：&#10;输入1：[具体输入]&#10;输出1：[具体输出]&#10;输入2：[具体输入]&#10;输出2：[具体输出]"
            value={problemForm.test_cases}
            onChange={(e) => setProblemForm(prev => ({ ...prev, test_cases: e.target.value }))}
          />
        </div>

        <div className="form-group">
          <label>预期输出：</label>
          <textarea 
            rows={4} 
            placeholder="请输入预期输出"
            value={problemForm.expected_output}
            onChange={(e) => setProblemForm(prev => ({ ...prev, expected_output: e.target.value }))}
          />
        </div>

        <div className="form-row">
          <div className="form-group">
            <label>难度等级：</label>
            <select 
              value={problemForm.difficulty}
              onChange={(e) => setProblemForm(prev => ({ ...prev, difficulty: e.target.value }))}
            >
              <option value="easy">简单</option>
              <option value="medium">中等</option>
              <option value="hard">困难</option>
            </select>
          </div>

          <div className="form-group">
            <label>时间限制(ms)：</label>
            <input 
              type="number" 
              value={problemForm.time_limit}
              onChange={(e) => setProblemForm(prev => ({ ...prev, time_limit: parseInt(e.target.value) || 1000 }))}
            />
          </div>

          <div className="form-group">
            <label>内存限制(MB)：</label>
            <input 
              type="number" 
              value={problemForm.memory_limit}
              onChange={(e) => setProblemForm(prev => ({ ...prev, memory_limit: parseInt(e.target.value) || 128 }))}
            />
          </div>
        </div>

        <div className="form-actions">
          <button 
            className="secondary-btn"
            onClick={() => setProblemForm({
              title: '',
              description: '',
              test_cases: '',
              expected_output: '',
              difficulty: 'easy',
              time_limit: 1000,
              memory_limit: 128
            })}
          >
            重置
          </button>
          <button 
            className="primary-btn"
            onClick={handleCreateProblem}
            disabled={loading || !problemForm.title || !problemForm.description || !problemForm.test_cases || !problemForm.expected_output}
          >
            {loading ? '创建中...' : '创建题目'}
          </button>
        </div>
      </div>
    </div>
  );

  // 处理手动创建题目
  const handleCreateProblem = async () => {
    try {
      setLoading(true);
      setError('');

      // 这里需要导入createProblem函数
      const { createProblem } = await import('../services/api');
      await createProblem(problemForm);

      alert('题目创建成功！');

      // 重置表单
      setProblemForm({
        title: '',
        description: '',
        test_cases: '',
        expected_output: '',
        difficulty: 'easy',
        time_limit: 1000,
        memory_limit: 128
      });

      // 切换到题目管理标签页
      setActiveTab('problems');

    } catch (error) {
      setError(error.response?.data?.error || '创建题目失败');
    } finally {
      setLoading(false);
    }
  };

  // 添加学生标签页渲染函数
  const renderAddStudentTab = () => (
    <div className="tab-content">
      <div className="section-header">
        <h2>➕ 添加学生</h2>
        <button 
          className="secondary-btn"
          onClick={() => setActiveTab('students')}
        >
          ← 返回学生管理
        </button>
      </div>

      <div className="add-student-form">
        <div className="form-row">
          <div className="form-group">
            <label>用户名：</label>
            <input 
              type="text" 
              placeholder="请输入用户名"
              value={addStudentForm.username}
              onChange={(e) => setAddStudentForm(prev => ({ ...prev, username: e.target.value }))}
            />
          </div>

          <div className="form-group">
            <label>密码：</label>
            <input 
              type="password" 
              placeholder="请输入密码"
              value={addStudentForm.password}
              onChange={(e) => setAddStudentForm(prev => ({ ...prev, password: e.target.value }))}
            />
          </div>
        </div>

        <div className="form-row">
          <div className="form-group">
            <label>姓名：</label>
            <input 
              type="text" 
              placeholder="请输入学生姓名"
              value={addStudentForm.name}
              onChange={(e) => setAddStudentForm(prev => ({ ...prev, name: e.target.value }))}
            />
          </div>

          <div className="form-group">
            <label>邮箱：</label>
            <input 
              type="email" 
              placeholder="请输入邮箱地址"
              value={addStudentForm.email}
              onChange={(e) => setAddStudentForm(prev => ({ ...prev, email: e.target.value }))}
            />
          </div>
        </div>

        <div className="form-row">
          <div className="form-group">
            <label>手机号：</label>
            <input 
              type="tel" 
              placeholder="请输入手机号码"
              value={addStudentForm.phone}
              onChange={(e) => setAddStudentForm(prev => ({ ...prev, phone: e.target.value }))}
            />
          </div>

          <div className="form-group">
            <label>学校：</label>
            <select 
              value={addStudentForm.school_id}
              onChange={(e) => handleAddStudentSchoolChange(e.target.value)}
            >
              <option value="">请选择学校</option>
              {schools.map(school => (
                <option key={school.id} value={school.id}>
                  {school.name}
                </option>
              ))}
            </select>
          </div>
        </div>

        <div className="form-row">
          <div className="form-group">
            <label>专业：</label>
            <select 
              value={addStudentForm.major_id}
              onChange={(e) => handleAddStudentMajorChange(e.target.value)}
              disabled={!addStudentForm.school_id}
            >
              <option value="">请选择专业</option>
              {majors.map(major => (
                <option key={major.id} value={major.id}>
                  {major.name}
                </option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label>班级：</label>
            <select 
              value={addStudentForm.class_id}
              onChange={(e) => setAddStudentForm(prev => ({ ...prev, class_id: e.target.value }))}
              disabled={!addStudentForm.major_id}
            >
              <option value="">请选择班级</option>
              {classes.map(cls => (
                <option key={cls.id} value={cls.id}>
                  {cls.name}
                </option>
              ))}
            </select>
          </div>
        </div>

        <div className="form-actions">
          <button 
            className="secondary-btn"
            onClick={() => setAddStudentForm({
              username: '',
              password: '',
              name: '',
              email: '',
              phone: '',
              school_id: '',
              major_id: '',
              class_id: ''
            })}
          >
            重置
          </button>
          <button 
            className="primary-btn"
            onClick={handleAddStudent}
            disabled={loading || !addStudentForm.username || !addStudentForm.password || !addStudentForm.name}
          >
            {loading ? '添加中...' : '添加学生'}
          </button>
        </div>
      </div>
    </div>
  );

  // 处理添加学生时的学校选择变化
  const handleAddStudentSchoolChange = async (schoolId) => {
    setAddStudentForm(prev => ({ 
      ...prev, 
      school_id: schoolId,
      major_id: '',
      class_id: ''
    }));

    if (schoolId) {
      try {
        const majorsData = await getMajors(schoolId);
        setMajors(majorsData);
      } catch (error) {
        console.error('Error fetching majors:', error);
      }
    } else {
      setMajors([]);
    }
  };

  // 处理添加学生时的专业选择变化
  const handleAddStudentMajorChange = async (majorId) => {
    setAddStudentForm(prev => ({ 
      ...prev, 
      major_id: majorId,
      class_id: ''
    }));

    if (majorId) {
      try {
        const classesData = await getClasses(majorId);
        setClasses(classesData);
      } catch (error) {
        console.error('Error fetching classes:', error);
      }
    } else {
      setClasses([]);
    }
  };

  // 处理添加学生
  const handleAddStudent = async () => {
    try {
      setLoading(true);
      setError('');

      // 这里需要导入register函数
      const { register } = await import('../services/api');
      const result = await register({
        ...addStudentForm,
        role: 'student'
      });

      alert('学生添加成功！');

      // 重置表单
      setAddStudentForm({
        username: '',
        password: '',
        name: '',
        email: '',
        phone: '',
        school_id: '',
        major_id: '',
        class_id: ''
      });

      // 刷新用户列表
      const usersData = await getUsers(1, 50, 'student');
      setUsers(usersData.users);

      // 切换到学生管理标签页
      setActiveTab('students');

    } catch (error) {
      setError(error.response?.data?.error || '添加学生失败');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="teacher-dashboard">
      <div className="dashboard-header">
        <h1>教师管理控制台</h1>
      </div>

      <div className="dashboard-tabs">
        <button 
          className={`tab-btn ${activeTab === 'students' ? 'active' : ''}`}
          onClick={() => setActiveTab('students')}
        >
          学生管理
        </button>
        <button 
          className={`tab-btn ${activeTab === 'batch-import' ? 'active' : ''}`}
          onClick={() => setActiveTab('batch-import')}
        >
          批量导入
        </button>
        <button 
          className={`tab-btn ${activeTab === 'problems' ? 'active' : ''}`}
          onClick={() => setActiveTab('problems')}
        >
          题目管理
        </button>
        <button 
          className={`tab-btn ${activeTab === 'ai-generate' ? 'active' : ''}`}
          onClick={() => setActiveTab('ai-generate')}
        >
          🤖 AI生题
        </button>
      </div>

      {error && <div className="error-message">{error}</div>}

      {activeTab === 'students' && renderStudentsTab()}
      {activeTab === 'add-student' && renderAddStudentTab()}
      {activeTab === 'batch-import' && renderBatchImportTab()}
      {activeTab === 'problems' && renderProblemsTab()}
      {activeTab === 'ai-generate' && renderAIGenerateTab()}
      {activeTab === 'manual-create' && renderManualCreateTab()}
    </div>
  );
};

export default TeacherDashboardPage;
