import React, { useState, useEffect } from 'react';
import { getUsers, getSchools, getMajors, getClasses, batchImportStudents } from '../services/api';
import './TeacherDashboardPage.css';
import AIProblemGenerationPage from './AIProblemGenerationPage';

const TeacherDashboardPage = () => {
  const [activeTab, setActiveTab] = useState('students');
  const [users, setUsers] = useState([]);
  const [schools, setSchools] = useState([]);
  const [majors, setMajors] = useState([]);
  const [classes, setClasses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // ÊâπÈáèÂØºÂÖ•Áõ∏ÂÖ≥Áä∂ÊÄÅ
  const [importData, setImportData] = useState('');
  const [selectedSchool, setSelectedSchool] = useState('');
  const [selectedMajor, setSelectedMajor] = useState('');
  const [selectedClass, setSelectedClass] = useState('');

  useEffect(() => {
    fetchInitialData();
  }, []);

  const fetchInitialData = async () => {
    try {
      setLoading(true);
      const [schoolsData, usersData] = await Promise.all([
        getSchools(),
        getUsers(1, 50, 'student')
      ]);

      setSchools(schoolsData);
      setUsers(usersData.users);
    } catch (error) {
      setError('Ëé∑ÂèñÊï∞ÊçÆÂ§±Ë¥•');
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSchoolChange = async (schoolId) => {
    setSelectedSchool(schoolId);
    setSelectedMajor('');
    setSelectedClass('');

    if (schoolId) {
      try {
        const majorsData = await getMajors(schoolId);
        setMajors(majorsData);
      } catch (error) {
        console.error('Error fetching majors:', error);
      }
    } else {
      setMajors([]);
    }
  };

  const handleMajorChange = async (majorId) => {
    setSelectedMajor(majorId);
    setSelectedClass('');

    if (majorId) {
      try {
        const classesData = await getClasses(majorId);
        setClasses(classesData);
      } catch (error) {
        console.error('Error fetching classes:', error);
      }
    } else {
      setClasses([]);
    }
  };

  const handleBatchImport = async () => {
    if (!importData.trim()) {
      setError('ËØ∑ËæìÂÖ•Â≠¶ÁîüÊï∞ÊçÆ');
      return;
    }

    try {
      setLoading(true);
      setError('');

      // Ëß£ÊûêCSVÊ†ºÂºèÁöÑÊï∞ÊçÆ
      const lines = importData.trim().split('\n');
      const students = lines.map(line => {
        const [username, password, name, email, phone] = line.split(',').map(s => s.trim());
        return {
          username,
          password: password || '123456', // ÈªòËÆ§ÂØÜÁ†Å
          name,
          email: email || '',
          phone: phone || '',
          class_id: selectedClass || null,
          major_id: selectedMajor || null,
          school_id: selectedSchool || null
        };
      });

      const result = await batchImportStudents({ students });
      alert(`ÊâπÈáèÂØºÂÖ•ÂÆåÊàê: ÊàêÂäü ${result.success_count} ‰∏™, Â§±Ë¥• ${result.failed_count} ‰∏™`);

      if (result.success_count > 0) {
        // Âà∑Êñ∞Áî®Êà∑ÂàóË°®
        const usersData = await getUsers(1, 50, 'student');
        setUsers(usersData.users);
        setImportData('');
      }
    } catch (error) {
      setError(error.response?.data?.error || 'ÊâπÈáèÂØºÂÖ•Â§±Ë¥•');
    } finally {
      setLoading(false);
    }
  };

  const renderStudentsTab = () => (
    <div className="tab-content">
      <div className="section-header">
        <h2>Â≠¶ÁîüÁÆ°ÁêÜ</h2>
        <button 
          className="add-btn"
          onClick={() => setActiveTab('add-student')}
        >
          Ê∑ªÂä†Â≠¶Áîü
        </button>
      </div>

      <div className="users-table">
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Áî®Êà∑Âêç</th>
              <th>ÂßìÂêç</th>
              <th>ÈÇÆÁÆ±</th>
              <th>Áè≠Á∫ß</th>
              <th>Áä∂ÊÄÅ</th>
              <th>Êìç‰Ωú</th>
            </tr>
          </thead>
          <tbody>
            {users.map(user => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.username}</td>
                <td>{user.name}</td>
                <td>{user.email || '-'}</td>
                <td>{user.class_id || '-'}</td>
                <td>
                  <span className={`status-badge ${user.is_active ? 'active' : 'inactive'}`}>
                    {user.is_active ? 'Ê¥ªË∑É' : 'Á¶ÅÁî®'}
                  </span>
                </td>
                <td>
                  <button className="action-btn edit">ÁºñËæë</button>
                  <button className="action-btn delete">Âà†Èô§</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );

  const renderBatchImportTab = () => (
    <div className="tab-content">
      <h2>ÊâπÈáèÂØºÂÖ•Â≠¶Áîü</h2>

      <div className="import-form">
        <div className="form-group">
          <label>ÈÄâÊã©Â≠¶Ê†°:</label>
          <select 
            value={selectedSchool} 
            onChange={(e) => handleSchoolChange(e.target.value)}
          >
            <option value="">ËØ∑ÈÄâÊã©Â≠¶Ê†°</option>
            {schools.map(school => (
              <option key={school.id} value={school.id}>
                {school.name}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>ÈÄâÊã©‰∏ì‰∏ö:</label>
          <select 
            value={selectedMajor} 
            onChange={(e) => handleMajorChange(e.target.value)}
            disabled={!selectedSchool}
          >
            <option value="">ËØ∑ÈÄâÊã©‰∏ì‰∏ö</option>
            {majors.map(major => (
              <option key={major.id} value={major.id}>
                {major.name}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>ÈÄâÊã©Áè≠Á∫ß:</label>
          <select 
            value={selectedClass} 
            onChange={(e) => setSelectedClass(e.target.value)}
            disabled={!selectedMajor}
          >
            <option value="">ËØ∑ÈÄâÊã©Áè≠Á∫ß</option>
            {classes.map(cls => (
              <option key={cls.id} value={cls.id}>
                {cls.name}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Â≠¶ÁîüÊï∞ÊçÆ (CSVÊ†ºÂºè: Áî®Êà∑Âêç,ÂØÜÁ†Å,ÂßìÂêç,ÈÇÆÁÆ±,ÊâãÊú∫Âè∑):</label>
          <textarea
            value={importData}
            onChange={(e) => setImportData(e.target.value)}
            placeholder="student1,123456,Âº†‰∏â,zhangsan@example.com,13800138001&#10;student2,123456,ÊùéÂõõ,lisi@example.com,13800138002"
            rows={10}
            className="import-textarea"
          />
        </div>

        <button 
          onClick={handleBatchImport}
          disabled={loading || !importData.trim()}
          className="import-btn"
        >
          {loading ? 'ÂØºÂÖ•‰∏≠...' : 'ÂºÄÂßãÂØºÂÖ•'}
        </button>
      </div>
    </div>
  );

  const renderProblemsTab = () => (
    <div className="tab-content">
      <div className="section-header">
        <h2>È¢òÁõÆÁÆ°ÁêÜ</h2>
        <div className="problem-actions">
          <button 
            className="action-btn primary"
            onClick={() => setActiveTab('ai-generate')}
          >
            ü§ñ AIÊô∫ËÉΩÁîüÈ¢ò
          </button>
          <button 
            className="action-btn secondary"
            onClick={() => setActiveTab('manual-create')}
          >
            ‚úèÔ∏è ÊâãÂä®ÂàõÂª∫È¢òÁõÆ
          </button>
        </div>
      </div>
      <p>ÈÄâÊã©ÂàõÂª∫È¢òÁõÆÁöÑÊñπÂºè...</p>
    </div>
  );

  // Ê∑ªÂä†AIÁîüÈ¢òÊ†áÁ≠æÈ°µÊ∏≤ÊüìÂáΩÊï∞
  const renderAIGenerateTab = () => (
    <div className="tab-content full-width">
      <AIProblemGenerationPage />
    </div>
  );

  // Ê∑ªÂä†ÊâãÂä®ÂàõÂª∫È¢òÁõÆÊ†áÁ≠æÈ°µÊ∏≤ÊüìÂáΩÊï∞
  const renderManualCreateTab = () => (
    <div className="tab-content">
      <h2>‚úèÔ∏è ÊâãÂä®ÂàõÂª∫È¢òÁõÆ</h2>
      <div className="manual-create-form">
        <div className="form-group">
          <label>È¢òÁõÆÂêçÁß∞Ôºö</label>
          <input type="text" placeholder="ËØ∑ËæìÂÖ•È¢òÁõÆÂêçÁß∞" />
        </div>

        <div className="form-group">
          <label>È¢òÁõÆÊèèËø∞Ôºö</label>
          <textarea 
            rows={6} 
            placeholder="ËØ∑ËæìÂÖ•È¢òÁõÆÊèèËø∞ÔºåÂåÖÊã¨ÈóÆÈ¢òËÉåÊôØ„ÄÅËæìÂÖ•ËæìÂá∫Ê†ºÂºèËØ¥ÊòéÁ≠â"
          />
        </div>

        <div className="form-group">
          <label>ÊµãËØïÁî®‰æãÔºö</label>
          <textarea 
            rows={8} 
            placeholder="ËØ∑ËæìÂÖ•ÊµãËØïÁî®‰æãÔºåÊ†ºÂºè‰∏∫Ôºö&#10;ËæìÂÖ•1Ôºö[ÂÖ∑‰ΩìËæìÂÖ•]&#10;ËæìÂá∫1Ôºö[ÂÖ∑‰ΩìËæìÂá∫]&#10;ËæìÂÖ•2Ôºö[ÂÖ∑‰ΩìËæìÂÖ•]&#10;ËæìÂá∫2Ôºö[ÂÖ∑‰ΩìËæìÂá∫]"
          />
        </div>

        <div className="form-group">
          <label>È¢ÑÊúüËæìÂá∫Ôºö</label>
          <textarea 
            rows={4} 
            placeholder="ËØ∑ËæìÂÖ•È¢ÑÊúüËæìÂá∫"
          />
        </div>

        <div className="form-row">
          <div className="form-group">
            <label>ÈöæÂ∫¶Á≠âÁ∫ßÔºö</label>
            <select>
              <option value="easy">ÁÆÄÂçï</option>
              <option value="medium">‰∏≠Á≠â</option>
              <option value="hard">Âõ∞Èöæ</option>
            </select>
          </div>

          <div className="form-group">
            <label>Êó∂Èó¥ÈôêÂà∂(ms)Ôºö</label>
            <input type="number" defaultValue={1000} />
          </div>

          <div className="form-group">
            <label>ÂÜÖÂ≠òÈôêÂà∂(MB)Ôºö</label>
            <input type="number" defaultValue={128} />
          </div>
        </div>

        <div className="form-actions">
          <button className="secondary-btn">ÈáçÁΩÆ</button>
          <button className="primary-btn">ÂàõÂª∫È¢òÁõÆ</button>
        </div>
      </div>
    </div>
  );

  return (
    <div className="teacher-dashboard">
      <div className="dashboard-header">
        <h1>ÊïôÂ∏àÁÆ°ÁêÜÊéßÂà∂Âè∞</h1>
      </div>

      <div className="dashboard-tabs">
        <button 
          className={`tab-btn ${activeTab === 'students' ? 'active' : ''}`}
          onClick={() => setActiveTab('students')}
        >
          Â≠¶ÁîüÁÆ°ÁêÜ
        </button>
        <button 
          className={`tab-btn ${activeTab === 'batch-import' ? 'active' : ''}`}
          onClick={() => setActiveTab('batch-import')}
        >
          ÊâπÈáèÂØºÂÖ•
        </button>
        <button 
          className={`tab-btn ${activeTab === 'problems' ? 'active' : ''}`}
          onClick={() => setActiveTab('problems')}
        >
          È¢òÁõÆÁÆ°ÁêÜ
        </button>
        <button 
          className={`tab-btn ${activeTab === 'ai-generate' ? 'active' : ''}`}
          onClick={() => setActiveTab('ai-generate')}
        >
          ü§ñ AIÁîüÈ¢ò
        </button>
      </div>

      {error && <div className="error-message">{error}</div>}

      {activeTab === 'students' && renderStudentsTab()}
      {activeTab === 'batch-import' && renderBatchImportTab()}
      {activeTab === 'problems' && renderProblemsTab()}
      {activeTab === 'ai-generate' && renderAIGenerateTab()}
      {activeTab === 'manual-create' && renderManualCreateTab()}
    </div>
  );
};

export default TeacherDashboardPage;
