import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { login } from '../services/api';
import './LoginPage.css';

const LoginPage = () => {
  const [loginType, setLoginType] = useState('student'); // 'student', 'teacher', 'admin'
  const [form, setForm] = useState({
    username: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const result = await login(form);
      localStorage.setItem('token', result.token);
      localStorage.setItem('user', JSON.stringify(result.user));
      // Á´ãÂç≥ÂπøÊí≠ËÆ§ËØÅÁä∂ÊÄÅÂèòÂåñÔºåÁ°Æ‰øùÂØºËà™Ê†èÈ¶ñÊ¨°ÁôªÂΩïÂç≥ÊòæÁ§∫
      window.dispatchEvent(new Event('auth-changed'));

      // Ê†πÊçÆÁî®Êà∑ËßíËâ≤Ë∑≥ËΩ¨Âà∞‰∏çÂêåÈ°µÈù¢
      if (result.user.role === 'admin') {
        navigate('/admin/dashboard');
      } else if (result.user.role === 'teacher') {
        navigate('/teacher/dashboard');
      } else {
        navigate('/problems');
      }
    } catch (error) {
      setError(error.response?.data?.error || 'ÁôªÂΩïÂ§±Ë¥•');
    } finally {
      setLoading(false);
    }
  };

  const handleLoginTypeChange = (type) => {
    setLoginType(type);
    setForm({ username: '', password: '' });
    setError('');
  };

  const getPlaceholder = () => {
    if (loginType === 'student') return 'ËØ∑ËæìÂÖ•Â≠¶Âè∑';
    if (loginType === 'teacher') return 'ËØ∑ËæìÂÖ•Â∑•Âè∑';
    return 'ËØ∑ËæìÂÖ•Â∑•Âè∑';
  };

  const getLabel = () => {
    if (loginType === 'student') return 'Â≠¶Âè∑';
    if (loginType === 'teacher') return 'Â∑•Âè∑';
    return 'Â∑•Âè∑';
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h1>OJÁ≥ªÁªüÁôªÂΩï</h1>

        {/* ÁôªÂΩïÁ±ªÂûãÈÄâÊã© */}
        <div className="login-type-selector">
          <button
            type="button"
            className={`type-btn ${loginType === 'student' ? 'active' : ''}`}
            onClick={() => handleLoginTypeChange('student')}
          >
            üéì Â≠¶ÁîüÁôªÂΩï
          </button>
          <button
            type="button"
            className={`type-btn ${loginType === 'teacher' ? 'active' : ''}`}
            onClick={() => handleLoginTypeChange('teacher')}
          >
            üë®‚Äçüè´ ÊïôÂ∏àÁôªÂΩï
          </button>
          <button
            type="button"
            className={`type-btn ${loginType === 'admin' ? 'active' : ''}`}
            onClick={() => handleLoginTypeChange('admin')}
          >
            üëë ÁÆ°ÁêÜÂëòÁôªÂΩï
          </button>
        </div>

        {error && <div className="error-message">{error}</div>}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>{getLabel()}</label>
            <input
              type="text"
              value={form.username}
              onChange={(e) => setForm({...form, username: e.target.value})}
              required
              placeholder={getPlaceholder()}
              autoComplete="username"
            />
          </div>

          <div className="form-group">
            <label>ÂØÜÁ†Å</label>
            <input
              type="password"
              value={form.password}
              onChange={(e) => setForm({...form, password: e.target.value})}
              required
              placeholder="ËØ∑ËæìÂÖ•ÂØÜÁ†Å"
              autoComplete="current-password"
            />
          </div>

          <button type="submit" className="login-btn" disabled={loading}>
            {loading ? 'ÁôªÂΩï‰∏≠...' : 'ÁôªÂΩï'}
          </button>
        </form>

        <div className="login-info">
          {loginType === 'student' ? (
            <div className="info-box">
              <p>üí° Â≠¶ÁîüÁôªÂΩïËØ¥ÊòéÔºö</p>
              <ul>
                <li>‰ΩøÁî®Â≠¶Âè∑‰Ωú‰∏∫ÁôªÂΩïË¥¶Âè∑</li>
                <li>ÂØÜÁ†ÅÁî±‰ªªËØæÊïôÂ∏àÊèê‰æõ</li>
                <li>Â¶ÇÊúâÈóÆÈ¢òËØ∑ËÅîÁ≥ª‰ªªËØæÊïôÂ∏à</li>
              </ul>
            </div>
          ) : loginType === 'teacher' ? (
            <div className="info-box">
              <p>üí° ÊïôÂ∏àÁôªÂΩïËØ¥ÊòéÔºö</p>
              <ul>
                <li>‰ΩøÁî®Â∑•Âè∑‰Ωú‰∏∫ÁôªÂΩïË¥¶Âè∑</li>
                <li>ÂØÜÁ†ÅÁî±ÁÆ°ÁêÜÂëòÊèê‰æõ</li>
                <li>Â¶ÇÊúâÈóÆÈ¢òËØ∑ËÅîÁ≥ªÁ≥ªÁªüÁÆ°ÁêÜÂëò</li>
              </ul>
            </div>
          ) : (
            <div className="info-box">
              <p>üí° ÁÆ°ÁêÜÂëòÁôªÂΩïËØ¥ÊòéÔºö</p>
              <ul>
                <li>‰ΩøÁî®Â∑•Âè∑‰Ωú‰∏∫ÁôªÂΩïË¥¶Âè∑</li>
                <li>ÂØÜÁ†ÅÁî±Á≥ªÁªüÊèê‰æõ</li>
                <li>Â¶ÇÊúâÈóÆÈ¢òËØ∑ËÅîÁ≥ªÁ≥ªÁªüÁª¥Êä§‰∫∫Âëò</li>
              </ul>
            </div>
          )}
        </div>

        <div className="login-links">
          <a href="/forgot-password">ÂøòËÆ∞ÂØÜÁ†ÅÔºü</a>
          {loginType === 'admin' && <a href="/admin/register">Ê≥®ÂÜåÁÆ°ÁêÜÂëò</a>}
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
