import React, { useState } from 'react';
import { 
  aiGenerateProblem, 
  aiPreviewProblem, 
  aiValidateAndCreateProblem,
  getProblems
} from '../services/api';
import './AIProblemGenerationPage.css';
import WhaleIcon from '../components/WhaleIcon';

const AIProblemGenerationPage = () => {
  const [step, setStep] = useState(1); // 1: è¾“å…¥éœ€æ±‚, 2: é¢„è§ˆä¿®æ”¹, 3: æœ€ç»ˆç¡®è®¤
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [requirements, setRequirements] = useState('');
  const [generatedProblem, setGeneratedProblem] = useState(null);
  const [editedProblem, setEditedProblem] = useState(null);
  const [validationResult, setValidationResult] = useState('');

  // æ·»åŠ è°ƒè¯•ä¿¡æ¯
  const [debugInfo, setDebugInfo] = useState('');

  // æµ‹è¯•ç”¨ä¾‹ç®¡ç†
  const [testCases, setTestCases] = useState([{ input: '', output: '' }]);

  // æ ¼å¼æ¨¡æ¿å¼¹çª—çŠ¶æ€
  const [showFormatModal, setShowFormatModal] = useState(false);
  const [activeFormatTab, setActiveFormatTab] = useState('testcase');

  // è§£ææµ‹è¯•ç”¨ä¾‹ï¼ˆä»AIç”Ÿæˆçš„æ ¼å¼è½¬æ¢ä¸ºç»“æ„åŒ–æ ¼å¼ï¼‰
  const parseGeneratedTestCases = (testCasesText, expectedOutputText) => {
    if (!testCasesText || !expectedOutputText) return [{ input: '', output: '' }];

    // æ·»åŠ è°ƒè¯•æ—¥å¿—
    console.log('å¼€å§‹è§£ææµ‹è¯•ç”¨ä¾‹:');
    console.log('testCasesText:', testCasesText);
    console.log('expectedOutputText:', expectedOutputText);

    try {
      // å°è¯•è§£æä¸ºJSONæ ¼å¼
      const parsed = JSON.parse(testCasesText);
      if (Array.isArray(parsed)) {
        console.log('æˆåŠŸè§£æä¸ºJSONæ ¼å¼');
        return parsed.map(tc => ({
          input: tc.input || '',
          output: tc.output || ''
        }));
      }
    } catch (e) {
      // å¦‚æœä¸æ˜¯JSONï¼Œå°è¯•è§£ææ–‡æœ¬æ ¼å¼
      console.log('è§£æä¸ºJSONå¤±è´¥ï¼Œå°è¯•è§£ææ–‡æœ¬æ ¼å¼');
    }

    // ç­–ç•¥1ï¼šè¯†åˆ«ç»“æ„åŒ–æ ¼å¼ï¼ˆè¾“å…¥1ï¼šxxx è¾“å‡º1ï¼šxxxï¼‰
    console.log('å°è¯•è¯†åˆ«ç»“æ„åŒ–æ ¼å¼');
    const structuredResult = parseStructuredFormat(testCasesText);
    if (structuredResult.length > 0) {
      console.log('æˆåŠŸè§£æä¸ºç»“æ„åŒ–æ ¼å¼');
      return structuredResult;
    }

    // ç­–ç•¥2ï¼šæ™ºèƒ½è¡Œåˆ†å‰²ï¼ˆåŸºäºå†…å®¹ç‰¹å¾ï¼‰
    console.log('å°è¯•æ™ºèƒ½è¡Œåˆ†å‰²');
    const lines = testCasesText.split('\n').filter(line => line.trim());
    if (lines.length > 0) {
      const smartResult = smartLineSplit(lines);
      if (smartResult.length > 0) {
        console.log('ä½¿ç”¨æ™ºèƒ½è¡Œåˆ†å‰²æˆåŠŸ');
        return smartResult;
      }
    }

    // ç­–ç•¥3ï¼šå›é€€åˆ°ç®€åŒ–è§£æé€»è¾‘
    console.log('ä½¿ç”¨ç®€åŒ–è§£æé€»è¾‘');

    // å°è¯•ä»testCasesTextä¸­æå–è¾“å…¥å’Œè¾“å‡º
    let inputContent = '';
    let outputContent = '';

    // å¦‚æœAIç”Ÿæˆäº†åŒ…å«"è¾“å…¥ï¼š"å’Œ"è¾“å‡ºï¼š"çš„æ ¼å¼ï¼Œå°è¯•æå–
    if (testCasesText.includes('è¾“å…¥ï¼š') && testCasesText.includes('è¾“å‡ºï¼š')) {
      const parts = testCasesText.split('è¾“å‡ºï¼š');
      if (parts.length >= 2) {
        inputContent = parts[0].replace('è¾“å…¥ï¼š', '').trim();
        outputContent = parts[1].trim();
      }
    } else {
      // å¦‚æœæ²¡æœ‰æ˜ç¡®æ ‡è®°ï¼Œå°è¯•æ™ºèƒ½åˆ†å‰²
      const lines = testCasesText.split('\n').filter(line => line.trim());
      if (lines.length > 0) {
        // å‡è®¾å‰ä¸€åŠæ˜¯è¾“å…¥ï¼Œåä¸€åŠæ˜¯è¾“å‡º
        const midPoint = Math.ceil(lines.length / 2);
        inputContent = lines.slice(0, midPoint).join('\n');
        outputContent = lines.slice(midPoint).join('\n');
      }
    }

    // å¦‚æœä»testCasesTextæ²¡æœ‰æå–åˆ°å†…å®¹ï¼Œå°è¯•ä»expectedOutputText
    if (!inputContent && !outputContent && expectedOutputText) {
      outputContent = expectedOutputText.trim();
    }

    console.log('æå–çš„è¾“å…¥å†…å®¹:', inputContent);
    console.log('æå–çš„è¾“å‡ºå†…å®¹:', outputContent);

    // è¿”å›ä¸€ä¸ªåŒ…å«æå–å†…å®¹çš„æµ‹è¯•ç”¨ä¾‹ï¼Œè®©æ•™å¸ˆæ‰‹åŠ¨è°ƒæ•´
    if (inputContent || outputContent) {
      return [{
        input: inputContent,
        output: outputContent,
        _needsManualReview: true // æ ‡è®°éœ€è¦æ‰‹åŠ¨æ£€æŸ¥
      }];
    }

    // å¦‚æœä»€ä¹ˆéƒ½æ²¡æå–åˆ°ï¼Œè¿”å›ç©ºçš„æµ‹è¯•ç”¨ä¾‹
    return [{ input: '', output: '' }];
  };

  // è§£æç»“æ„åŒ–æ ¼å¼çš„å‡½æ•°
  const parseStructuredFormat = (testCasesText) => {
    const result = [];

    // ç­–ç•¥1ï¼šåŒ¹é…æ–°æ ¼å¼ï¼šæµ‹è¯•ç”¨ä¾‹X: è¾“å…¥ï¼šxxx è¾“å‡ºï¼šxxx
    const newFormatPattern = /æµ‹è¯•ç”¨ä¾‹(\d+):\s*\nè¾“å…¥ï¼š\s*([\s\S]*?)(?=\nè¾“å‡ºï¼š|$)\nè¾“å‡ºï¼š\s*([\s\S]*?)(?=\næµ‹è¯•ç”¨ä¾‹\d+:|$)/g;

    let match;
    while ((match = newFormatPattern.exec(testCasesText)) !== null) {
      const index = parseInt(match[1]);
      const input = match[2].trim();
      const output = match[3].trim();

      result.push({
        input: input,
        output: output,
        _needsManualReview: false
      });
    }

    if (result.length > 0) {
      console.log(`æ–°æ ¼å¼è§£æåˆ° ${result.length} ä¸ªæµ‹è¯•ç”¨ä¾‹`);
      return result;
    }

    // ç­–ç•¥2ï¼šåŒ¹é…æ—§æ ¼å¼ï¼šè¾“å…¥1ï¼š[å†…å®¹] è¾“å‡º1ï¼š[å†…å®¹] è¾“å…¥2ï¼š[å†…å®¹] è¾“å‡º2ï¼š[å†…å®¹] ...
    const inputPattern = /è¾“å…¥(\d+)[ï¼š:]\s*([\s\S]*?)(?=è¾“å‡º\1[ï¼š:]|è¾“å…¥\d+[ï¼š:]|$)/g;
    const outputPattern = /è¾“å‡º(\d+)[ï¼š:]\s*([\s\S]*?)(?=è¾“å…¥\d+[ï¼š:]|è¾“å‡º\d+[ï¼š:]|$)/g;

    const inputs = {};
    const outputs = {};

    // æå–æ‰€æœ‰è¾“å…¥
    while ((match = inputPattern.exec(testCasesText)) !== null) {
      const index = parseInt(match[1]);
      inputs[index] = match[2].trim();
    }

    // æå–æ‰€æœ‰è¾“å‡º
    while ((match = outputPattern.exec(testCasesText)) !== null) {
      const index = parseInt(match[1]);
      outputs[index] = match[2].trim();
    }

    // å¦‚æœæ‰¾åˆ°ç»“æ„åŒ–æ ¼å¼ï¼ŒæŒ‰ç´¢å¼•åŒ¹é…
    if (Object.keys(inputs).length > 0 || Object.keys(outputs).length > 0) {
      const maxIndex = Math.max(
        ...Object.keys(inputs).map(k => parseInt(k)),
        ...Object.keys(outputs).map(k => parseInt(k))
      );

      for (let i = 1; i <= maxIndex; i++) {
        result.push({
          input: inputs[i] || '',
          output: outputs[i] || '',
          _needsManualReview: !inputs[i] || !outputs[i] // å¦‚æœç¼ºå°‘è¾“å…¥æˆ–è¾“å‡ºï¼Œæ ‡è®°éœ€è¦æ£€æŸ¥
        });
      }

      console.log(`æ—§æ ¼å¼è§£æåˆ° ${result.length} ä¸ªæµ‹è¯•ç”¨ä¾‹`);
      return result;
    }

    return [];
  };

  // æ™ºèƒ½è¡Œåˆ†å‰²å‡½æ•°
  const smartLineSplit = (lines) => {
    const result = [];
    let currentInput = [];
    let currentOutput = [];
    let inInputSection = true;
    let testCaseIndex = 1;

    // é¢„å¤„ç†ï¼šè¯†åˆ«å¯èƒ½çš„è¾“å…¥è¾“å‡ºæ¨¡å¼
    const hasInputOutputPattern = lines.some(line => 
      line.toLowerCase().includes('è¾“å…¥') || line.toLowerCase().includes('è¾“å‡º')
    );

    // å¦‚æœæ²¡æœ‰æ˜ç¡®çš„è¾“å…¥è¾“å‡ºæ ‡è®°ï¼Œå°è¯•åŸºäºå†…å®¹ç‰¹å¾åˆ†å‰²
    if (!hasInputOutputPattern) {
      return splitByContentFeatures(lines);
    }

    for (let i = 0; i < lines.length; i++) {
      const line = lines[i];

      // æ£€æµ‹è¾“å…¥è¾“å‡ºåˆ†ç•Œç‚¹
      if (isInputOutputBoundary(line, lines, i)) {
        // ä¿å­˜å½“å‰çš„æµ‹è¯•ç”¨ä¾‹
        if (currentInput.length > 0 || currentOutput.length > 0) {
          result.push({
            input: currentInput.join('\n'),
            output: currentOutput.join('\n'),
            _needsManualReview: currentInput.length === 0 || currentOutput.length === 0
          });
          testCaseIndex++;
        }

        // é‡ç½®å½“å‰æµ‹è¯•ç”¨ä¾‹
        currentInput = [];
        currentOutput = [];
        inInputSection = !inInputSection;
      }

      // æ ¹æ®å½“å‰çŠ¶æ€æ·»åŠ åˆ°è¾“å…¥æˆ–è¾“å‡º
      if (inInputSection) {
        currentInput.push(line);
      } else {
        currentOutput.push(line);
      }
    }

    // æ·»åŠ æœ€åä¸€ä¸ªæµ‹è¯•ç”¨ä¾‹
    if (currentInput.length > 0 || currentOutput.length > 0) {
      result.push({
        input: currentInput.join('\n'),
        output: currentOutput.join('\n'),
        _needsManualReview: currentInput.length === 0 || currentOutput.length === 0
      });
    }

    console.log(`æ™ºèƒ½è¡Œåˆ†å‰²å¾—åˆ° ${result.length} ä¸ªæµ‹è¯•ç”¨ä¾‹`);
    return result;
  };

  // åŸºäºå†…å®¹ç‰¹å¾çš„åˆ†å‰²å‡½æ•°
  const splitByContentFeatures = (lines) => {
    const result = [];

    // å°è¯•æ‰¾åˆ°è¾“å…¥è¾“å‡ºçš„åˆ†ç•Œç‚¹
    let splitPoint = findOptimalSplitPoint(lines);

    if (splitPoint > 0) {
      const inputLines = lines.slice(0, splitPoint);
      const outputLines = lines.slice(splitPoint);

      // å°è¯•è¿›ä¸€æ­¥åˆ†å‰²ä¸ºå¤šä¸ªæµ‹è¯•ç”¨ä¾‹
      const testCases = splitIntoMultipleTestCases(inputLines, outputLines);

      if (testCases.length > 1) {
        return testCases;
      } else {
        return [{
          input: inputLines.join('\n'),
          output: outputLines.join('\n'),
          _needsManualReview: false
        }];
      }
    }

    // å¦‚æœæ‰¾ä¸åˆ°åˆ†ç•Œç‚¹ï¼Œä½¿ç”¨ç®€å•çš„å‰ååˆ†å‰²
    const midPoint = Math.ceil(lines.length / 2);
    return [{
      input: lines.slice(0, midPoint).join('\n'),
      output: lines.slice(midPoint).join('\n'),
      _needsManualReview: true
    }];
  };

  // æ‰¾åˆ°æœ€ä¼˜åˆ†å‰²ç‚¹
  const findOptimalSplitPoint = (lines) => {
    // ç­–ç•¥1ï¼šå¯»æ‰¾æ˜æ˜¾çš„åˆ†éš”ç¬¦
    for (let i = 0; i < lines.length; i++) {
      const line = lines[i].trim();
      if (line === '---' || line === '===' || line === '***' || line === '') {
        // æ£€æŸ¥å‰åæ˜¯å¦æœ‰å†…å®¹
        const hasContentBefore = i > 0 && lines[i-1].trim() !== '';
        const hasContentAfter = i < lines.length - 1 && lines[i+1].trim() !== '';

        if (hasContentBefore && hasContentAfter) {
          return i;
        }
      }
    }

    // ç­–ç•¥2ï¼šåŸºäºæ•°å­—æ¨¡å¼è¯†åˆ«
    for (let i = 1; i < lines.length; i++) {
      const prevLine = lines[i-1].trim();
      const currentLine = lines[i].trim();

      // å¦‚æœå‰ä¸€è¡Œæ˜¯å•ä¸ªæ•°å­—ï¼Œå½“å‰è¡Œä¹Ÿæ˜¯æ•°å­—ï¼Œå¯èƒ½æ˜¯æ–°æµ‹è¯•ç”¨ä¾‹
      if (/^\d+$/.test(prevLine) && /^\d+/.test(currentLine)) {
        return i;
      }
    }

    // ç­–ç•¥3ï¼šåŸºäºç©ºè¡Œåˆ†éš”
    for (let i = 1; i < lines.length - 1; i++) {
      if (lines[i].trim() === '' && lines[i-1].trim() !== '' && lines[i+1].trim() !== '') {
        return i + 1;
      }
    }

    return -1;
  };

  // åˆ†å‰²ä¸ºå¤šä¸ªæµ‹è¯•ç”¨ä¾‹
  const splitIntoMultipleTestCases = (inputLines, outputLines) => {
    const result = [];

