import React, { useState } from 'react';
import { 
  aiGenerateProblem, 
  aiPreviewProblem, 
  aiValidateAndCreateProblem,
  getProblems
} from '../services/api';
import './AIProblemGenerationPage.css';
import WhaleIcon from '../components/WhaleIcon';

const AIProblemGenerationPage = () => {
  const [step, setStep] = useState(1); // 1: è¾“å…¥éœ€æ±‚, 2: é¢„è§ˆä¿®æ”¹, 3: æœ€ç»ˆç¡®è®¤
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [requirements, setRequirements] = useState('');
  const [generatedProblem, setGeneratedProblem] = useState(null);
  const [editedProblem, setEditedProblem] = useState(null);
  const [validationResult, setValidationResult] = useState('');

  // æ·»åŠ è°ƒè¯•ä¿¡æ¯
  const [debugInfo, setDebugInfo] = useState('');

  // æµ‹è¯•ç”¨ä¾‹ç®¡ç†
  const [testCases, setTestCases] = useState([{ input: '', output: '' }]);

  // è§£æžæµ‹è¯•ç”¨ä¾‹ï¼ˆä»ŽAIç”Ÿæˆçš„æ ¼å¼è½¬æ¢ä¸ºç»“æž„åŒ–æ ¼å¼ï¼‰
  const parseGeneratedTestCases = (testCasesText, expectedOutputText) => {
    if (!testCasesText || !expectedOutputText) return [{ input: '', output: '' }];

    // æ·»åŠ è°ƒè¯•æ—¥å¿—
    console.log('å¼€å§‹è§£æžæµ‹è¯•ç”¨ä¾‹:');
    console.log('testCasesText:', testCasesText);
    console.log('expectedOutputText:', expectedOutputText);

    try {
      // å°è¯•è§£æžä¸ºJSONæ ¼å¼
      const parsed = JSON.parse(testCasesText);
      if (Array.isArray(parsed)) {
        console.log('æˆåŠŸè§£æžä¸ºJSONæ ¼å¼');
        return parsed.map(tc => ({
          input: tc.input || '',
          output: tc.output || ''
        }));
      }
    } catch (e) {
      // å¦‚æžœä¸æ˜¯JSONï¼Œå°è¯•è§£æžæ–‡æœ¬æ ¼å¼
      console.log('è§£æžä¸ºJSONå¤±è´¥ï¼Œå°è¯•è§£æžæ–‡æœ¬æ ¼å¼');
    }

    // ç­–ç•¥1ï¼šè¯†åˆ«ç»“æž„åŒ–æ ¼å¼ï¼ˆè¾“å…¥1ï¼šxxx è¾“å‡º1ï¼šxxxï¼‰
    console.log('å°è¯•è¯†åˆ«ç»“æž„åŒ–æ ¼å¼');
    const structuredResult = parseStructuredFormat(testCasesText);
    if (structuredResult.length > 0) {
      console.log('æˆåŠŸè§£æžä¸ºç»“æž„åŒ–æ ¼å¼');
      return structuredResult;
    }

    // ç­–ç•¥2ï¼šæ™ºèƒ½è¡Œåˆ†å‰²ï¼ˆåŸºäºŽå†…å®¹ç‰¹å¾ï¼‰
    console.log('å°è¯•æ™ºèƒ½è¡Œåˆ†å‰²');
    const lines = testCasesText.split('\n').filter(line => line.trim());
    if (lines.length > 0) {
      const smartResult = smartLineSplit(lines);
      if (smartResult.length > 0) {
        console.log('ä½¿ç”¨æ™ºèƒ½è¡Œåˆ†å‰²æˆåŠŸ');
        return smartResult;
      }
    }

    // ç­–ç•¥3ï¼šå›žé€€åˆ°ç®€åŒ–è§£æžé€»è¾‘
    console.log('ä½¿ç”¨ç®€åŒ–è§£æžé€»è¾‘');

    // å°è¯•ä»ŽtestCasesTextä¸­æå–è¾“å…¥å’Œè¾“å‡º
    let inputContent = '';
    let outputContent = '';

    // å¦‚æžœAIç”Ÿæˆäº†åŒ…å«"è¾“å…¥ï¼š"å’Œ"è¾“å‡ºï¼š"çš„æ ¼å¼ï¼Œå°è¯•æå–
    if (testCasesText.includes('è¾“å…¥ï¼š') && testCasesText.includes('è¾“å‡ºï¼š')) {
      const parts = testCasesText.split('è¾“å‡ºï¼š');
      if (parts.length >= 2) {
        inputContent = parts[0].replace('è¾“å…¥ï¼š', '').trim();
        outputContent = parts[1].trim();
      }
    } else {
      // å¦‚æžœæ²¡æœ‰æ˜Žç¡®æ ‡è®°ï¼Œå°è¯•æ™ºèƒ½åˆ†å‰²
      const lines = testCasesText.split('\n').filter(line => line.trim());
      if (lines.length > 0) {
        // å‡è®¾å‰ä¸€åŠæ˜¯è¾“å…¥ï¼ŒåŽä¸€åŠæ˜¯è¾“å‡º
        const midPoint = Math.ceil(lines.length / 2);
        inputContent = lines.slice(0, midPoint).join('\n');
        outputContent = lines.slice(midPoint).join('\n');
      }
    }

    // å¦‚æžœä»ŽtestCasesTextæ²¡æœ‰æå–åˆ°å†…å®¹ï¼Œå°è¯•ä»ŽexpectedOutputText
    if (!inputContent && !outputContent && expectedOutputText) {
      outputContent = expectedOutputText.trim();
    }

    console.log('æå–çš„è¾“å…¥å†…å®¹:', inputContent);
    console.log('æå–çš„è¾“å‡ºå†…å®¹:', outputContent);

    // è¿”å›žä¸€ä¸ªåŒ…å«æå–å†…å®¹çš„æµ‹è¯•ç”¨ä¾‹ï¼Œè®©æ•™å¸ˆæ‰‹åŠ¨è°ƒæ•´
    if (inputContent || outputContent) {
      return [{
        input: inputContent,
        output: outputContent,
        _needsManualReview: true // æ ‡è®°éœ€è¦æ‰‹åŠ¨æ£€æŸ¥
      }];
    }

    // å¦‚æžœä»€ä¹ˆéƒ½æ²¡æå–åˆ°ï¼Œè¿”å›žç©ºçš„æµ‹è¯•ç”¨ä¾‹
    return [{ input: '', output: '' }];
  };

  // è§£æžç»“æž„åŒ–æ ¼å¼çš„å‡½æ•°
  const parseStructuredFormat = (testCasesText) => {
    const result = [];

    // åŒ¹é…æ¨¡å¼ï¼šè¾“å…¥1ï¼š[å†…å®¹] è¾“å‡º1ï¼š[å†…å®¹] è¾“å…¥2ï¼š[å†…å®¹] è¾“å‡º2ï¼š[å†…å®¹] ...
    const inputPattern = /è¾“å…¥(\d+)[ï¼š:]\s*([\s\S]*?)(?=è¾“å‡º\1[ï¼š:]|è¾“å…¥\d+[ï¼š:]|$)/g;
    const outputPattern = /è¾“å‡º(\d+)[ï¼š:]\s*([\s\S]*?)(?=è¾“å…¥\d+[ï¼š:]|è¾“å‡º\d+[ï¼š:]|$)/g;

    const inputs = {};
    const outputs = {};

    // æå–æ‰€æœ‰è¾“å…¥
    let match;
    while ((match = inputPattern.exec(testCasesText)) !== null) {
      const index = parseInt(match[1]);
      inputs[index] = match[2].trim();
    }

    // æå–æ‰€æœ‰è¾“å‡º
    while ((match = outputPattern.exec(testCasesText)) !== null) {
      const index = parseInt(match[1]);
      outputs[index] = match[2].trim();
    }

    // å¦‚æžœæ‰¾åˆ°ç»“æž„åŒ–æ ¼å¼ï¼ŒæŒ‰ç´¢å¼•åŒ¹é…
    if (Object.keys(inputs).length > 0 || Object.keys(outputs).length > 0) {
      const maxIndex = Math.max(
        ...Object.keys(inputs).map(k => parseInt(k)),
        ...Object.keys(outputs).map(k => parseInt(k))
      );

      for (let i = 1; i <= maxIndex; i++) {
        result.push({
          input: inputs[i] || '',
          output: outputs[i] || '',
          _needsManualReview: !inputs[i] || !outputs[i] // å¦‚æžœç¼ºå°‘è¾“å…¥æˆ–è¾“å‡ºï¼Œæ ‡è®°éœ€è¦æ£€æŸ¥
        });
      }

      console.log(`è§£æžåˆ° ${result.length} ä¸ªç»“æž„åŒ–æµ‹è¯•ç”¨ä¾‹`);
      return result;
    }

    return [];
  };

  // æ™ºèƒ½è¡Œåˆ†å‰²å‡½æ•°
  const smartLineSplit = (lines) => {
    const result = [];
    let currentInput = [];
    let currentOutput = [];
    let inInputSection = true;
    let testCaseIndex = 1;

    for (let i = 0; i < lines.length; i++) {
      const line = lines[i];

      // æ£€æµ‹è¾“å…¥è¾“å‡ºåˆ†ç•Œç‚¹
      if (isInputOutputBoundary(line, lines, i)) {
        // ä¿å­˜å½“å‰çš„æµ‹è¯•ç”¨ä¾‹
        if (currentInput.length > 0 || currentOutput.length > 0) {
          result.push({
            input: currentInput.join('\n'),
            output: currentOutput.join('\n'),
            _needsManualReview: currentInput.length === 0 || currentOutput.length === 0
          });
          testCaseIndex++;
        }

        // é‡ç½®å½“å‰æµ‹è¯•ç”¨ä¾‹
        currentInput = [];
        currentOutput = [];
        inInputSection = !inInputSection;
      }

      // æ ¹æ®å½“å‰çŠ¶æ€æ·»åŠ åˆ°è¾“å…¥æˆ–è¾“å‡º
      if (inInputSection) {
        currentInput.push(line);
      } else {
        currentOutput.push(line);
      }
    }

    // æ·»åŠ æœ€åŽä¸€ä¸ªæµ‹è¯•ç”¨ä¾‹
    if (currentInput.length > 0 || currentOutput.length > 0) {
      result.push({
        input: currentInput.join('\n'),
        output: currentOutput.join('\n'),
        _needsManualReview: currentInput.length === 0 || currentOutput.length === 0
      });
    }

    console.log(`æ™ºèƒ½è¡Œåˆ†å‰²å¾—åˆ° ${result.length} ä¸ªæµ‹è¯•ç”¨ä¾‹`);
    return result;
  };

  // æ£€æµ‹è¾“å…¥è¾“å‡ºåˆ†ç•Œç‚¹çš„å‡½æ•°
  const isInputOutputBoundary = (line, allLines, currentIndex) => {
    const lineLower = line.toLowerCase();

    // æ£€æµ‹æ˜Žæ˜¾çš„åˆ†éš”ç¬¦
    if (lineLower.includes('---') || lineLower.includes('===') || lineLower.includes('***')) {
      return true;
    }

    // æ£€æµ‹æ•°å­—ç¼–å·ï¼ˆå¦‚ "1." "2." ç­‰ï¼‰
    if (/^\d+\./.test(line.trim())) {
      return true;
    }

    // æ£€æµ‹"è¾“å…¥Xï¼š"æˆ–"è¾“å‡ºXï¼š"æ ¼å¼
    if (/^(è¾“å…¥|è¾“å‡º)\d+[ï¼š:]/.test(line.trim())) {
      return true;
    }

    // æ£€æµ‹ç©ºè¡ŒåŽçš„æ–°æµ‹è¯•ç”¨ä¾‹å¼€å§‹
    if (currentIndex > 0 && allLines[currentIndex - 1].trim() === '') {
      const nextLines = allLines.slice(currentIndex + 1, currentIndex + 3);
      if (nextLines.some(l => l.toLowerCase().includes('è¾“å…¥') || l.toLowerCase().includes('è¾“å‡º'))) {
        return true;
      }
    }

    // æ£€æµ‹è¿žç»­ç©ºè¡Œï¼ˆå¯èƒ½è¡¨ç¤ºæµ‹è¯•ç”¨ä¾‹åˆ†éš”ï¼‰
    if (currentIndex > 0 && currentIndex < allLines.length - 1) {
      const prevLine = allLines[currentIndex - 1].trim();
      const nextLine = allLines[currentIndex + 1].trim();
      if (prevLine === '' && nextLine === '' && line.trim() === '') {
        return true;
      }
    }

    return false;
  };

  // æ­¥éª¤1: AIç”Ÿæˆé¢˜ç›®
  const handleGenerate = async () => {
    if (!requirements.trim()) {
      setError('è¯·è¾“å…¥ç”Ÿé¢˜éœ€æ±‚');
      return;
    }

    try {
      setLoading(true);
      setError('');

      // æ·»åŠ è°ƒè¯•ä¿¡æ¯
      const token = localStorage.getItem('token');
      const user = localStorage.getItem('user');
      setDebugInfo(`Token: ${token ? token.substring(0, 20) + '...' : 'No token'}, User: ${user ? JSON.parse(user).username : 'No user'}`);

      console.log('å¼€å§‹è°ƒç”¨AIç”Ÿé¢˜API...');
      console.log('Token:', token);
      console.log('Requirements:', requirements);

      const result = await aiGenerateProblem(requirements);

      console.log('APIå“åº”:', result);

      if (result.success) {
        setGeneratedProblem(result.problem);
        setEditedProblem({ ...result.problem }); // å¤åˆ¶ä¸€ä»½ç”¨äºŽç¼–è¾‘

        // è§£æžæµ‹è¯•ç”¨ä¾‹
        const parsedCases = parseGeneratedTestCases(result.problem.test_cases, result.problem.expected_output);
        setTestCases(parsedCases);

        setStep(2);
      } else {
        setError(result.error || 'AIç”Ÿæˆå¤±è´¥');
      }
    } catch (error) {
