import React, { useState } from 'react';
import { 
  aiGenerateProblem, 
  aiPreviewProblem, 
  aiValidateAndCreateProblem,
  getProblems
} from '../services/api';
import './AIProblemGenerationPage.css';
import WhaleIcon from '../components/WhaleIcon';

const AIProblemGenerationPage = () => {
  const [step, setStep] = useState(1); // 1: è¾“å…¥éœ€æ±‚, 2: é¢„è§ˆä¿®æ”¹, 3: æœ€ç»ˆç¡®è®¤
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [requirements, setRequirements] = useState('');
  const [generatedProblem, setGeneratedProblem] = useState(null);
  const [editedProblem, setEditedProblem] = useState(null);
  const [validationResult, setValidationResult] = useState('');

  // æ·»åŠ è°ƒè¯•ä¿¡æ¯
  const [debugInfo, setDebugInfo] = useState('');

  // æµ‹è¯•ç”¨ä¾‹ç®¡ç†
  const [testCases, setTestCases] = useState([]);
  const [showTestCaseManager, setShowTestCaseManager] = useState(false);

  // è§£ææµ‹è¯•ç”¨ä¾‹ï¼ˆä»AIç”Ÿæˆçš„æ ¼å¼è½¬æ¢ä¸ºç»“æ„åŒ–æ ¼å¼ï¼‰
  const parseGeneratedTestCases = (testCasesText, expectedOutputText) => {
    if (!testCasesText || !expectedOutputText) return [];

    try {
      // å°è¯•è§£æä¸ºJSONæ ¼å¼
      const parsed = JSON.parse(testCasesText);
      if (Array.isArray(parsed)) {
        return parsed.map(tc => ({
          input: tc.input || '',
          output: tc.output || ''
        }));
      }
    } catch (e) {
      // å¦‚æœä¸æ˜¯JSONï¼Œå°è¯•è§£ææ–‡æœ¬æ ¼å¼
      console.log('è§£æä¸ºJSONå¤±è´¥ï¼Œå°è¯•è§£ææ–‡æœ¬æ ¼å¼');
    }

    // è§£ææ–‡æœ¬æ ¼å¼ï¼šæŒ‰è¡Œåˆ†å‰²ï¼Œå‡è®¾è¾“å…¥è¾“å‡ºäº¤æ›¿å‡ºç°
    const lines = testCasesText.split('\n').filter(line => line.trim());
    const cases = [];

    for (let i = 0; i < lines.length; i += 2) {
      if (i + 1 < lines.length) {
        cases.push({
          input: lines[i].replace(/^è¾“å…¥\d*[ï¼š:]\s*/, '').trim(),
          output: lines[i + 1].replace(/^è¾“å‡º\d*[ï¼š:]\s*/, '').trim()
        });
      }
    }

    // å¦‚æœæ²¡æœ‰è§£æåˆ°æµ‹è¯•ç”¨ä¾‹ï¼Œå°è¯•ä»expected_outputè§£æ
    if (cases.length === 0 && expectedOutputText) {
      const outputLines = expectedOutputText.split('\n').filter(line => line.trim());
      if (outputLines.length > 0) {
        // å¯èƒ½æ˜¯æ— è¾“å…¥å¤šè¡Œè¾“å‡ºçš„æƒ…å†µ
        cases.push({
          input: '',
          output: outputLines.join('\n')
        });
      }
    }

    return cases;
  };

  // æ­¥éª¤1: AIç”Ÿæˆé¢˜ç›®
  const handleGenerate = async () => {
    if (!requirements.trim()) {
      setError('è¯·è¾“å…¥ç”Ÿé¢˜éœ€æ±‚');
      return;
    }

    try {
      setLoading(true);
      setError('');

      // æ·»åŠ è°ƒè¯•ä¿¡æ¯
      const token = localStorage.getItem('token');
      const user = localStorage.getItem('user');
      setDebugInfo(`Token: ${token ? token.substring(0, 20) + '...' : 'No token'}, User: ${user ? JSON.parse(user).username : 'No user'}`);

      console.log('å¼€å§‹è°ƒç”¨AIç”Ÿé¢˜API...');
      console.log('Token:', token);
      console.log('Requirements:', requirements);

      const result = await aiGenerateProblem(requirements);

      console.log('APIå“åº”:', result);

      if (result.success) {
        setGeneratedProblem(result.problem);
        setEditedProblem({ ...result.problem }); // å¤åˆ¶ä¸€ä»½ç”¨äºç¼–è¾‘

        // è§£ææµ‹è¯•ç”¨ä¾‹
        const parsedCases = parseGeneratedTestCases(result.problem.test_cases, result.problem.expected_output);
        setTestCases(parsedCases);

        setStep(2);
      } else {
        setError(result.error || 'AIç”Ÿæˆå¤±è´¥');
      }
    } catch (error) {
      console.error('AIç”Ÿæˆé”™è¯¯è¯¦æƒ…:', error);
      console.error('é”™è¯¯å“åº”:', error.response);
      console.error('é”™è¯¯æ•°æ®:', error.response?.data);

      let errorMessage = 'ç”Ÿæˆé¢˜ç›®å¤±è´¥ï¼Œè¯·é‡è¯•';

      if (error.response?.status === 401) {
        errorMessage = 'è®¤è¯å¤±è´¥ï¼Œä½¿ç”¨æµ‹è¯•æ¨¡å¼...';

        // ä½¿ç”¨æ¨¡æ‹Ÿæ•°æ®æ¥æµ‹è¯•å‰ç«¯åŠŸèƒ½
        const mockProblem = {
          title: 'å­—ç¬¦ä¸²åè½¬ï¼ˆæµ‹è¯•æ¨¡å¼ï¼‰',
          description: 'ç»™å®šä¸€ä¸ªå­—ç¬¦ä¸²ï¼Œè¯·å°†å…¶åè½¬åè¾“å‡ºã€‚\n\nè¾“å…¥æ ¼å¼ï¼šä¸€è¡ŒåŒ…å«ä¸€ä¸ªå­—ç¬¦ä¸²\nè¾“å‡ºæ ¼å¼ï¼šè¾“å‡ºåè½¬åçš„å­—ç¬¦ä¸²',
          test_cases: 'è¾“å…¥1ï¼šhello\nè¾“å‡º1ï¼šolleh\n\nè¾“å…¥2ï¼šworld\nè¾“å‡º2ï¼šdlrow\n\nè¾“å…¥3ï¼štest\nè¾“å‡º3ï¼štset',
          expected_output: 'olleh\ndlrow\ntset',
          difficulty: 'easy',
          time_limit: 1000,
          memory_limit: 128,
          ai_generated: true,
          original_requirements: requirements,
          generated_at: new Date().toISOString()
        };

        setGeneratedProblem(mockProblem);
        setEditedProblem({ ...mockProblem });

        // è§£ææµ‹è¯•ç”¨ä¾‹
        const parsedCases = parseGeneratedTestCases(mockProblem.test_cases, mockProblem.expected_output);
        setTestCases(parsedCases);

        setStep(2);
        setError(''); // æ¸…é™¤é”™è¯¯ä¿¡æ¯
        return;
      } else if (error.response?.data?.error) {
        errorMessage = error.response.data.error;
      } else if (error.message) {
        errorMessage = error.message;
      }

      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  // æ­¥éª¤2: é¢„è§ˆä¿®æ”¹
  const handlePreview = async () => {
    try {
      setLoading(true);
      setError('');

      // å°†æµ‹è¯•ç”¨ä¾‹è½¬æ¢ä¸ºå­—ç¬¦ä¸²æ ¼å¼ï¼Œä¿æŒå‘åå…¼å®¹
      const testCasesText = testCases.map(tc => 
        `è¾“å…¥ï¼š${tc.input}\nè¾“å‡ºï¼š${tc.output}`
      ).join('\n\n');

      const expectedOutputText = testCases.map(tc => tc.output).join('\n');

      const updatedProblem = {
        ...editedProblem,
        test_cases: testCasesText,
        expected_output: expectedOutputText
      };

      setEditedProblem(updatedProblem);

      const result = await aiPreviewProblem(updatedProblem);

      if (result.success) {
        setStep(3);
      } else {
        setError('é¢„è§ˆå¤±è´¥');
      }
    } catch (error) {
      setError(error.response?.data?.error || 'é¢„è§ˆå¤±è´¥');
    } finally {
      setLoading(false);
    }
  };

  // æ­¥éª¤3: æœ€ç»ˆåˆ›å»º
  const handleCreateProblem = async (forceCreate = false) => {
    try {
      setLoading(true);
      setError('');

      // å°†æµ‹è¯•ç”¨ä¾‹è½¬æ¢ä¸ºJSONæ ¼å¼
      const testCasesJson = JSON.stringify(testCases.map(tc => ({
        input: tc.input || '',
        output: tc.output || ''
      })));

      const problemData = {
        ...editedProblem,
        test_cases: testCasesJson,
        expected_output: testCases.map(tc => tc.output || '').join('\n'), // ä¿æŒå‘åå…¼å®¹
        force_create: forceCreate
      };

      const result = await aiValidateAndCreateProblem(problemData);

      if (result.success) {
        alert('é¢˜ç›®åˆ›å»ºæˆåŠŸï¼');

        // å°è¯•åˆ·æ–°é¢˜ç›®åˆ—è¡¨
        try {
          const problemsData = await getProblems(1, 50);
          console.log('é¢˜ç›®åˆ›å»ºæˆåŠŸï¼Œæ–°é¢˜ç›®æ•°æ®:', problemsData);

          // å°è¯•é€šè¿‡å¤šç§æ–¹å¼é€šçŸ¥çˆ¶ç»„ä»¶åˆ·æ–°
          // æ–¹æ³•1: localStorageæ ‡è®°
          localStorage.setItem('refreshProblems', 'true');
          localStorage.setItem('lastProblemCreated', new Date().toISOString());

          // æ–¹æ³•2: è‡ªå®šä¹‰äº‹ä»¶
          window.dispatchEvent(new CustomEvent('problemCreated', { 
