import React, { useState, useEffect } from 'react';
import { getUsers, getSchools, getMajors, getClasses, getDepartments, registerTeacher, updateUser, deleteUser, getAllCourses, createCourse, updateCourse, deleteCourse, createSchool, updateSchool, createDepartment, updateDepartment, createMajor, updateMajor, createClass, updateClass } from '../services/api';
import './AdminDashboardPage.css';
import WhaleIcon from '../components/WhaleIcon';

const AdminDashboardPage = () => {
  const [activeTab, setActiveTab] = useState('teachers');
  const [activeSubTab, setActiveSubTab] = useState('list');
  const [teachers, setTeachers] = useState([]);
  const [schools, setSchools] = useState([]);
  const [majors, setMajors] = useState([]);
  const [classes, setClasses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // 编辑教师相关状态
  const [editingTeacher, setEditingTeacher] = useState(null);
  const [editForm, setEditForm] = useState({
    email: '',
    phone: '',
    school_id: '',
    major_id: '',
    is_active: true,
    new_password: ''
  });

  // 添加教师相关状态
  const [addTeacherForm, setAddTeacherForm] = useState({
    job_no: '',
    name: '',
    password: '',
    email: '',
    phone: '',
    school_id: '',
    major_id: ''
  });

  // 课程相关状态
  const [courses, setCourses] = useState([]);
  const [courseForm, setCourseForm] = useState({
    name: '',
    description: '',
    class_id: '',
    major_id: '',
    school_id: '',
    teacher_id: ''
  });

  // 编辑课程状态
  const [editingCourse, setEditingCourse] = useState(null);
  const [isEditingCourse, setIsEditingCourse] = useState(false);

  // 组织架构管理相关状态
  const [departments, setDepartments] = useState([]);
  const [newSchoolForm, setNewSchoolForm] = useState({ name: '', code: '' });
  const [newDepartmentForm, setNewDepartmentForm] = useState({ name: '', code: '', school_id: '' });
  const [newMajorForm, setNewMajorForm] = useState({ name: '', code: '', department_id: '', school_id: '' });
  const [newClassForm, setNewClassForm] = useState({ name: '', grade: '', major_id: '', department_id: '' });
  const [editingSchool, setEditingSchool] = useState(null);
  const [editingDepartment, setEditingDepartment] = useState(null);
  const [editingMajor, setEditingMajor] = useState(null);
  const [editingClass, setEditingClass] = useState(null);

  useEffect(() => {
    fetchInitialData();
  }, []);

  const fetchInitialData = async () => {
    try {
      setLoading(true);
      setError('');
      setSuccess('');

      // 从真实API获取数据
      try {
        const schoolsData = await getSchools();
        setSchools(schoolsData);
        console.log('从API获取的学校数据:', schoolsData);
      } catch (apiError) {
        console.warn('API获取学校数据失败:', apiError);
        setSchools([]);
      }

      // 尝试从API获取教师数据
      try {
        const teachersData = await getUsers(1, 50, 'teacher');
        setTeachers(teachersData.users);
      } catch (apiError) {
        console.warn('API获取教师数据失败:', apiError);
        setTeachers([]);
      }

      // 尝试从API获取课程数据
      try {
        const coursesData = await getAllCourses(1, 50);
        setCourses(coursesData.courses || []);
      } catch (apiError) {
        console.warn('API获取课程数据失败:', apiError);
        setCourses([]);
      }

      // 尝试从API获取院部数据
      try {
        const departmentsData = await getDepartments();
        setDepartments(departmentsData);
        console.log('从API获取的院部数据:', departmentsData);
      } catch (apiError) {
        console.warn('API获取院部数据失败:', apiError);
        setDepartments([]);
      }

      // 尝试从API获取专业数据
      try {
        const majorsData = await getMajors();
        setMajors(majorsData);
        console.log('从API获取的专业数据:', majorsData);
      } catch (apiError) {
        console.warn('API获取专业数据失败:', apiError);
        setMajors([]);
      }

      // 尝试从API获取班级数据
      try {
        const classesData = await getClasses();
        setClasses(classesData);
        console.log('从API获取的班级数据:', classesData);
      } catch (apiError) {
        console.warn('API获取班级数据失败:', apiError);
        setClasses([]);
      }

    } catch (error) {
      setError(error?.response?.data?.error || '获取数据失败');
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSchoolChange = async (schoolId) => {
    console.log('学校选择改变:', schoolId);
    setAddTeacherForm(prev => ({ ...prev, school_id: schoolId, major_id: '' }));

    if (schoolId) {
      try {
        const majorsData = await getMajors(schoolId);
        setMajors(majorsData);
        console.log('从API获取的专业数据:', majorsData);
      } catch (error) {
        console.error('Error fetching majors:', error);
        setMajors([]);
      }
    } else {
      setMajors([]);
    }
  };

  // 课程相关处理函数
  const handleCourseSchoolChange = async (schoolId) => {
    setCourseForm(prev => ({ 
      ...prev, 
      school_id: schoolId,
      major_id: '',
      class_id: ''
    }));

    if (schoolId) {
      try {
        const majorsData = await getMajors(schoolId);
        setMajors(majorsData);
      } catch (error) {
        console.error('Error fetching majors:', error);
      }
    } else {
      setMajors([]);
    }
  };

  const handleCourseMajorChange = async (majorId) => {
    setCourseForm(prev => ({ 
      ...prev, 
      major_id: majorId,
      class_id: ''
    }));

    if (majorId) {
      try {
        const classesData = await getClasses(majorId);
        setClasses(classesData);
      } catch (error) {
        console.error('Error fetching classes:', error);
      }
    } else {
      setClasses([]);
    }
  };

  const handleCreateCourse = async () => {
    try {
      setLoading(true);
      setError('');
      setSuccess('');

      // 准备提交数据，只包含后端需要的字段
      const submitData = {
        name: courseForm.name,
        description: courseForm.description || '',
        teacher_id: courseForm.teacher_id,
        class_id: courseForm.class_id
      };

      if (isEditingCourse && editingCourse) {
        await updateCourse(editingCourse.id, submitData);
        setSuccess('课程更新成功！');
      } else {
        await createCourse(submitData);
        setSuccess('课程创建成功！');
      }

      // 重置表单
      setCourseForm({
        name: '',
        description: '',
        class_id: '',
        major_id: '',
        school_id: '',
        teacher_id: ''
      });

      setIsEditingCourse(false);
      setEditingCourse(null);

      // 刷新课程列表
      const coursesData = await getAllCourses(1, 50);
      setCourses(coursesData.courses || []);

      // 切换到课程列表
      setActiveSubTab('list');

    } catch (error) {
      console.error('课程创建/更新失败:', error);
      setError(error.response?.data?.error || (isEditingCourse ? '更新课程失败' : '创建课程失败'));
    } finally {
      setLoading(false);
    }
  };

  const handleEditCourse = (course) => {
    setEditingCourse(course);
    setCourseForm({
      name: course.name,
      description: course.description || '',
      school_id: course.school_id || '',
      major_id: course.major_id || '',
      class_id: course.class_id || '',
      teacher_id: course.teacher_id || ''
    });
    setIsEditingCourse(true);
    setActiveSubTab('add');
  };

  const handleDeleteCourse = async (courseId) => {
    if (!window.confirm('确定要删除该课程吗？此操作不可恢复。')) return;
    try {
      setLoading(true);
      setError('');
      setSuccess('');

      await deleteCourse(courseId);
      setSuccess('课程已删除');

      // 刷新课程列表
      const coursesData = await getAllCourses(1, 50);
      setCourses(coursesData.courses || []);

    } catch (error) {
      setError(error.response?.data?.error || '删除课程失败');
    } finally {
      setLoading(false);
    }
  };

  const handleAddTeacher = async () => {
    try {
      setLoading(true);
      setError('');
      setSuccess('');

      await registerTeacher(addTeacherForm);

      setSuccess('教师账号创建成功！');

      // 重置表单
      setAddTeacherForm({
        job_no: '',
        name: '',
        password: '',
        email: '',
        phone: '',
        school_id: '',
        major_id: ''
      });

      // 刷新教师列表
      const teachersData = await getUsers(1, 50, 'teacher');
      setTeachers(teachersData.users);

      // 切换到教师列表
      setActiveSubTab('list');

    } catch (error) {
      setError(error.response?.data?.error || '创建教师账号失败');
    } finally {
      setLoading(false);
    }
  };

  const handleEditTeacherClick = (teacher) => {
    setEditingTeacher(teacher);
    setEditForm({
      email: teacher.email || '',
      phone: teacher.phone || '',
      school_id: teacher.school_id || '',
      major_id: teacher.major_id || '',
      is_active: !!teacher.is_active,
      new_password: ''
    });
    setError('');
    setSuccess('');
  };

  const handleSaveTeacher = async () => {
    if (!editingTeacher) return;
    try {
      setLoading(true);
      setError('');
      setSuccess('');
      await updateUser(editingTeacher.id, editForm);
      setSuccess('教师信息已更新');
      // 刷新列表
      const teachersData = await getUsers(1, 50, 'teacher');
      setTeachers(teachersData.users);
      setEditingTeacher(null);
    } catch (error) {
      setError(error.response?.data?.error || '更新教师信息失败');
    } finally {
      setLoading(false);
    }
  };

  const handleCancelEditTeacher = () => {
    setEditingTeacher(null);
    setEditForm({ email: '', phone: '', school_id: '', major_id: '', is_active: true, new_password: '' });
  };

  const handleDeleteTeacher = async (userId) => {
    if (!window.confirm('确定要删除该教师吗？此操作不可恢复。')) return;
    try {
      setLoading(true);
      setError('');
      setSuccess('');
      await deleteUser(userId);
      setSuccess('教师已删除');

      // 刷新教师列表
      const teachersData = await getUsers(1, 50, 'teacher');
      setTeachers(teachersData.users);

      // 同时刷新课程列表，确保数据一致性
      try {
        const coursesData = await getAllCourses(1, 50);
        setCourses(coursesData.courses || []);
      } catch (apiError) {
        console.warn('刷新课程列表失败:', apiError);
      }
    } catch (error) {
      setError(error.response?.data?.error || '删除教师失败');
    } finally {
      setLoading(false);
    }
  };

  // 组织架构管理相关处理函数

  // 当编辑模态框打开时，初始化表单数据
  const initializeEditForms = () => {
    if (editingSchool) {
      if (editingSchool.id) {
        // 编辑模式
        setNewSchoolForm({ name: editingSchool.name, code: editingSchool.code || '' });
      } else {
        // 添加模式
        setNewSchoolForm({ name: '', code: '' });
      }
    }

    if (editingDepartment) {
      if (editingDepartment.id) {
        // 编辑模式
        setNewDepartmentForm({ 
          name: editingDepartment.name, 
          code: editingDepartment.code || '', 
          school_id: editingDepartment.school_id || '' 
        });
      } else {
        // 添加模式
        setNewDepartmentForm({ name: '', code: '', school_id: '' });
      }
    }

    if (editingMajor) {
      if (editingMajor.id) {
        // 编辑模式
        setNewMajorForm({ 
          name: editingMajor.name, 
          code: editingMajor.code || '', 
          department_id: editingMajor.department_id || '', 
          school_id: editingMajor.school_id || '' 
        });
      } else {
        // 添加模式
        setNewMajorForm({ name: '', code: '', department_id: '', school_id: '' });
      }
    }

    if (editingClass) {
      if (editingClass.id) {
        // 编辑模式
        setNewClassForm({ 
          name: editingClass.name, 
          grade: editingClass.grade || '', 
          major_id: editingClass.major_id || '', 
          department_id: editingClass.department_id || '' 
        });
      } else {
        // 添加模式
        setNewClassForm({ name: '', grade: '', major_id: '', department_id: '' });
      }
    }
  };

  // 监听编辑状态变化，初始化表单
  useEffect(() => {
    initializeEditForms();
  }, [editingSchool, editingDepartment, editingMajor, editingClass]);

  const handleSaveSchool = async () => {
    try {
      setLoading(true);
      setError('');
      setSuccess('');

      if (editingSchool.id) {
        // 更新学校
        await updateSchool(editingSchool.id, newSchoolForm);
        setSuccess('学校更新成功！');
      } else {
        // 创建学校
        await createSchool(newSchoolForm);
        setSuccess('学校创建成功！');
      }

      // 刷新学校列表
      const schoolsData = await getSchools();
      setSchools(schoolsData);

      // 重置表单
      setNewSchoolForm({ name: '', code: '' });
      setEditingSchool(null);

    } catch (error) {
      setError(error.response?.data?.error || '保存学校失败');
    } finally {
      setLoading(false);
    }
  };

  const handleSaveDepartment = async () => {
    try {
      setLoading(true);
      setError('');
      setSuccess('');

      if (editingDepartment.id) {
        // 更新院部
        await updateDepartment(editingDepartment.id, newDepartmentForm);
        setSuccess('院部更新成功！');
      } else {
        // 创建院部
        await createDepartment(newDepartmentForm);
        setSuccess('院部创建成功！');
      }

      // 刷新院部列表
      const departmentsData = await getDepartments();
      setDepartments(departmentsData);

      // 重置表单
      setNewDepartmentForm({ name: '', code: '', school_id: '' });
      setEditingDepartment(null);

    } catch (error) {
      setError(error.response?.data?.error || '保存院部失败');
    } finally {
      setLoading(false);
    }
  };

  const handleSaveMajor = async () => {
    try {
      setLoading(true);
      setError('');
      setSuccess('');

      if (editingMajor.id) {
        // 更新专业
        await updateMajor(editingMajor.id, newMajorForm);
        setSuccess('专业更新成功！');
      } else {
        // 创建专业
        await createMajor(newMajorForm);
        setSuccess('专业创建成功！');
      }

      // 刷新专业列表
      const majorsData = await getMajors();
      setMajors(majorsData);

      // 重置表单
      setNewMajorForm({ name: '', code: '', department_id: '', school_id: '' });
      setEditingMajor(null);

    } catch (error) {
      setError(error.response?.data?.error || '保存专业失败');
    } finally {
      setLoading(false);
    }
  };

  const handleSaveClass = async () => {
    try {
      setLoading(true);
      setError('');
      setSuccess('');

      if (editingClass.id) {
        // 更新班级
        await updateClass(editingClass.id, newClassForm);
        setSuccess('班级更新成功！');
      } else {
        // 创建班级
        await createClass(newClassForm);
        setSuccess('班级创建成功！');
      }

      // 刷新班级列表
      const classesData = await getClasses();
      setClasses(classesData);

      // 重置表单
      setNewClassForm({ name: '', grade: '', major_id: '', department_id: '' });
      setEditingClass(null);

    } catch (error) {
      setError(error.response?.data?.error || '保存班级失败');
    } finally {
      setLoading(false);
    }
  };



  return (
    <div className="admin-dashboard">
      <div className="dashboard-header">
        <h1>管理员控制台</h1>
      </div>

      {error && <div className="error-message">{error}</div>}
      {success && <div className="success-message">{success}</div>}

      <div className="dashboard-tabs">
        <button 
          className={`tab-btn ${activeTab === 'teachers' ? 'active' : ''}`}
          onClick={() => {
            setActiveTab('teachers');
            setActiveSubTab('list');
            // 清除课程相关状态
            setEditingCourse(null);
          }}
        >
          教师管理
        </button>
        <button 
          className={`tab-btn ${activeTab === 'courses' ? 'active' : ''}`}
          onClick={() => {
            setActiveTab('courses');
            setActiveSubTab('list');
            // 清除教师相关状态
            setEditingTeacher(null);
          }}
        >
          课程管理
        </button>
        <button 
          className={`tab-btn ${activeTab === 'organization' ? 'active' : ''}`}
          onClick={() => {
            setActiveTab('organization');
            setActiveSubTab('schools');
          }}
        >
          组织架构管理
        </button>
      </div>

      {activeTab === 'teachers' && (
        <div className="tab-content">
          <div className="sub-tabs">
            <button 
              className={`sub-tab-btn ${activeSubTab === 'list' ? 'active' : ''}`}
              onClick={() => setActiveSubTab('list')}
            >
              教师列表
            </button>
            <button 
              className={`sub-tab-btn ${activeSubTab === 'add' ? 'active' : ''}`}
              onClick={() => setActiveSubTab('add')}
            >
              添加教师
            </button>
          </div>

          {activeSubTab === 'list' && (
            <div className="section-content">
              <div className="section-header">
                <h2>教师列表</h2>
                <button 
                  className="add-btn"
                  onClick={() => setActiveSubTab('add')}
                >
                  添加教师
                </button>
              </div>

                <div className="teachers-table">
                  <table>
                    <thead>
                      <tr>
                        <th>ID</th>
                        <th>工号</th>
                        <th>姓名</th>
                        <th>邮箱</th>
                        <th>学校</th>
                        <th>院部</th>
                        <th>状态</th>
                        <th>操作</th>
                      </tr>
                    </thead>
                    <tbody>
                      {teachers.map(teacher => (
                        <tr key={teacher.id}>
                          <td>{teacher.id}</td>
                          <td>{teacher.username}</td>
                          <td>{teacher.name}</td>
                          <td>{teacher.email || '-'}</td>
                          <td>{teacher.school_name || '-'}</td>
                          <td>{teacher.major_name || '-'}</td>
                          <td>
                            <span className={`status-badge ${teacher.is_active ? 'active' : 'inactive'}`}>
                              {teacher.is_active ? '活跃' : '禁用'}
                            </span>
                          </td>
                          <td>
                            <button className="action-btn edit" onClick={() => handleEditTeacherClick(teacher)}>编辑</button>
                            <button className="action-btn delete" onClick={() => handleDeleteTeacher(teacher.id)}>删除</button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            )}

          {activeSubTab === 'add' && (
            <div className="section-content">
              <div className="section-header">
                <h2>➕ 添加教师</h2>
                <div className="header-actions">
                  <button 
                    className="debug-btn"
                    onClick={() => {
                      console.log('当前状态:', { schools, majors, addTeacherForm });
                      alert(`学校数量: ${schools.length}\n院部数量: ${majors.length}\n选择的学校ID: ${addTeacherForm.school_id}`);
                    }}
                  >
                    🐛 调试信息
                  </button>
                  <button 
                    className="secondary-btn"
                    onClick={() => setActiveSubTab('list')}
                  >
                    ← 返回教师列表
                  </button>
                </div>
              </div>

                <div className="add-teacher-form">
                  <div className="form-row">
                    <div className="form-group">
                      <label>工号：<span className="required-mark">*</span></label>
                      <input 
                        type="text" 
                        placeholder="请输入工号"
                        value={addTeacherForm.job_no}
                        onChange={(e) => setAddTeacherForm(prev => ({ ...prev, job_no: e.target.value }))}
                      />
                    </div>

                    <div className="form-group">
                      <label>姓名：<span className="required-mark">*</span></label>
                      <input 
                        type="text" 
                        placeholder="请输入教师姓名"
                        value={addTeacherForm.name}
                        onChange={(e) => setAddTeacherForm(prev => ({ ...prev, name: e.target.value }))}
                      />
                    </div>
                  </div>

                  <div className="form-row">
                    <div className="form-group">
                      <label>密码：<span className="required-mark">*</span></label>
                      <input 
                        type="password" 
                        placeholder="请输入初始密码"
                        value={addTeacherForm.password}
                        onChange={(e) => setAddTeacherForm(prev => ({ ...prev, password: e.target.value }))}
                      />
                    </div>

                    <div className="form-group">
                      <label>邮箱：</label>
                      <input 
                        type="email" 
                        placeholder="请输入邮箱地址"
                        value={addTeacherForm.email}
                        onChange={(e) => setAddTeacherForm(prev => ({ ...prev, email: e.target.value }))}
                      />
                    </div>
                  </div>

                  <div className="form-row">
                    <div className="form-group">
                      <label>手机号：</label>
                      <input 
