import React, { useState } from 'react';
import { 
  aiGenerateProblem, 
  aiPreviewProblem, 
  aiValidateAndCreateProblem 
} from '../services/api';
import './AIProblemGenerationPage.css';

const AIProblemGenerationPage = () => {
  const [step, setStep] = useState(1); // 1: ËæìÂÖ•ÈúÄÊ±Ç, 2: È¢ÑËßà‰øÆÊîπ, 3: ÊúÄÁªàÁ°ÆËÆ§
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [requirements, setRequirements] = useState('');
  const [generatedProblem, setGeneratedProblem] = useState(null);
  const [editedProblem, setEditedProblem] = useState(null);
  const [validationResult, setValidationResult] = useState('');

  // Ê∑ªÂä†Ë∞ÉËØï‰ø°ÊÅØ
  const [debugInfo, setDebugInfo] = useState('');

  // Ê≠•È™§1: AIÁîüÊàêÈ¢òÁõÆ
  const handleGenerate = async () => {
    if (!requirements.trim()) {
      setError('ËØ∑ËæìÂÖ•ÁîüÈ¢òÈúÄÊ±Ç');
      return;
    }

    try {
      setLoading(true);
      setError('');

      // Ê∑ªÂä†Ë∞ÉËØï‰ø°ÊÅØ
      const token = localStorage.getItem('token');
      const user = localStorage.getItem('user');
      setDebugInfo(`Token: ${token ? token.substring(0, 20) + '...' : 'No token'}, User: ${user ? JSON.parse(user).username : 'No user'}`);

      const result = await aiGenerateProblem(requirements);

      if (result.success) {
        setGeneratedProblem(result.problem);
        setEditedProblem({ ...result.problem }); // Â§çÂà∂‰∏Ä‰ªΩÁî®‰∫éÁºñËæë
        setStep(2);
      } else {
        setError(result.error || 'AIÁîüÊàêÂ§±Ë¥•');
      }
    } catch (error) {
      console.error('AIÁîüÊàêÈîôËØØËØ¶ÊÉÖ:', error);
      setError(error.response?.data?.error || 'ÁîüÊàêÈ¢òÁõÆÂ§±Ë¥•ÔºåËØ∑ÈáçËØï');
    } finally {
      setLoading(false);
    }
  };

  // Ê≠•È™§2: È¢ÑËßà‰øÆÊîπ
  const handlePreview = async () => {
    try {
      setLoading(true);
      setError('');

      const result = await aiPreviewProblem(editedProblem);

      if (result.success) {
        setStep(3);
      } else {
        setError('È¢ÑËßàÂ§±Ë¥•');
      }
    } catch (error) {
      setError(error.response?.data?.error || 'È¢ÑËßàÂ§±Ë¥•');
    } finally {
      setLoading(false);
    }
  };

  // Ê≠•È™§3: ÊúÄÁªàÂàõÂª∫
  const handleCreateProblem = async (forceCreate = false) => {
    try {
      setLoading(true);
      setError('');

      const problemData = {
        ...editedProblem,
        force_create: forceCreate
      };

      const result = await aiValidateAndCreateProblem(problemData);

      if (result.success) {
        alert('È¢òÁõÆÂàõÂª∫ÊàêÂäüÔºÅ');
        // ÈáçÁΩÆÁä∂ÊÄÅ
        setStep(1);
        setRequirements('');
        setGeneratedProblem(null);
        setEditedProblem(null);
        setValidationResult('');
      } else {
        setValidationResult(result.validation_result);
        setError(result.message);
      }
    } catch (error) {
      const errorData = error.response?.data;
      if (errorData?.validation_result) {
        setValidationResult(errorData.validation_result);
      }
      setError(errorData?.message || 'ÂàõÂª∫È¢òÁõÆÂ§±Ë¥•');
    } finally {
      setLoading(false);
    }
  };

  // Â§ÑÁêÜËæìÂÖ•ÂèòÂåñ
  const handleInputChange = (field, value) => {
    setEditedProblem(prev => ({
      ...prev,
      [field]: value
    }));
  };

  // ÈáçÊñ∞ÂºÄÂßã
  const handleRestart = () => {
    setStep(1);
    setRequirements('');
    setGeneratedProblem(null);
    setEditedProblem(null);
    setValidationResult('');
    setError('');
  };

  return (
    <div className="ai-problem-generation">
      <div className="page-header">
        <h1>ü§ñ AIÊô∫ËÉΩÁîüÈ¢ò</h1>
        <div className="step-indicator">
          <div className={`step ${step >= 1 ? 'active' : ''}`}>1. ËæìÂÖ•ÈúÄÊ±Ç</div>
          <div className={`step ${step >= 2 ? 'active' : ''}`}>2. È¢ÑËßà‰øÆÊîπ</div>
          <div className={`step ${step >= 3 ? 'active' : ''}`}>3. ÊúÄÁªàÁ°ÆËÆ§</div>
        </div>
      </div>

      {error && (
        <div className="error-message">
          <span className="error-icon">‚ö†Ô∏è</span>
          {error}
        </div>
      )}

      {/* Ê≠•È™§1: ËæìÂÖ•ÈúÄÊ±Ç */}
      {step === 1 && (
        <div className="step-content">
          <h2>üìù ÊèèËø∞ÊÇ®ÁöÑÈ¢òÁõÆÈúÄÊ±Ç</h2>
          <div className="requirements-section">
            <textarea
              value={requirements}
              onChange={(e) => setRequirements(e.target.value)}
              placeholder="ËØ∑ËØ¶ÁªÜÊèèËø∞ÊÇ®ÊÉ≥Ë¶ÅÁîüÊàêÁöÑÈ¢òÁõÆÔºå‰æãÂ¶ÇÔºö&#10;&#10;ÁîüÊàê‰∏Ä‰∏™ÂÖ≥‰∫éÊï∞ÁªÑÊéíÂ∫èÁöÑÁÆÄÂçïÈ¢òÁõÆÔºåÈÄÇÂêàÂàùÂ≠¶ËÄÖÔºö&#10;- ÈöæÂ∫¶ÔºöÁÆÄÂçï&#10;- ÂåÖÂê´3-5‰∏™ÊµãËØïÁî®‰æã&#10;- Ë¶ÅÊ±Ç‰ΩøÁî®ÂÜíÊ≥°ÊéíÂ∫èÊàñÈÄâÊã©ÊéíÂ∫è&#10;- ËæìÂÖ•ËæìÂá∫Ê†ºÂºèË¶ÅÊ∏ÖÊô∞"
              rows={8}
              className="requirements-input"
            />
            <div className="input-tips">
              <h4>üí° ÁîüÈ¢òÂª∫ËÆÆÔºö</h4>
              <ul>
                <li>ÊòéÁ°ÆÊåáÂÆöÈ¢òÁõÆÁ±ªÂûãÔºàÂ¶ÇÔºöÊï∞ÁªÑ„ÄÅÂ≠óÁ¨¶‰∏≤„ÄÅÁÆóÊ≥ïÁ≠âÔºâ</li>
                <li>ËØ¥ÊòéÈöæÂ∫¶Á≠âÁ∫ßÔºàÁÆÄÂçï/‰∏≠Á≠â/Âõ∞ÈöæÔºâ</li>
                <li>ÊåáÂÆöÊµãËØïÁî®‰æãÊï∞Èáè</li>
                <li>ÊèèËø∞ÁâπÊÆäË¶ÅÊ±ÇÊàñÈôêÂà∂Êù°‰ª∂</li>
              </ul>
            </div>
          </div>

          <div className="action-buttons">
            <button 
              onClick={handleGenerate}
              disabled={loading || !requirements.trim()}
              className="primary-btn"
            >
              {loading ? 'üîÑ AIÁîüÊàê‰∏≠...' : 'üöÄ ÂºÄÂßãÁîüÊàê'}
            </button>
          </div>
        </div>
      )}

      {/* Ê≠•È™§2: È¢ÑËßà‰øÆÊîπ */}
      {step === 2 && editedProblem && (
        <div className="step-content">
          <h2>‚úèÔ∏è È¢ÑËßàÂíå‰øÆÊîπÁîüÊàêÁöÑÈ¢òÁõÆ</h2>

          <div className="problem-editor">
            <div className="editor-section">
              <label>È¢òÁõÆÂêçÁß∞Ôºö</label>
              <input
                type="text"
                value={editedProblem.title || ''}
                onChange={(e) => handleInputChange('title', e.target.value)}
                className="title-input"
              />
            </div>

            <div className="editor-section">
              <label>È¢òÁõÆÊèèËø∞Ôºö</label>
              <textarea
                value={editedProblem.description || ''}
                onChange={(e) => handleInputChange('description', e.target.value)}
                rows={6}
                className="description-input"
              />
            </div>

            <div className="editor-section">
              <label>ÊµãËØïÁî®‰æãÔºö</label>
              <textarea
                value={editedProblem.test_cases || ''}
                onChange={(e) => handleInputChange('test_cases', e.target.value)}
                rows={8}
                className="test-cases-input"
              />
            </div>

            <div className="editor-section">
              <label>È¢ÑÊúüËæìÂá∫Ôºö</label>
              <textarea
                value={editedProblem.expected_output || ''}
                onChange={(e) => handleInputChange('expected_output', e.target.value)}
                rows={4}
                className="expected-output-input"
              />
            </div>

            <div className="editor-row">
              <div className="editor-section">
                <label>ÈöæÂ∫¶Á≠âÁ∫ßÔºö</label>
                <select
                  value={editedProblem.difficulty || 'easy'}
                  onChange={(e) => handleInputChange('difficulty', e.target.value)}
                  className="difficulty-select"
                >
                  <option value="easy">ÁÆÄÂçï</option>
                  <option value="medium">‰∏≠Á≠â</option>
                  <option value="hard">Âõ∞Èöæ</option>
                </select>
              </div>

              <div className="editor-section">
                <label>Êó∂Èó¥ÈôêÂà∂(ms)Ôºö</label>
                <input
                  type="number"
                  value={editedProblem.time_limit || 1000}
                  onChange={(e) => handleInputChange('time_limit', parseInt(e.target.value))}
                  className="time-limit-input"
                />
              </div>

              <div className="editor-section">
                <label>ÂÜÖÂ≠òÈôêÂà∂(MB)Ôºö</label>
                <input
                  type="number"
                  value={editedProblem.memory_limit || 128}
                  onChange={(e) => handleInputChange('memory_limit', parseInt(e.target.value))}
                  className="memory-limit-input"
                />
              </div>
            </div>
          </div>

          <div className="action-buttons">
            <button onClick={handleRestart} className="secondary-btn">
              üîÑ ÈáçÊñ∞ÂºÄÂßã
            </button>
            <button 
              onClick={handlePreview}
              disabled={loading}
              className="primary-btn"
            >
              {loading ? 'Â§ÑÁêÜ‰∏≠...' : 'üëÄ È¢ÑËßàÁ°ÆËÆ§'}
            </button>
          </div>
        </div>
      )}

      {/* Ê≠•È™§3: ÊúÄÁªàÁ°ÆËÆ§ */}
      {step === 3 && editedProblem && (
        <div className="step-content">
          <h2>üîç AIÈ™åËØÅÂíåÊúÄÁªàÁ°ÆËÆ§</h2>

          <div className="final-preview">
            <div className="preview-card">
              <h3>üìã È¢òÁõÆÈ¢ÑËßà</h3>
              <div className="preview-content">
                <div className="preview-item">
                  <strong>È¢òÁõÆÂêçÁß∞Ôºö</strong>
                  <span>{editedProblem.title}</span>
                </div>
                <div className="preview-item">
                  <strong>ÈöæÂ∫¶Á≠âÁ∫ßÔºö</strong>
                  <span className={`difficulty-badge ${editedProblem.difficulty}`}>
                    {editedProblem.difficulty === 'easy' ? 'ÁÆÄÂçï' : 
                     editedProblem.difficulty === 'medium' ? '‰∏≠Á≠â' : 'Âõ∞Èöæ'}
                  </span>
                </div>
                <div className="preview-item">
                  <strong>Êó∂Èó¥/ÂÜÖÂ≠òÈôêÂà∂Ôºö</strong>
                  <span>{editedProblem.time_limit}ms / {editedProblem.memory_limit}MB</span>
                </div>
                <div className="preview-item">
                  <strong>È¢òÁõÆÊèèËø∞Ôºö</strong>
                  <pre className="description-preview">{editedProblem.description}</pre>
                </div>
                <div className="preview-item">
                  <strong>ÊµãËØïÁî®‰æãÔºö</strong>
                  <pre className="test-cases-preview">{editedProblem.test_cases}</pre>
                </div>
              </div>
            </div>

            {validationResult && (
              <div className="validation-result">
                <h3>ü§ñ AIÈ™åËØÅÁªìÊûú</h3>
                <div className="validation-content">
                  <pre>{validationResult}</pre>
                </div>
              </div>
            )}
          </div>

          <div className="action-buttons">
            <button onClick={() => setStep(2)} className="secondary-btn">
              ‚Üê ËøîÂõû‰øÆÊîπ
            </button>
            <button onClick={handleRestart} className="secondary-btn">
              üîÑ ÈáçÊñ∞ÂºÄÂßã
            </button>
            <button 
              onClick={() => handleCreateProblem(false)}
              disabled={loading}
              className="primary-btn"
            >
              {loading ? 'ÂàõÂª∫‰∏≠...' : '‚úÖ ÂàõÂª∫È¢òÁõÆ'}
            </button>
            {validationResult && !validationResult.includes('È™åËØÅÈÄöËøá') && (
              <button 
                onClick={() => handleCreateProblem(true)}
                disabled={loading}
                className="warning-btn"
              >
                ‚ö†Ô∏è Âº∫Âà∂ÂàõÂª∫
              </button>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default AIProblemGenerationPage;
