import React, { useState, useEffect } from 'react';
import { getUsers, getSchools, getMajors, registerTeacher, updateUser, deleteUser } from '../services/api';
import './AdminDashboardPage.css';

const AdminDashboardPage = () => {
  const [activeSubTab, setActiveSubTab] = useState('list');
  const [teachers, setTeachers] = useState([]);
  const [schools, setSchools] = useState([]);
  const [majors, setMajors] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // ÁºñËæëÊïôÂ∏àÁõ∏ÂÖ≥Áä∂ÊÄÅ
  const [editingTeacher, setEditingTeacher] = useState(null);
  const [editForm, setEditForm] = useState({
    email: '',
    phone: '',
    school_id: '',
    major_id: '',
    is_active: true,
    new_password: ''
  });

  // Ê∑ªÂä†ÊïôÂ∏àÁõ∏ÂÖ≥Áä∂ÊÄÅ
  const [addTeacherForm, setAddTeacherForm] = useState({
    job_no: '',
    name: '',
    password: '',
    email: '',
    phone: '',
    school_id: '',
    major_id: ''
  });

  useEffect(() => {
    fetchInitialData();
  }, []);

  const fetchInitialData = async () => {
    try {
      setLoading(true);
      setError('');
      setSuccess('');

      // Áõ¥Êé•‰ΩøÁî®ÊµãËØïÊï∞ÊçÆÔºåÁ°Æ‰øùÁïåÈù¢Ê≠£Â∏∏Â∑•‰Ωú
      const testSchools = [
        { id: 1, name: 'Á¥¢ÈÇ¶Â§ßÂ≠¶', code: 'SORBONNE' },
        { id: 2, name: 'Â∑¥ÈªéÈ´òÁ≠âÂ∏àËåÉÂ≠¶Èô¢', code: 'ENS' },
        { id: 3, name: 'Â∑¥ÈªéÁªºÂêàÁêÜÂ∑•Â≠¶Èô¢', code: 'X' }
      ];
      const testMajors = [
        { id: 1, name: 'ËÆ°ÁÆóÊú∫ÁßëÂ≠¶Á≥ª', code: 'CS', school_id: 1 },
        { id: 2, name: 'Êï∞Â≠¶Á≥ª', code: 'MATH', school_id: 1 },
        { id: 3, name: 'Áâ©ÁêÜÁ≥ª', code: 'PHYS', school_id: 1 },
        { id: 4, name: 'Â∑•Á®ãÁ≥ª', code: 'ENG', school_id: 2 },
        { id: 5, name: 'ÂåñÂ≠¶Á≥ª', code: 'CHEM', school_id: 2 }
      ];

      setSchools(testSchools);
      setMajors(testMajors);

      console.log('ÊµãËØïÊï∞ÊçÆÂ∑≤ËÆæÁΩÆ:', { schools: testSchools, majors: testMajors });

      // Â∞ùËØï‰ªéAPIËé∑ÂèñÊïôÂ∏àÊï∞ÊçÆ
      try {
        const teachersData = await getUsers(1, 50, 'teacher');
        setTeachers(teachersData.users);
      } catch (apiError) {
        console.warn('APIËé∑ÂèñÊïôÂ∏àÊï∞ÊçÆÂ§±Ë¥•:', apiError);
        setTeachers([]);
      }

    } catch (error) {
      setError(error?.response?.data?.error || 'Ëé∑ÂèñÊï∞ÊçÆÂ§±Ë¥•');
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSchoolChange = async (schoolId) => {
    console.log('Â≠¶Ê†°ÈÄâÊã©ÊîπÂèò:', schoolId);
    setAddTeacherForm(prev => ({ ...prev, school_id: schoolId, major_id: '' }));

    if (schoolId) {
      // Áõ¥Êé•‰ΩøÁî®ÊµãËØïÊï∞ÊçÆËøáÊª§
      const testMajors = [
        { id: 1, name: 'ËÆ°ÁÆóÊú∫ÁßëÂ≠¶Á≥ª', code: 'CS', school_id: 1 },
        { id: 2, name: 'Êï∞Â≠¶Á≥ª', code: 'MATH', school_id: 1 },
        { id: 3, name: 'Áâ©ÁêÜÁ≥ª', code: 'PHYS', school_id: 1 },
        { id: 4, name: 'Â∑•Á®ãÁ≥ª', code: 'ENG', school_id: 2 },
        { id: 5, name: 'ÂåñÂ≠¶Á≥ª', code: 'CHEM', school_id: 2 }
      ];
      const filteredMajors = testMajors.filter(major => major.school_id == schoolId);
      console.log('ËøáÊª§ÂêéÁöÑÈô¢ÈÉ®Êï∞ÊçÆ:', filteredMajors);
      setMajors(filteredMajors);
    } else {
      setMajors([]);
    }
  };

  const handleAddTeacher = async () => {
    try {
      setLoading(true);
      setError('');
      setSuccess('');

      await registerTeacher(addTeacherForm);

      setSuccess('ÊïôÂ∏àË¥¶Âè∑ÂàõÂª∫ÊàêÂäüÔºÅ');

      // ÈáçÁΩÆË°®Âçï
      setAddTeacherForm({
        job_no: '',
        name: '',
        password: '',
        email: '',
        phone: '',
        school_id: '',
        major_id: ''
      });

      // Âà∑Êñ∞ÊïôÂ∏àÂàóË°®
      const teachersData = await getUsers(1, 50, 'teacher');
      setTeachers(teachersData.users);

      // ÂàáÊç¢Âà∞ÊïôÂ∏àÂàóË°®
      setActiveSubTab('list');

    } catch (error) {
      setError(error.response?.data?.error || 'ÂàõÂª∫ÊïôÂ∏àË¥¶Âè∑Â§±Ë¥•');
    } finally {
      setLoading(false);
    }
  };

  const handleEditTeacherClick = (teacher) => {
    setEditingTeacher(teacher);
    setEditForm({
      email: teacher.email || '',
      phone: teacher.phone || '',
      school_id: teacher.school_id || '',
      major_id: teacher.major_id || '',
      is_active: !!teacher.is_active,
      new_password: ''
    });
    setError('');
    setSuccess('');
  };

  const handleSaveTeacher = async () => {
    if (!editingTeacher) return;
    try {
      setLoading(true);
      setError('');
      setSuccess('');
      await updateUser(editingTeacher.id, editForm);
      setSuccess('ÊïôÂ∏à‰ø°ÊÅØÂ∑≤Êõ¥Êñ∞');
      // Âà∑Êñ∞ÂàóË°®
      const teachersData = await getUsers(1, 50, 'teacher');
      setTeachers(teachersData.users);
      setEditingTeacher(null);
    } catch (error) {
      setError(error.response?.data?.error || 'Êõ¥Êñ∞ÊïôÂ∏à‰ø°ÊÅØÂ§±Ë¥•');
    } finally {
      setLoading(false);
    }
  };

  const handleCancelEditTeacher = () => {
    setEditingTeacher(null);
    setEditForm({ email: '', phone: '', school_id: '', major_id: '', is_active: true, new_password: '' });
  };

  const handleDeleteTeacher = async (userId) => {
    if (!window.confirm('Á°ÆÂÆöË¶ÅÂà†Èô§ËØ•ÊïôÂ∏àÂêóÔºüÊ≠§Êìç‰Ωú‰∏çÂèØÊÅ¢Â§ç„ÄÇ')) return;
    try {
      setLoading(true);
      setError('');
      setSuccess('');
      await deleteUser(userId);
      setSuccess('ÊïôÂ∏àÂ∑≤Âà†Èô§');
      const teachersData = await getUsers(1, 50, 'teacher');
      setTeachers(teachersData.users);
    } catch (error) {
      setError(error.response?.data?.error || 'Âà†Èô§ÊïôÂ∏àÂ§±Ë¥•');
    } finally {
      setLoading(false);
    }
  };



  return (
    <div className="admin-dashboard">
      <div className="dashboard-header">
        <h1>üëë ÁÆ°ÁêÜÂëòÊéßÂà∂Âè∞</h1>
      </div>

      {error && <div className="error-message">{error}</div>}
      {success && <div className="success-message">{success}</div>}

      <div className="tab-content">
        <div className="sub-tabs">
          <button 
            className={`sub-tab-btn ${activeSubTab === 'list' ? 'active' : ''}`}
            onClick={() => setActiveSubTab('list')}
          >
            ÊïôÂ∏àÂàóË°®
          </button>
          <button 
            className={`sub-tab-btn ${activeSubTab === 'add' ? 'active' : ''}`}
            onClick={() => setActiveSubTab('add')}
          >
            Ê∑ªÂä†ÊïôÂ∏à
          </button>
        </div>

        {activeSubTab === 'list' && (
          <div className="section-content">
            <div className="section-header">
              <h2>ÊïôÂ∏àÂàóË°®</h2>
              <button 
                className="add-btn"
                onClick={() => setActiveSubTab('add')}
              >
                Ê∑ªÂä†ÊïôÂ∏à
              </button>
            </div>

              <div className="teachers-table">
                <table>
                  <thead>
                    <tr>
                      <th>ID</th>
                      <th>Â∑•Âè∑</th>
                      <th>ÂßìÂêç</th>
                      <th>ÈÇÆÁÆ±</th>
                      <th>Â≠¶Ê†°</th>
                      <th>Èô¢ÈÉ®</th>
                      <th>Áä∂ÊÄÅ</th>
                      <th>Êìç‰Ωú</th>
                    </tr>
                  </thead>
                  <tbody>
                    {teachers.map(teacher => (
                      <tr key={teacher.id}>
                        <td>{teacher.id}</td>
                        <td>{teacher.username}</td>
                        <td>{teacher.name}</td>
                        <td>{teacher.email || '-'}</td>
                        <td>{teacher.school_id || '-'}</td>
                        <td>{teacher.major_id || '-'}</td>
                        <td>
                          <span className={`status-badge ${teacher.is_active ? 'active' : 'inactive'}`}>
                            {teacher.is_active ? 'Ê¥ªË∑É' : 'Á¶ÅÁî®'}
                          </span>
                        </td>
                        <td>
                          <button className="action-btn edit" onClick={() => handleEditTeacherClick(teacher)}>ÁºñËæë</button>
                          <button className="action-btn delete" onClick={() => handleDeleteTeacher(teacher.id)}>Âà†Èô§</button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}

        {activeSubTab === 'add' && (
          <div className="section-content">
            <div className="section-header">
              <h2>‚ûï Ê∑ªÂä†ÊïôÂ∏à</h2>
              <div className="header-actions">
                <button 
                  className="debug-btn"
                  onClick={() => {
                    console.log('ÂΩìÂâçÁä∂ÊÄÅ:', { schools, majors, addTeacherForm });
                    alert(`Â≠¶Ê†°Êï∞Èáè: ${schools.length}\nÈô¢ÈÉ®Êï∞Èáè: ${majors.length}\nÈÄâÊã©ÁöÑÂ≠¶Ê†°ID: ${addTeacherForm.school_id}`);
                  }}
                >
                  üêõ Ë∞ÉËØï‰ø°ÊÅØ
                </button>
                <button 
                  className="secondary-btn"
                  onClick={() => setActiveSubTab('list')}
                >
                  ‚Üê ËøîÂõûÊïôÂ∏àÂàóË°®
                </button>
              </div>
            </div>

              <div className="add-teacher-form">
                <div className="form-row">
                  <div className="form-group">
                    <label>Â∑•Âè∑Ôºö<span className="required-mark">*</span></label>
                    <input 
                      type="text" 
                      placeholder="ËØ∑ËæìÂÖ•Â∑•Âè∑"
                      value={addTeacherForm.job_no}
                      onChange={(e) => setAddTeacherForm(prev => ({ ...prev, job_no: e.target.value }))}
                    />
                  </div>

                  <div className="form-group">
                    <label>ÂßìÂêçÔºö<span className="required-mark">*</span></label>
                    <input 
                      type="text" 
                      placeholder="ËØ∑ËæìÂÖ•ÊïôÂ∏àÂßìÂêç"
                      value={addTeacherForm.name}
                      onChange={(e) => setAddTeacherForm(prev => ({ ...prev, name: e.target.value }))}
                    />
                  </div>
                </div>

                <div className="form-row">
                  <div className="form-group">
                    <label>ÂØÜÁ†ÅÔºö<span className="required-mark">*</span></label>
                    <input 
                      type="password" 
                      placeholder="ËØ∑ËæìÂÖ•ÂàùÂßãÂØÜÁ†Å"
                      value={addTeacherForm.password}
                      onChange={(e) => setAddTeacherForm(prev => ({ ...prev, password: e.target.value }))}
                    />
                  </div>

                  <div className="form-group">
                    <label>ÈÇÆÁÆ±Ôºö</label>
                    <input 
                      type="email" 
                      placeholder="ËØ∑ËæìÂÖ•ÈÇÆÁÆ±Âú∞ÂùÄ"
                      value={addTeacherForm.email}
                      onChange={(e) => setAddTeacherForm(prev => ({ ...prev, email: e.target.value }))}
                    />
                  </div>
                </div>

                <div className="form-row">
                  <div className="form-group">
                    <label>ÊâãÊú∫Âè∑Ôºö</label>
                    <input 
                      type="tel" 
                      placeholder="ËØ∑ËæìÂÖ•ÊâãÊú∫Âè∑Á†Å"
                      value={addTeacherForm.phone}
                      onChange={(e) => setAddTeacherForm(prev => ({ ...prev, phone: e.target.value }))}
                    />
                  </div>

                  <div className="form-group">
                    <label>Â≠¶Ê†°Ôºö<span className="required-mark">*</span></label>
                    <select 
                      value={addTeacherForm.school_id} 
                      onChange={(e) => handleSchoolChange(e.target.value)}
                    >
                      <option value="">ËØ∑ÈÄâÊã©Â≠¶Ê†°</option>
                      {schools.map(school => (
                        <option key={school.id} value={school.id}>
                          {school.name}
                        </option>
                      ))}
                    </select>
                    <small>ÂèØÁî®Â≠¶Ê†°Êï∞Èáè: {schools.length}</small>
                  </div>
                </div>

                <div className="form-row">
                  <div className="form-group">
                    <label>Èô¢ÈÉ®Ôºö<span className="required-mark">*</span></label>
                    <select 
                      value={addTeacherForm.major_id} 
                      onChange={(e) => setAddTeacherForm(prev => ({ ...prev, major_id: e.target.value }))}
                      disabled={!addTeacherForm.school_id}
                    >
                      <option value="">ËØ∑ÈÄâÊã©Èô¢ÈÉ®</option>
                      {majors.map(major => (
                        <option key={major.id} value={major.id}>
                          {major.name}
                        </option>
                      ))}
                    </select>
                    <small>ÂèØÁî®Èô¢ÈÉ®Êï∞Èáè: {majors.length} | Â≠¶Ê†°ID: {addTeacherForm.school_id}</small>
                  </div>

                  <div className="form-group">
                    <label>&nbsp;</label>
                    <div className="form-help">
                      <p>ÊïôÂ∏àË¥¶Âè∑ÂàõÂª∫ÂêéÔºåÂ∑•Âè∑„ÄÅÂßìÂêç„ÄÅÂ≠¶Ê†°„ÄÅÈô¢ÈÉ®‰ø°ÊÅØ‰∏çÂèØ‰øÆÊîπ</p>
                      <p>ÊïôÂ∏àÂè™ËÉΩ‰øÆÊîπËá™Â∑±ÁöÑÂØÜÁ†Å</p>
                    </div>
                  </div>
                </div>

                <div className="form-actions">
                  <button 
                    className="submit-btn"
                    onClick={handleAddTeacher}
                    disabled={loading || !addTeacherForm.job_no || !addTeacherForm.name || !addTeacherForm.password || !addTeacherForm.school_id || !addTeacherForm.major_id}
                  >
                    {loading ? 'ÂàõÂª∫‰∏≠...' : 'ÂàõÂª∫ÊïôÂ∏àË¥¶Âè∑'}
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>

      {editingTeacher && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h3>ÁºñËæëÊïôÂ∏àÔºö{editingTeacher.name}ÔºàÂ∑•Âè∑ {editingTeacher.username}Ôºâ</h3>
            </div>
            <div className="modal-body">
              <div className="form-row">
                <div className="form-group">
                  <label>ÈÇÆÁÆ±Ôºö</label>
                  <input type="email" value={editForm.email} onChange={(e) => setEditForm(prev => ({ ...prev, email: e.target.value }))} />
                </div>
                <div className="form-group">
                  <label>ÊâãÊú∫Âè∑Ôºö</label>
                  <input type="tel" value={editForm.phone} onChange={(e) => setEditForm(prev => ({ ...prev, phone: e.target.value }))} />
                </div>
              </div>
              <div className="form-row">
                <div className="form-group">
                  <label>Â≠¶Ê†°Ôºö</label>
                  <select value={editForm.school_id} onChange={async (e) => {
                    const val = e.target.value;
                    setEditForm(prev => ({ ...prev, school_id: val, major_id: '' }));
                    if (val) {
                      const majorsData = await getMajors(val);
                      setMajors(majorsData);
                    } else {
                      setMajors([]);
                    }
                  }}>
                    <option value="">ËØ∑ÈÄâÊã©Â≠¶Ê†°</option>
                    {schools.map(s => <option key={s.id} value={s.id}>{s.name}</option>)}
                  </select>
                </div>
                <div className="form-group">
                  <label>Èô¢ÈÉ®Ôºö</label>
                  <select value={editForm.major_id} onChange={(e) => setEditForm(prev => ({ ...prev, major_id: e.target.value }))} disabled={!editForm.school_id}>
                    <option value="">ËØ∑ÈÄâÊã©Èô¢ÈÉ®</option>
                    {majors.map(m => <option key={m.id} value={m.id}>{m.name}</option>)}
                  </select>
                </div>
              </div>
              <div className="form-row">
                <div className="form-group">
                  <label>ÂêØÁî®Áä∂ÊÄÅÔºö</label>
                  <select value={editForm.is_active ? '1' : '0'} onChange={(e) => setEditForm(prev => ({ ...prev, is_active: e.target.value === '1' }))}>
                    <option value="1">ÂêØÁî®</option>
                    <option value="0">Á¶ÅÁî®</option>
                  </select>
                </div>
                <div className="form-group">
                  <label>ÈáçÁΩÆÂØÜÁ†ÅÔºàÂèØÈÄâÔºâÔºö</label>
                  <input type="password" value={editForm.new_password} onChange={(e) => setEditForm(prev => ({ ...prev, new_password: e.target.value }))} />
                </div>
              </div>
            </div>
            <div className="modal-footer">
              <button className="secondary-btn" onClick={handleCancelEditTeacher}>ÂèñÊ∂à</button>
              <button className="submit-btn" onClick={handleSaveTeacher} disabled={loading}>{loading ? '‰øùÂ≠ò‰∏≠...' : '‰øùÂ≠ò'}</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboardPage;
