import React, { useState, useEffect, useCallback } from 'react';
import './AssignmentWhitelistManager.css';
import { 
  getAssignmentOverdueUsers, 
  addUserToOverdueWhitelist, 
  removeUserFromOverdueWhitelist 
} from '../services/api';

const AssignmentWhitelistManager = ({ assignment, onUpdate }) => {
  const [overdueUsers, setOverdueUsers] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [selectedUser, setSelectedUser] = useState('');
  const [message, setMessage] = useState('');
  const [courseStudents, setCourseStudents] = useState([]);

  // Âä†ËΩΩÈÄæÊúüÁî®Êà∑ÁôΩÂêçÂçï
  const loadOverdueUsers = useCallback(async () => {
    try {
      setIsLoading(true);
      const response = await getAssignmentOverdueUsers(assignment.id);
      setOverdueUsers(response.overdue_users || []);
    } catch (error) {
      console.error('Âä†ËΩΩÁôΩÂêçÂçïÂ§±Ë¥•:', error);
      setMessage('Âä†ËΩΩÁôΩÂêçÂçïÂ§±Ë¥•');
    } finally {
      setIsLoading(false);
    }
  }, [assignment.id]);

  // Âä†ËΩΩËØæÁ®ãÂ≠¶ÁîüÂàóË°®
  const loadCourseStudents = useCallback(async () => {
    try {
      // ËøôÈáåÂ∫îËØ•Ë∞ÉÁî®Ëé∑ÂèñËØæÁ®ãÂ≠¶ÁîüÁöÑAPI
      // ÊöÇÊó∂‰ΩøÁî®Ê®°ÊãüÊï∞ÊçÆ
      const mockStudents = [
        { id: 1, name: 'Âº†‰∏â', username: 'zhangsan001' },
        { id: 2, name: 'ÊùéÂõõ', username: 'lisi002' },
        { id: 3, name: 'Áéã‰∫î', username: 'wangwu003' }
      ];
      setCourseStudents(mockStudents);
    } catch (error) {
      console.error('Âä†ËΩΩËØæÁ®ãÂ≠¶ÁîüÂ§±Ë¥•:', error);
    }
  }, []);

  // Ê∑ªÂä†Áî®Êà∑Âà∞ÁôΩÂêçÂçï
  const addUserToWhitelist = async () => {
    if (!selectedUser) {
      setMessage('ËØ∑ÈÄâÊã©Ë¶ÅÊ∑ªÂä†ÁöÑÂ≠¶Áîü');
      return;
    }

    try {
      setIsLoading(true);
      const response = await addUserToOverdueWhitelist(assignment.id, parseInt(selectedUser));

      setOverdueUsers(response.overdue_users || []);
      setSelectedUser('');
      setMessage('Â≠¶ÁîüÂ∑≤Ê∑ªÂä†Âà∞ÁôΩÂêçÂçï');

      // ÈÄöÁü•Áà∂ÁªÑ‰ª∂Êõ¥Êñ∞
      if (onUpdate) {
        onUpdate();
      }
    } catch (error) {
      console.error('Ê∑ªÂä†Â≠¶ÁîüÂ§±Ë¥•:', error);
      setMessage(error.message || 'Ê∑ªÂä†Â≠¶ÁîüÂ§±Ë¥•');
    } finally {
      setIsLoading(false);
    }
  };

  // ‰ªéÁôΩÂêçÂçïÁßªÈô§Áî®Êà∑
  const removeUserFromWhitelist = async (userId) => {
    try {
      setIsLoading(true);
      const response = await removeUserFromOverdueWhitelist(assignment.id, userId);

      setOverdueUsers(response.overdue_users || []);
      setMessage('Â≠¶ÁîüÂ∑≤‰ªéÁôΩÂêçÂçï‰∏≠ÁßªÈô§');

      // ÈÄöÁü•Áà∂ÁªÑ‰ª∂Êõ¥Êñ∞
      if (onUpdate) {
        onUpdate();
      }
    } catch (error) {
      console.error('ÁßªÈô§Â≠¶ÁîüÂ§±Ë¥•:', error);
      setMessage(error.message || 'ÁßªÈô§Â≠¶ÁîüÂ§±Ë¥•');
    } finally {
      setIsLoading(false);
    }
  };

  // ÁªÑ‰ª∂ÊåÇËΩΩÊó∂Âä†ËΩΩÊï∞ÊçÆ
  useEffect(() => {
    if (assignment.allow_overdue_submission) {
      loadOverdueUsers();
      loadCourseStudents();
    }
  }, [assignment.id, assignment.allow_overdue_submission, loadOverdueUsers, loadCourseStudents]);

  // Â¶ÇÊûú‰Ωú‰∏ö‰∏çÂÖÅËÆ∏Ë°•‰∫§Ôºå‰∏çÊòæÁ§∫ÁªÑ‰ª∂
  if (!assignment.allow_overdue_submission) {
    return null;
  }

  return (
    <div className="assignment-whitelist-manager">
      <div className="whitelist-header">
        <h5>üë• Ë°•‰∫§ÁôΩÂêçÂçïÁÆ°ÁêÜ</h5>
        <span className="whitelist-count">({overdueUsers.length}‰∫∫)</span>
      </div>

      <div className="whitelist-content">
        {/* Ê∑ªÂä†Â≠¶ÁîüÂà∞ÁôΩÂêçÂçï */}
        <div className="add-student-section">
          <div className="add-student-form">
            <select
              value={selectedUser}
              onChange={(e) => setSelectedUser(e.target.value)}
              disabled={isLoading}
            >
              <option value="">ÈÄâÊã©Â≠¶Áîü...</option>
              {courseStudents
                .filter(student => !overdueUsers.some(u => u.id === student.id))
                .map(student => (
                  <option key={student.id} value={student.id}>
                    {student.name} ({student.username})
                  </option>
                ))}
            </select>
            <button
              onClick={addUserToWhitelist}
              disabled={!selectedUser || isLoading}
              className="add-student-btn"
            >
              {isLoading ? 'Ê∑ªÂä†‰∏≠...' : 'Ê∑ªÂä†Âà∞ÁôΩÂêçÂçï'}
            </button>
          </div>
        </div>

        {/* ÁôΩÂêçÂçïÂ≠¶ÁîüÂàóË°® */}
        <div className="whitelist-students">
          {isLoading ? (
            <div className="loading">Âä†ËΩΩ‰∏≠...</div>
          ) : overdueUsers.length > 0 ? (
            <div className="student-list">
              {overdueUsers.map(user => (
                <div key={user.id} className="student-item">
                  <div className="student-info">
                    <span className="student-name">{user.name}</span>
                    <span className="student-username">({user.username})</span>
                  </div>
                  <button
                    className="remove-student-btn"
                    onClick={() => removeUserFromWhitelist(user.id)}
                    disabled={isLoading}
                    title="‰ªéÁôΩÂêçÂçï‰∏≠ÁßªÈô§"
                  >
                    ‚úï
                  </button>
                </div>
              ))}
            </div>
          ) : (
            <div className="empty-whitelist">
              <p>ÊöÇÊó†ÁôΩÂêçÂçïÂ≠¶Áîü</p>
              <small>Ê∑ªÂä†Â≠¶ÁîüÂà∞ÁôΩÂêçÂçïÂêéÔºå‰ªñ‰ª¨Â∞±ÂèØ‰ª•Âú®Êà™Ê≠¢Êó∂Èó¥ÂêéË°•‰∫§‰Ωú‰∏ö</small>
            </div>
          )}
        </div>

        {/* Ê∂àÊÅØÊèêÁ§∫ */}
        {message && (
          <div className={`message ${message.includes('Â§±Ë¥•') ? 'error' : 'success'}`}>
            {message}
          </div>
        )}

        {/* ÁôΩÂêçÂçïËØ¥Êòé */}
        <div className="whitelist-info">
          <h6>üí° ÁôΩÂêçÂçïËØ¥Êòé</h6>
          <ul>
            <li>Âè™ÊúâÁôΩÂêçÂçï‰∏≠ÁöÑÂ≠¶ÁîüÂèØ‰ª•Âú®Êà™Ê≠¢Êó∂Èó¥ÂêéË°•‰∫§‰Ωú‰∏ö</li>
            <li>Ë°•‰∫§Êà™Ê≠¢Êó∂Èó¥ÂêéÔºåÁ≥ªÁªüÂ∞ÜÊãíÁªùÊâÄÊúâË°•‰∫§ËØ∑Ê±Ç</li>
            <li>ÂèØ‰ª•ÈöèÊó∂Ê∑ªÂä†ÊàñÁßªÈô§ÁôΩÂêçÂçïÂ≠¶Áîü</li>
            <li>Âª∫ËÆÆÂè™Â∞ÜÁ°ÆÂÆûÈúÄË¶ÅË°•‰∫§ÁöÑÂ≠¶ÁîüÊ∑ªÂä†Âà∞ÁôΩÂêçÂçï</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default AssignmentWhitelistManager;
