import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import SubmitPage from './pages/SubmitPage';
import ResultPage from './pages/ResultPage';
import ProblemListPage from './pages/ProblemListPage';
import ProblemDetailPage from './pages/ProblemDetailPage';
import TeacherDashboardPage from './pages/TeacherDashboardPage';
import TestPage from './TestPage';
import './App.css';


// 简单的路由保护组件
const ProtectedRoute = ({ children, allowedRoles = [] }) => {
  const user = JSON.parse(localStorage.getItem('user') || '{}');
  const token = localStorage.getItem('token');

  if (!token) {
    return <Navigate to="/login" replace />;
  }

  if (allowedRoles.length > 0 && !allowedRoles.includes(user.role)) {
    return <Navigate to="/" replace />;
  }

  return children;
};

function App() {
  const user = JSON.parse(localStorage.getItem('user') || '{}');
  const isLoggedIn = !!localStorage.getItem('token');

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  return (
    <Router>
      <div className="app-container">
        {isLoggedIn && (
          <nav className="navbar">
            <div className="container">
              <Link to="/" className="logo">OJ System</Link>
              <div className="nav-links">
                <Link to="/problems">题目列表</Link>
                <Link to="/submissions">提交记录</Link>
                {user.role === 'teacher' && (
                  <>
                    <Link to="/teacher/dashboard">教师管理</Link>
                    <Link to="/users">用户管理</Link>
                  </>
                )}
                <div className="user-menu">
                  <span>欢迎, {user.name}</span>
                  <button onClick={handleLogout} className="logout-btn">退出</button>
                </div>
              </div>
            </div>
          </nav>
        )}

        <main className="main-content">
          <div className="container">
            <Routes>
              <Route path="/test" element={<TestPage />} />
              <Route path="/login" element={!isLoggedIn ? <LoginPage /> : <Navigate to="/" replace />} />
              <Route path="/register" element={!isLoggedIn ? <RegisterPage /> : <Navigate to="/" replace />} />
              <Route path="/" element={<Navigate to="/test" replace />} />
              <Route path="/problems" element={
                <ProtectedRoute>
                  <ProblemListPage />
                </ProtectedRoute>
              } />
              <Route path="/problem/:problemId" element={
                <ProtectedRoute>
                  <ProblemDetailPage />
                </ProtectedRoute>
              } />
              <Route path="/submit/:problemId" element={
                <ProtectedRoute>
                  <SubmitPage />
                </ProtectedRoute>
              } />
              <Route path="/result/:submissionId" element={
                <ProtectedRoute>
                  <ResultPage />
                </ProtectedRoute>
              } />
              <Route path="/submissions" element={
                <ProtectedRoute>
                  <SubmitPage />
                </ProtectedRoute>
              } />
              <Route path="/teacher/dashboard" element={
                <ProtectedRoute allowedRoles={['teacher', 'admin']}>
                  <TeacherDashboardPage />
                </ProtectedRoute>
              } />
            </Routes>
          </div>
        </main>

        {isLoggedIn && (
          <footer className="footer">
            <div className="container">
              <p>© 2023 OJ System | 在线评测平台</p>
              <div className="footer-links">
                <a href="/">关于我们</a>
                <a href="/">帮助中心</a>
                <a href="/">联系我们</a>
              </div>
            </div>
          </footer>
        )}
      </div>
    </Router>
  );
}

export default App;
