import React, { useState, useEffect } from 'react';
import { getTeacherCourses, getCourseAssignments, getCourseStudents } from '../services/api';
import './StudentDashboardPage.css';
import WhaleIcon from '../components/WhaleIcon';
const StudentDashboardPage = () => {
  const [activeTab, setActiveTab] = useState('courses');
  const [activeSubTab, setActiveSubTab] = useState('list');
  const [courses, setCourses] = useState([]);
  const [assignments, setAssignments] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // ËØæÁ®ãÁõ∏ÂÖ≥Áä∂ÊÄÅÔºàÂè™ËØªÔºåÁî®‰∫éÊü•ÁúãÔºâ
  const [editingCourse, setEditingCourse] = useState(null);

  // ‰Ωú‰∏öÁõ∏ÂÖ≥Áä∂ÊÄÅ
  const [editingAssignment, setEditingAssignment] = useState(null);
  const [viewingAssignment, setViewingAssignment] = useState(null);
  const [assignmentProblems, setAssignmentProblems] = useState([]);

  // Â≠¶Áîü-ËØæÁ®ãÂÖ≥ËÅîÂÖ≥Á≥ªÁä∂ÊÄÅ
  const [studentClassRelations, setStudentClassRelations] = useState([]);

  useEffect(() => {
    fetchInitialData();
  }, []);

  // ‰ªéAPIÂä†ËΩΩÂ≠¶Áîü-ËØæÁ®ãÂÖ≥ËÅîÂÖ≥Á≥ª
  const fetchStudentClassRelations = async (courseId) => {
    try {
      const courseStudentsData = await getCourseStudents(courseId);
      if (courseStudentsData && courseStudentsData.length > 0) {
        const apiRelations = courseStudentsData.map(student => ({
          student_id: student.student_id,
          class_id: student.class_id,
          course_id: courseId,
          student_name: student.student_name,
          student_no: student.student_no,
          added_at: student.added_at || new Date().toISOString()
        }));
        setStudentClassRelations(apiRelations);
      } else {
        setStudentClassRelations([]);
      }
    } catch (error) {
      console.error('Ëé∑ÂèñËØæÁ®ãÂ≠¶ÁîüÂÖ≥ËÅîÂÖ≥Á≥ªÂ§±Ë¥•:', error);
      setStudentClassRelations([]);
    }
  };

  // ÁõëÂê¨ËØæÁ®ãÂèòÂåñÔºåÂä†ËΩΩËØæÁ®ãÂ≠¶ÁîüÂÖ≥ËÅîÂÖ≥Á≥ª
  useEffect(() => {
    const loadCourseStudents = async () => {
      if (editingCourse && editingCourse.id) {
        console.log('ÂºÄÂßãÂä†ËΩΩËØæÁ®ãÂ≠¶ÁîüÊï∞ÊçÆÔºåËØæÁ®ãID:', editingCourse.id);
        await fetchStudentClassRelations(editingCourse.id);
      }
    };

    loadCourseStudents();
  }, [editingCourse]); // eslint-disable-line react-hooks/exhaustive-deps

  const fetchInitialData = async () => {
    try {
      setLoading(true);
      setError('');
      setSuccess('');

      // Ëé∑ÂèñÂ≠¶ÁîüÊâÄÂú®ÁöÑËØæÁ®ãÊï∞ÊçÆ
      try {
        const coursesData = await getTeacherCourses();
        if (coursesData && coursesData.courses) {
          setCourses(coursesData.courses);
          console.log('Ëé∑ÂèñÂà∞Â≠¶ÁîüËØæÁ®ãÊï∞ÊçÆ:', coursesData.courses);
        } else {
          setCourses([]);
        }
      } catch (error) {
        console.warn('Ëé∑ÂèñËØæÁ®ãÊï∞ÊçÆÂ§±Ë¥•:', error);
        setCourses([]);
      }

      // Ëé∑Âèñ‰Ωú‰∏öÊï∞ÊçÆ
      try {
        const assignmentsData = await getCourseAssignments();
        if (assignmentsData && assignmentsData.assignments) {
          setAssignments(assignmentsData.assignments);
          console.log('Ëé∑ÂèñÂà∞‰Ωú‰∏öÊï∞ÊçÆ:', assignmentsData.assignments);
        } else {
          setAssignments([]);
        }
      } catch (error) {
        console.warn('Ëé∑Âèñ‰Ωú‰∏öÊï∞ÊçÆÂ§±Ë¥•:', error);
        setAssignments([]);
      }

    } catch (error) {
      setError(error?.response?.data?.error || 'Ëé∑ÂèñÊï∞ÊçÆÂ§±Ë¥•');
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  // Êü•ÁúãËØæÁ®ãËØ¶ÊÉÖ
  const handleViewCourseDetail = (courseId) => {
    const course = courses.find(c => c.id === courseId);
    if (course) {
      setEditingCourse(course);
      setActiveSubTab('detail');
    }
  };

  // Êü•Áúã‰Ωú‰∏öËØ¶ÊÉÖ
  const handleViewAssignmentDetail = async (assignment) => {
    try {
      setViewingAssignment(assignment);

      // Ëé∑Âèñ‰Ωú‰∏öÂåÖÂê´ÁöÑÈ¢òÁõÆËØ¶ÊÉÖ
      if (assignment.problem_ids && assignment.problem_ids.length > 0) {
        // ËøôÈáåÂèØ‰ª•Ë∞ÉÁî®APIËé∑ÂèñÈ¢òÁõÆËØ¶ÊÉÖÔºåÊöÇÊó∂‰ΩøÁî®È¢òÁõÆIDÂàóË°®
        setAssignmentProblems(assignment.problem_ids.map(id => ({
          id: id,
          title: `È¢òÁõÆ ${id}`,
          description: `ËøôÊòØ‰Ωú‰∏ö"${assignment.name}"‰∏≠ÁöÑÁ¨¨${id}È¢ò`
        })));
      } else {
        setAssignmentProblems([]);
      }
    } catch (error) {
      console.error('Ëé∑Âèñ‰Ωú‰∏öÈ¢òÁõÆËØ¶ÊÉÖÂ§±Ë¥•:', error);
      setAssignmentProblems([]);
    }
  };

  // ÂÖ≥Èó≠‰Ωú‰∏öËØ¶ÊÉÖ
  const handleCloseAssignmentDetail = () => {
    setViewingAssignment(null);
    setAssignmentProblems([]);
  };

  // ËøîÂõûËØæÁ®ãÂàóË°®
  const handleBackToCourses = () => {
    setEditingCourse(null);
    setActiveSubTab('list');
  };

  // Ê†ºÂºèÂåñÊó•ÊúüÊòæÁ§∫
  const formatDate = (dateString) => {
    if (!dateString) return 'Êú™ËÆæÁΩÆ';
    const date = new Date(dateString);
    return date.toLocaleString('zh-CN', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  // ËØæÁ®ãÊü•ÁúãÊ†áÁ≠æÈ°µÊ∏≤ÊüìÂáΩÊï∞ÔºàÂè™ËØªÂäüËÉΩÔºâ
  const renderCoursesTab = () => (
    <div className="tab-content">
      <div className="sub-tabs">
        <button 
          className={`sub-tab-btn ${activeSubTab === 'list' ? 'active' : ''}`}
          onClick={() => setActiveSubTab('list')}
        >
          ËØæÁ®ãÂàóË°®
        </button>
        {editingCourse && (
          <button 
            className={`sub-tab-btn ${activeSubTab === 'detail' ? 'active' : ''}`}
            onClick={() => setActiveSubTab('detail')}
          >
            ËØæÁ®ãËØ¶ÊÉÖ
          </button>
        )}
      </div>

      {activeSubTab === 'list' && (
        <div className="section-content">
          <div className="section-header">
            <h2>ÊàëÁöÑËØæÁ®ã</h2>
            <div className="section-info">
              <p>üí° ÁÇπÂáª‰ªªÊÑèËØæÁ®ãÂç°ÁâáÂç≥ÂèØÊü•ÁúãËØ¶ÊÉÖÂíå‰Ωú‰∏ö‰ø°ÊÅØ</p>
            </div>
          </div>

          <div className="courses-grid">
            {courses.length === 0 ? (
              <div className="empty-state">
                <p>ÊöÇÊó†ËØæÁ®ãÔºåËØ∑ËÅîÁ≥ªÁÆ°ÁêÜÂëòÊàñÊïôÂ∏àÊ∑ªÂä†ËØæÁ®ã</p>
              </div>
            ) : (
              courses.map(course => (
                <div 
                  key={course.id} 
                  className="course-card"
                  onClick={() => handleViewCourseDetail(course.id)}
                  style={{ cursor: 'pointer' }}
                >
                  <div className="course-header">
                    <h3>{course.name}</h3>
                    <span className="course-status active">ËøõË°å‰∏≠</span>
                  </div>
                  <div className="course-info">
                    <p><strong>ÊïôÂ≠¶Áè≠Ôºö</strong>{course.class_name || 'Êú™ËÆæÁΩÆ'}</p>
                    <p><strong>‰∏ì‰∏öÔºö</strong>{course.major_name || 'Êú™ËÆæÁΩÆ'}</p>
                    <p><strong>Â≠¶Ê†°Ôºö</strong>{course.school_name || 'Êú™ËÆæÁΩÆ'}</p>
                    <p><strong>Â≠¶ÁîüÊï∞ÈáèÔºö</strong>{course.student_count || 0}‰∫∫</p>
                    <p><strong>ÂàõÂª∫Êó∂Èó¥Ôºö</strong>{formatDate(course.created_at)}</p>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      )}

      {activeSubTab === 'detail' && editingCourse && (
        <div className="section-content">
          <div className="section-header">
            <button className="back-btn" onClick={handleBackToCourses}>
              ‚Üê ËøîÂõûËØæÁ®ãÂàóË°®
            </button>
            <h2>ËØæÁ®ãËØ¶ÊÉÖÔºö{editingCourse.name}</h2>
          </div>

          <div className="course-detail-content">
            {/* ËØæÁ®ãÂü∫Êú¨‰ø°ÊÅØ */}
            <div className="course-info-section">
              <h3>üìö ËØæÁ®ãÂü∫Êú¨‰ø°ÊÅØ</h3>
              <div className="info-grid">
                <div className="info-item">
                  <label>ËØæÁ®ãÂêçÁß∞Ôºö</label>
                  <span>{editingCourse.name}</span>
                </div>
                <div className="info-item">
                  <label>ËØæÁ®ãÊèèËø∞Ôºö</label>
                  <span>{editingCourse.description || 'ÊöÇÊó†ÊèèËø∞'}</span>
                </div>
                <div className="info-item">
                  <label>ÊïôÂ≠¶Áè≠Ôºö</label>
                  <span>{editingCourse.class_name || 'Êú™ËÆæÁΩÆ'}</span>
                </div>
                <div className="info-item">
                  <label>‰∏ì‰∏öÔºö</label>
                  <span>{editingCourse.major_name || 'Êú™ËÆæÁΩÆ'}</span>
                </div>
                <div className="info-item">
                  <label>Â≠¶Ê†°Ôºö</label>
                  <span>{editingCourse.school_name || 'Êú™ËÆæÁΩÆ'}</span>
                </div>
                <div className="info-item">
                  <label>Â≠¶ÁîüÊï∞ÈáèÔºö</label>
                  <span>{editingCourse.student_count || 0}‰∫∫</span>
                </div>
                <div className="info-item">
                  <label>ÂàõÂª∫Êó∂Èó¥Ôºö</label>
                  <span>{formatDate(editingCourse.created_at)}</span>
                </div>
              </div>
            </div>

            {/* ËØæÁ®ã‰Ωú‰∏ö */}
            <div className="course-assignments-section">
              <h3>üìù ËØæÁ®ã‰Ωú‰∏ö</h3>
              {assignments.filter(a => a.course_id === editingCourse.id).length === 0 ? (
                <div className="empty-state">
                  <p>ËØ•ËØæÁ®ãÊöÇÊó†‰Ωú‰∏ö</p>
                </div>
              ) : (
                <div className="assignments-list">
                  {assignments
                    .filter(a => a.course_id === editingCourse.id)
                    .map(assignment => (
                      <div 
                        key={assignment.id} 
                        className="assignment-item clickable"
                        onClick={() => handleViewAssignmentDetail(assignment)}
                      >
                        <div className="assignment-header">
                          <h4>{assignment.name}</h4>
                          <span className={`assignment-status ${assignment.is_active ? 'active' : 'inactive'}`}>
                            {assignment.is_active ? 'ËøõË°å‰∏≠' : 'Â∑≤ÁªìÊùü'}
                          </span>
                        </div>
                        <div className="assignment-info">
                          <p><strong>ÊèèËø∞Ôºö</strong>{assignment.description || 'ÊöÇÊó†ÊèèËø∞'}</p>
                          <p><strong>Ë¶ÅÊ±ÇÔºö</strong>{assignment.requirements || 'ÊöÇÊó†Ë¶ÅÊ±Ç'}</p>
                          <p><strong>Êà™Ê≠¢Êó∂Èó¥Ôºö</strong>{formatDate(assignment.due_date)}</p>
                          <p><strong>È¢òÁõÆÊï∞ÈáèÔºö</strong>{assignment.problem_ids ? assignment.problem_ids.length : 0}È¢ò</p>
                          <p className="click-hint">üí° ÁÇπÂáªÊü•Áúã‰Ωú‰∏öËØ¶ÊÉÖÂíåÈ¢òÁõÆ</p>
                        </div>
                      </div>
                    ))}
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );

  return (
    <div className="student-dashboard">
      <div className="dashboard-header">
        <h1>Â≠¶Áîü‰ª™Ë°®Êùø</h1>
        <p>Ê¨¢ËøéÂõûÊù•Ôºå{JSON.parse(localStorage.getItem('user') || '{}').name}ÔºÅ</p>
      </div>

      {error && <div className="error-message">{error}</div>}
      {success && <div className="success-message">{success}</div>}

      <div className="dashboard-tabs">
        <button 
          className={`tab-btn ${activeTab === 'courses' ? 'active' : ''}`}
          onClick={() => {
            setActiveTab('courses');
            setActiveSubTab('list');
            setEditingCourse(null);
          }}
        >
          ÊàëÁöÑËØæÁ®ã
        </button>
      </div>

      {activeTab === 'courses' && renderCoursesTab()}
    </div>
  );
};

export default StudentDashboardPage;
