//代码提交页面组件：
//提供表单输入用户 ID、题目 ID、选择语言（Python/C++）、输入代码。
//提交表单时调用 API 发送代码，展示提交 ID 和结果查询链接。
//处理加载状态（提交中禁用按钮）。

import React, { useState } from 'react';
import { submitCode } from '../services/api';

const SubmitPage = () => {
  const [form, setForm] = useState({
    userId: '',
    problemId: '',
    code: '',
    language: 'python'
  });
  const [submission, setSubmission] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const result = await submitCode(form);
      setSubmission(result);
    } catch (error) {
      console.error('提交失败:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h1>提交代码</h1>
      {submission ? (
        <div>
          <p>提交ID: {submission.submission_id}</p>
          <p>状态: {submission.status}</p>
          <a href={`/result/${submission.submission_id}`}>查看结果</a>
        </div>
      ) : (
        <form onSubmit={handleSubmit}>
          <div>
            <label>用户ID:</label>
            <input 
              type="text" 
              value={form.userId}
              onChange={(e) => setForm({...form, userId: e.target.value})}
              required
            />
          </div>
          <div>
            <label>题目ID:</label>
            <input 
              type="text" 
              value={form.problemId}
              onChange={(e) => setForm({...form, problemId: e.target.value})}
              required
            />
          </div>
          <div>
            <label>语言:</label>
            <select
              value={form.language}
              onChange={(e) => setForm({...form, language: e.target.value})}
            >
              <option value="python">Python</option>
              <option value="cpp">C++</option>
            </select>
          </div>
          <div>
            <label>代码:</label>
            <textarea
              rows={10}
              cols={50}
              value={form.code}
              onChange={(e) => setForm({...form, code: e.target.value})}
              required
            />
          </div>
          <button type="submit" disabled={loading}>
            {loading ? '提交中...' : '提交'}
          </button>
        </form>
      )}
    </div>
  );
};

export default SubmitPage;
