import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { register, getSchools, getMajors, getClasses } from '../services/api';
import './RegisterPage.css';

const RegisterPage = () => {
  const [form, setForm] = useState({
    username: '',
    password: '',
    confirmPassword: '',
    name: '',
    email: '',
    phone: '',
    role: 'student',
    school_id: '',
    major_id: '',
    class_id: ''
  });

  const [schools, setSchools] = useState([]);
  const [majors, setMajors] = useState([]);
  const [classes, setClasses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetchSchools();
  }, []);

  const fetchSchools = async () => {
    try {
      const schoolsData = await getSchools();
      setSchools(schoolsData);
    } catch (error) {
      console.error('获取学校列表失败:', error);
    }
  };

  const handleSchoolChange = async (schoolId) => {
    setForm(prev => ({ ...prev, school_id: schoolId, major_id: '', class_id: '' }));
    setMajors([]);
    setClasses([]);

    if (schoolId) {
      try {
        const majorsData = await getMajors(schoolId);
        setMajors(majorsData);
      } catch (error) {
        console.error('获取专业列表失败:', error);
      }
    }
  };

  const handleMajorChange = async (majorId) => {
    setForm(prev => ({ ...prev, major_id: majorId, class_id: '' }));
    setClasses([]);

    if (majorId) {
      try {
        const classesData = await getClasses(majorId);
        setClasses(classesData);
      } catch (error) {
        console.error('获取班级列表失败:', error);
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');

    // 表单验证
    if (form.password !== form.confirmPassword) {
      setError('两次输入的密码不一致');
      setLoading(false);
      return;
    }

    if (form.password.length < 6) {
      setError('密码长度至少6位');
      setLoading(false);
      return;
    }

    try {
      const registerData = {
        username: form.username,
        password: form.password,
        name: form.name,
        email: form.email || undefined,
        phone: form.phone || undefined,
        role: form.role,
        school_id: form.school_id || undefined,
        major_id: form.major_id || undefined,
        class_id: form.class_id || undefined
      };

      await register(registerData);
      setSuccess('注册成功！请登录');

      // 3秒后跳转到登录页面
      setTimeout(() => {
        navigate('/login');
      }, 3000);

    } catch (error) {
      setError(error.response?.data?.error || '注册失败，请重试');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (field, value) => {
    setForm(prev => ({ ...prev, [field]: value }));
  };

  return (
    <div className="register-container">
      <div className="register-card">
        <h1>用户注册</h1>

        {error && <div className="error-message">⚠️ {error}</div>}
        {success && <div className="success-message">✅ {success}</div>}

        <form onSubmit={handleSubmit}>
          {/* 基本信息 */}
          <div className="form-section">
            <h3>基本信息</h3>

            <div className="form-group">
              <label>用户名 *</label>
              <input
                type="text"
                value={form.username}
                onChange={(e) => handleInputChange('username', e.target.value)}
                placeholder="请输入用户名"
                required
              />
            </div>

            <div className="form-group">
              <label>姓名 *</label>
              <input
                type="text"
                value={form.name}
                onChange={(e) => handleInputChange('name', e.target.value)}
                placeholder="请输入真实姓名"
                required
              />
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>密码 *</label>
                <input
                  type="password"
                  value={form.password}
                  onChange={(e) => handleInputChange('password', e.target.value)}
                  placeholder="至少6位"
                  required
                />
              </div>

              <div className="form-group">
                <label>确认密码 *</label>
                <input
                  type="password"
                  value={form.confirmPassword}
                  onChange={(e) => handleInputChange('confirmPassword', e.target.value)}
                  placeholder="再次输入密码"
                  required
                />
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>邮箱</label>
                <input
                  type="email"
                  value={form.email}
                  onChange={(e) => handleInputChange('email', e.target.value)}
                  placeholder="请输入邮箱地址"
                />
              </div>

              <div className="form-group">
                <label>手机号</label>
                <input
                  type="tel"
                  value={form.phone}
                  onChange={(e) => handleInputChange('phone', e.target.value)}
                  placeholder="请输入手机号"
                />
              </div>
            </div>

            <div className="form-group">
              <label>用户角色 *</label>
              <select
                value={form.role}
                onChange={(e) => handleInputChange('role', e.target.value)}
                required
              >
                <option value="student">学生</option>
                <option value="teacher">教师</option>
              </select>
            </div>
          </div>

          {/* 学校信息（可选） */}
          {form.role === 'student' && (
            <div className="form-section">
              <h3>学校信息（可选）</h3>

              <div className="form-group">
                <label>学校</label>
                <select
                  value={form.school_id}
                  onChange={(e) => handleSchoolChange(e.target.value)}
                >
                  <option value="">请选择学校</option>
                  {schools.map(school => (
                    <option key={school.id} value={school.id}>
                      {school.name}
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label>专业</label>
                <select
                  value={form.major_id}
                  onChange={(e) => handleMajorChange(e.target.value)}
                  disabled={!form.school_id}
                >
                  <option value="">请选择专业</option>
                  {majors.map(major => (
                    <option key={major.id} value={major.id}>
                      {major.name}
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label>班级</label>
                <select
                  value={form.class_id}
                  onChange={(e) => handleInputChange('class_id', e.target.value)}
                  disabled={!form.major_id}
                >
                  <option value="">请选择班级</option>
                  {classes.map(cls => (
                    <option key={cls.id} value={cls.id}>
                      {cls.name}
                    </option>
                  ))}
                </select>
              </div>
            </div>
          )}

          <div className="form-actions">
            <button 
              type="submit" 
              disabled={loading || success}
              className="register-btn"
            >
              {loading ? '注册中...' : success ? '注册成功！' : '注册'}
            </button>
          </div>
        </form>

        <div className="login-link">
          已有账户？<Link to="/login">立即登录</Link>
        </div>
      </div>
    </div>
  );
};

export default RegisterPage;
