import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getTeacherCourses, getCourseAssignments, getCourseStudents, getProblems, getAssignmentCompletionStatus } from '../services/api';
import './StudentDashboardPage.css';
import WhaleIcon from '../components/WhaleIcon';
const StudentDashboardPage = () => {
  const [activeTab, setActiveTab] = useState('courses');
  const [activeSubTab, setActiveSubTab] = useState('list');
  const [courses, setCourses] = useState([]);
  const [assignments, setAssignments] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // ËØæÁ®ãÁõ∏ÂÖ≥Áä∂ÊÄÅÔºàÂè™ËØªÔºåÁî®‰∫éÊü•ÁúãÔºâ
  const [editingCourse, setEditingCourse] = useState(null);

  // ‰Ωú‰∏öÁõ∏ÂÖ≥Áä∂ÊÄÅ
  const [editingAssignment, setEditingAssignment] = useState(null);
  const [viewingAssignment, setViewingAssignment] = useState(null);
  const [assignmentProblems, setAssignmentProblems] = useState([]);

  // ‰Ωú‰∏öÂÆåÊàêÁä∂ÊÄÅ
  const [assignmentCompletionStatus, setAssignmentCompletionStatus] = useState({});

  // Â≠¶Áîü-ËØæÁ®ãÂÖ≥ËÅîÂÖ≥Á≥ªÁä∂ÊÄÅ
  const [studentClassRelations, setStudentClassRelations] = useState([]);

  useEffect(() => {
    fetchInitialData();

    // Ê∑ªÂä†ÂÆöÊúüÂà∑Êñ∞‰Ωú‰∏öÂÆåÊàêÁä∂ÊÄÅÁöÑÂÆöÊó∂Âô®
    const refreshInterval = setInterval(() => {
      if (editingCourse && editingCourse.id) {
        fetchAssignmentCompletionStatus(editingCourse.id);
      }
    }, 30000); // ÊØè30ÁßíÂà∑Êñ∞‰∏ÄÊ¨°

    // ÁõëÂê¨È°µÈù¢ÂèØËßÅÊÄßÂèòÂåñÔºåÂΩìÈ°µÈù¢ÈáçÊñ∞ÂèØËßÅÊó∂Âà∑Êñ∞Êï∞ÊçÆ
    const handleVisibilityChange = () => {
      if (!document.hidden && editingCourse && editingCourse.id) {
        console.log('È°µÈù¢ÈáçÊñ∞ÂèØËßÅÔºåÂà∑Êñ∞‰Ωú‰∏öÂÆåÊàêÁä∂ÊÄÅ');
        fetchAssignmentCompletionStatus(editingCourse.id);
      }
    };

    // ÁõëÂê¨storage‰∫ã‰ª∂ÔºåÁî®‰∫éË∑®Ê†áÁ≠æÈ°µÈÄö‰ø°
    const handleStorageChange = (e) => {
      if (e.key === 'submissionStatusChanged' && editingCourse && editingCourse.id) {
        console.log('Ê£ÄÊµãÂà∞Êèê‰∫§Áä∂ÊÄÅÂèòÂåñÔºåÂà∑Êñ∞‰Ωú‰∏öÂÆåÊàêÁä∂ÊÄÅ');
        fetchAssignmentCompletionStatus(editingCourse.id);
        // Ê∏ÖÈô§Ê†áËÆ∞
        localStorage.removeItem('submissionStatusChanged');
      }
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);
    window.addEventListener('storage', handleStorageChange);

    return () => {
      clearInterval(refreshInterval);
      document.removeEventListener('visibilitychange', handleVisibilityChange);
      window.removeEventListener('storage', handleStorageChange);
    };
  }, [editingCourse]); // eslint-disable-line react-hooks/exhaustive-deps

  // ‰ªéAPIÂä†ËΩΩÂ≠¶Áîü-ËØæÁ®ãÂÖ≥ËÅîÂÖ≥Á≥ª
  const fetchStudentClassRelations = async (courseId) => {
    try {
      const courseStudentsData = await getCourseStudents(courseId);
      if (courseStudentsData && courseStudentsData.length > 0) {
        const apiRelations = courseStudentsData.map(student => ({
          student_id: student.student_id,
          class_id: student.class_id,
          course_id: courseId,
          student_name: student.student_name,
          student_no: student.student_no,
          added_at: student.added_at || new Date().toISOString()
        }));
        setStudentClassRelations(apiRelations);
      } else {
        setStudentClassRelations([]);
      }
    } catch (error) {
      console.error('Ëé∑ÂèñËØæÁ®ãÂ≠¶ÁîüÂÖ≥ËÅîÂÖ≥Á≥ªÂ§±Ë¥•:', error);
      setStudentClassRelations([]);
    }
  };

  // ÁõëÂê¨ËØæÁ®ãÂèòÂåñÔºåÂä†ËΩΩËØæÁ®ãÂ≠¶ÁîüÂÖ≥ËÅîÂÖ≥Á≥ª
  useEffect(() => {
    const loadCourseStudents = async () => {
      if (editingCourse && editingCourse.id) {
        console.log('ÂºÄÂßãÂä†ËΩΩËØæÁ®ãÂ≠¶ÁîüÊï∞ÊçÆÔºåËØæÁ®ãID:', editingCourse.id);
        await fetchStudentClassRelations(editingCourse.id);
        // ÂêåÊó∂Âä†ËΩΩ‰Ωú‰∏öÂÆåÊàêÁä∂ÊÄÅ
        await fetchAssignmentCompletionStatus(editingCourse.id);
      }
    };

    loadCourseStudents();
  }, [editingCourse]); // eslint-disable-next-line react-hooks/exhaustive-deps

  // Ëé∑Âèñ‰Ωú‰∏öÂÆåÊàêÁä∂ÊÄÅ
  const fetchAssignmentCompletionStatus = async (courseId) => {
    try {
      const completionData = await getAssignmentCompletionStatus(courseId);
      if (completionData && completionData.assignments) {
        const statusMap = {};
        completionData.assignments.forEach(assignment => {
          statusMap[assignment.id] = assignment.completion_status;
        });
        setAssignmentCompletionStatus(statusMap);
        console.log('Ëé∑ÂèñÂà∞‰Ωú‰∏öÂÆåÊàêÁä∂ÊÄÅ:', statusMap);
      }
    } catch (error) {
      console.error('Ëé∑Âèñ‰Ωú‰∏öÂÆåÊàêÁä∂ÊÄÅÂ§±Ë¥•:', error);
      setAssignmentCompletionStatus({});
    }
  };

  const fetchInitialData = async () => {
    try {
      setLoading(true);
      setError('');
      setSuccess('');

      // Ëé∑ÂèñÂ≠¶ÁîüÊâÄÂú®ÁöÑËØæÁ®ãÊï∞ÊçÆ
      try {
        const coursesData = await getTeacherCourses();
        if (coursesData && coursesData.courses) {
          setCourses(coursesData.courses);
          console.log('Ëé∑ÂèñÂà∞Â≠¶ÁîüËØæÁ®ãÊï∞ÊçÆ:', coursesData.courses);
        } else {
          setCourses([]);
        }
      } catch (error) {
        console.warn('Ëé∑ÂèñËØæÁ®ãÊï∞ÊçÆÂ§±Ë¥•:', error);
        setCourses([]);
      }

      // Ëé∑Âèñ‰Ωú‰∏öÊï∞ÊçÆ
      try {
        const assignmentsData = await getCourseAssignments();
        if (assignmentsData && assignmentsData.assignments) {
          setAssignments(assignmentsData.assignments);
          console.log('Ëé∑ÂèñÂà∞‰Ωú‰∏öÊï∞ÊçÆ:', assignmentsData.assignments);
        } else {
          setAssignments([]);
        }
      } catch (error) {
        console.warn('Ëé∑Âèñ‰Ωú‰∏öÊï∞ÊçÆÂ§±Ë¥•:', error);
        setAssignments([]);
      }

    } catch (error) {
      setError(error?.response?.data?.error || 'Ëé∑ÂèñÊï∞ÊçÆÂ§±Ë¥•');
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  // Êü•ÁúãËØæÁ®ãËØ¶ÊÉÖ
  const handleViewCourseDetail = (courseId) => {
    const course = courses.find(c => c.id === courseId);
    if (course) {
      setEditingCourse(course);
      setActiveSubTab('detail');
    }
  };

  // Êü•Áúã‰Ωú‰∏öËØ¶ÊÉÖ
  const handleViewAssignmentDetail = async (assignment) => {
    try {
      setViewingAssignment(assignment);

      // Ëé∑Âèñ‰Ωú‰∏öÂåÖÂê´ÁöÑÈ¢òÁõÆËØ¶ÊÉÖ
      if (assignment.problem_ids && assignment.problem_ids.length > 0) {
        try {
          // Ë∞ÉÁî®APIËé∑ÂèñÈ¢òÁõÆËØ¶ÊÉÖ
          const problemsData = await getProblems(1, 100); // Ëé∑ÂèñÂâç100‰∏™È¢òÁõÆ
          if (problemsData && problemsData.problems) {
            // ËøáÊª§Âá∫‰Ωú‰∏ö‰∏≠ÂåÖÂê´ÁöÑÈ¢òÁõÆ
            const assignmentProblemsList = problemsData.problems.filter(problem => 
              assignment.problem_ids.includes(problem.id)
            );

            // ËΩ¨Êç¢‰∏∫ÈúÄË¶ÅÁöÑÊ†ºÂºè
            const formattedProblems = assignmentProblemsList.map(problem => ({
              id: problem.id,
              title: problem.title || `È¢òÁõÆ ${problem.id}`,
              description: problem.description || `ËøôÊòØ‰Ωú‰∏ö"${assignment.name}"‰∏≠ÁöÑÈ¢òÁõÆ`,
              difficulty: problem.difficulty || 'Êú™Áü•',
              type: problem.type || 'Êú™Áü•Á±ªÂûã'
            }));

            setAssignmentProblems(formattedProblems);
            console.log(`Ëé∑ÂèñÂà∞‰Ωú‰∏ö"${assignment.name}"ÁöÑÈ¢òÁõÆ:`, formattedProblems);
          } else {
            // Â¶ÇÊûúAPIË∞ÉÁî®Â§±Ë¥•Ôºå‰ΩøÁî®È¢òÁõÆID‰Ωú‰∏∫Â§áÈÄâ
            const formattedProblems = assignment.problem_ids.map((pid, idx) => ({
              id: pid,
              title: `È¢òÁõÆ ${idx + 1}`,
              description: `ËøôÊòØ‰Ωú‰∏ö"${assignment.name}"‰∏≠ÁöÑÈ¢òÁõÆ`,
              difficulty: 'Êú™Áü•',
              type: 'Êú™Áü•Á±ªÂûã'
            }));
            setAssignmentProblems(formattedProblems);
          }
        } catch (error) {
          console.warn('Ëé∑ÂèñÈ¢òÁõÆËØ¶ÊÉÖÂ§±Ë¥•Ôºå‰ΩøÁî®Â§áÈÄâÊï∞ÊçÆ:', error);
          const formattedProblems = assignment.problem_ids.map((pid, idx) => ({
            id: pid,
            title: `È¢òÁõÆ ${idx + 1}`,
            description: `ËøôÊòØ‰Ωú‰∏ö"${assignment.name}"‰∏≠ÁöÑÈ¢òÁõÆ`,
            difficulty: 'Êú™Áü•',
            type: 'Êú™Áü•Á±ªÂûã'
          }));
          setAssignmentProblems(formattedProblems);
        }
      } else {
        setAssignmentProblems([]);
      }
    } catch (error) {
      console.error('Êü•Áúã‰Ωú‰∏öËØ¶ÊÉÖÂ§±Ë¥•:', error);
    }
  };

  // ÂÖ≥Èó≠‰Ωú‰∏öËØ¶ÊÉÖ
  const handleCloseAssignmentDetail = () => {
    setViewingAssignment(null);
    setAssignmentProblems([]);
  };

  // ËøîÂõûËØæÁ®ãÂàóË°®
  const handleBackToCourses = () => {
    setEditingCourse(null);
    setActiveSubTab('list');
  };

  // Ê†ºÂºèÂåñÊó•ÊúüÊòæÁ§∫
  const formatDate = (dateString) => {
    if (!dateString) return 'Êú™Áü•';
    try {
      const date = new Date(dateString);
      return date.toLocaleString();
    } catch (e) {
      return dateString;
    }
  };

  const renderCoursesTab = () => (
    <div className="tab-content">
      <div className="sub-tabs">
        <button 
          className={`sub-tab-btn ${activeSubTab === 'list' ? 'active' : ''}`}
          onClick={() => setActiveSubTab('list')}
        >
          ËØæÁ®ãÂàóË°®
        </button>
        {editingCourse && (
          <button 
            className={`sub-tab-btn ${activeSubTab === 'detail' ? 'active' : ''}`}
            onClick={() => setActiveSubTab('detail')}
          >
            ËØæÁ®ãËØ¶ÊÉÖ
          </button>
        )}
      </div>

      <div className="section-content">
        {activeSubTab === 'list' && (
          <div className="courses-list">
            {loading ? (
              <div className="loading">Âä†ËΩΩ‰∏≠...</div>
            ) : courses.length === 0 ? (
              <div className="empty-state">
                <p>ÊöÇÊó†ËØæÁ®ã</p>
              </div>
            ) : (
              <div className="courses-grid">
                {courses.map(course => (
                  <div key={course.id} className="course-card">
                    <div className="course-header">
                      <h3>{course.name}</h3>
                      <span className="course-code">ËØæÁ®ãÁºñÂè∑: {course.id}</span>
                    </div>
                    <div className="course-body">
                      <p className="course-desc">{course.description || 'ÊöÇÊó†ËØæÁ®ãÊèèËø∞'}</p>
                      <div className="course-info">
                        <span className="teacher-name">ÊéàËØæÊïôÂ∏à: {course.teacher_name || 'Êú™Áü•'}</span>
                        <span className="student-count">Â≠¶ÁîüÊï∞Èáè: {course.student_count || 0}</span>
                      </div>
                      <div className="course-actions">
                        <button className="view-detail-btn" onClick={() => handleViewCourseDetail(course.id)}>Êü•ÁúãËØ¶ÊÉÖ</button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {activeSubTab === 'detail' && editingCourse && (
          <div className="course-detail">
            <div className="course-detail-header">
              <h2>{editingCourse.name}</h2>
              <button className="back-btn" onClick={handleBackToCourses}>ËøîÂõûËØæÁ®ãÂàóË°®</button>
            </div>

            <div className="course-info-grid">
              <div className="info-item">
                <label>ËØæÁ®ãÁºñÂè∑</label>
                <span>{editingCourse.id}</span>
              </div>
              <div className="info-item">
                <label>ÊéàËØæÊïôÂ∏à</label>
                <span>{editingCourse.teacher_name || 'Êú™Áü•'}</span>
              </div>
              <div className="info-item">
                <label>Â≠¶ÁîüÊï∞Èáè</label>
                <span>{editingCourse.student_count || 0}</span>
              </div>
            </div>

            <div className="assignments-section">
              <h3>ËØæÁ®ã‰Ωú‰∏ö</h3>
              {assignments.length === 0 ? (
                <div className="empty-state">
                  <p>ËØ•ËØæÁ®ãÊöÇÊó†‰Ωú‰∏ö</p>
                </div>
              ) : (
                <div className="assignments-grid">
                  {assignments
                    .filter(a => a.course_id === editingCourse.id)
                    .map(assignment => {
                      const completionStatus = assignmentCompletionStatus[assignment.id];
                      return (
                        <div key={assignment.id} className="assignment-card" onClick={() => handleViewAssignmentDetail(assignment)}>
                          <div className="assignment-header">
                            <h4>{assignment.name}</h4>
                            <span className={`status-badge ${assignment.is_active ? 'active' : 'inactive'}`}>
                              {assignment.is_active ? 'ËøõË°å‰∏≠' : 'Â∑≤ÁªìÊùü'}
                            </span>
                          </div>
                          <div className="assignment-body">
                            <p className="assignment-desc">{assignment.description || 'ÊöÇÊó†ÊèèËø∞'}</p>
                            <div className="assignment-info">
                              <span className="due-date">Êà™Ê≠¢Êó∂Èó¥: {formatDate(assignment.due_date)}</span>
                            </div>
                            {completionStatus && (
                              <div className="completion-status">
                                <p>
                                  ÂÆåÊàêÁä∂ÊÄÅÔºö
                                  <span className={`status-text ${completionStatus.is_completed ? 'completed' : 'in-progress'}`}>
                                    {completionStatus.status_text}
                                  </span>
                                </p>
                                <p>
                                  ÂÆåÊàêËøõÂ∫¶Ôºö
                                  <span className="progress-text">
                                    {completionStatus.completed_problems}/{completionStatus.total_problems} 
                                    ({completionStatus.completion_percentage}%)
                                  </span>
                                </p>
                                <div className="progress-bar">
                                  <div 
                                    className="progress-fill" 
                                    style={{ width: `${completionStatus.completion_percentage}%` }}
                                  ></div>
                                </div>
                              </div>
                            )}
                            <p className="click-hint">üí° ÁÇπÂáªÊü•Áúã‰Ωú‰∏öËØ¶ÊÉÖÂíåÈ¢òÁõÆ</p>
                          </div>
                        </div>
                      );
                    })}
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );

  return (
    <div className="student-dashboard">
      <div className="dashboard-header">
        <div className="header-content">
          <div className="header-left">
            <h1>Â≠¶Áîü‰ª™Ë°®Êùø</h1>
            <p>Ê¨¢ËøéÂõûÊù•Ôºå{JSON.parse(localStorage.getItem('user') || '{}').name}ÔºÅ</p>
          </div>
        </div>
      </div>

      {error && <div className="error-message">{error}</div>}
      {success && <div className="success-message">{success}</div>}

      <div className="dashboard-tabs-row">
        <div className="dashboard-tabs">
          <button 
            className={`tab-btn ${activeTab === 'courses' ? 'active' : ''}`}
            onClick={() => {
              setActiveTab('courses');
              setActiveSubTab('list');
              setEditingCourse(null);
            }}
          >
            ÊàëÁöÑËØæÁ®ã
          </button>
        </div>
        <div className="dashboard-tabs-actions">
          <Link to="/student/change-password" className="change-password-inline-btn">üîê ‰øÆÊîπÂØÜÁ†Å</Link>
        </div>
      </div>

      {activeTab === 'courses' && renderCoursesTab()}

      {/* ‰Ωú‰∏öËØ¶ÊÉÖÂºπÁ™ó */}
      {viewingAssignment && (
        <div className="modal-overlay" onClick={handleCloseAssignmentDetail}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>üìù ‰Ωú‰∏öËØ¶ÊÉÖÔºö{viewingAssignment.name}</h3>
              <div className="modal-header-actions">
                <button 
                  className="refresh-btn"
                  onClick={() => {
                    if (editingCourse && editingCourse.id) {
                      fetchAssignmentCompletionStatus(editingCourse.id);
                    }
                  }}
                  title="Âà∑Êñ∞ÂÆåÊàêÁä∂ÊÄÅ"
                >
                  üîÑ Âà∑Êñ∞Áä∂ÊÄÅ
                </button>
                <button className="close-btn" onClick={handleCloseAssignmentDetail}>
                  √ó
                </button>
              </div>
            </div>

            <div className="modal-body">
              {/* ‰Ωú‰∏öÂü∫Êú¨‰ø°ÊÅØ */}
              <div className="assignment-detail-section">
                <h4>üìã ‰Ωú‰∏ö‰ø°ÊÅØ</h4>
                <div className="detail-info-grid">
                  <div className="detail-info-item">
                    <label>‰Ωú‰∏öÂêçÁß∞Ôºö</label>
                    <span>{viewingAssignment.name}</span>
                  </div>
                  <div className="detail-info-item">
                    <label>‰Ωú‰∏öÊèèËø∞Ôºö</label>
                    <span>{viewingAssignment.description || 'ÊöÇÊó†ÊèèËø∞'}</span>
                  </div>
                  <div className="detail-info-item">
                    <label>‰Ωú‰∏öË¶ÅÊ±ÇÔºö</label>
                    <span>{viewingAssignment.requirements || 'ÊöÇÊó†Ë¶ÅÊ±Ç'}</span>
                  </div>
                  <div className="detail-info-item">
                    <label>Êà™Ê≠¢Êó∂Èó¥Ôºö</label>
                    <span>{formatDate(viewingAssignment.due_date)}</span>
                  </div>
                  <div className="detail-info-item">
                    <label>‰Ωú‰∏öÁä∂ÊÄÅÔºö</label>
                    <span className={`status-badge ${viewingAssignment.is_active ? 'active' : 'inactive'}`}>
                      {viewingAssignment.is_active ? 'ËøõË°å‰∏≠' : 'Â∑≤ÁªìÊùü'}
                    </span>
                  </div>
                  <div className="detail-info-item">
                    <label>È¢òÁõÆÊï∞ÈáèÔºö</label>
                    <span>{assignmentProblems.length}È¢ò</span>
                  </div>
                  {/* Ê∑ªÂä†ÂÆåÊàêÁä∂ÊÄÅÊòæÁ§∫ */}
                  {assignmentCompletionStatus[viewingAssignment.id] && (
                    <>
                      <div className="detail-info-item">
                        <label>ÂÆåÊàêÁä∂ÊÄÅÔºö</label>
                        <span className={`completion-status ${assignmentCompletionStatus[viewingAssignment.id].is_completed ? 'completed' : 'in-progress'}`}>
                          {assignmentCompletionStatus[viewingAssignment.id].status_text}
                        </span>
                      </div>
                      <div className="detail-info-item">
                        <label>ÂÆåÊàêËøõÂ∫¶Ôºö</label>
                        <span className="progress-text">
                          {assignmentCompletionStatus[viewingAssignment.id].completed_problems}/{assignmentCompletionStatus[viewingAssignment.id].total_problems} 
                          ({assignmentCompletionStatus[viewingAssignment.id].completion_percentage}%)
                        </span>
                      </div>
                    </>
                  )}
                </div>

                {/* Ê∑ªÂä†ËøõÂ∫¶Êù°ÊòæÁ§∫ */}
                {assignmentCompletionStatus[viewingAssignment.id] && (
                  <div className="completion-progress-detail">
                    <div className="progress-bar">
                      <div 
                        className="progress-fill" 
                        style={{ width: `${assignmentCompletionStatus[viewingAssignment.id].completion_percentage}%` }}
                      ></div>
                    </div>
                  </div>
                )}
              </div>

              {/* ‰Ωú‰∏öÈ¢òÁõÆÂàóË°® */}
              <div className="assignment-problems-section">
                <h4>üìö ‰Ωú‰∏öÈ¢òÁõÆ</h4>
                {assignmentProblems.length === 0 ? (
                  <div className="empty-problems">
                    <p>ËØ•‰Ωú‰∏öÊöÇÊó†È¢òÁõÆ</p>
                  </div>
                ) : (
                  <div className="problems-list">
                    {assignmentProblems.map((problem, index) => (
